{
  "name": "glasskube",
  "author": "Glasskube",
  "license": "Apache-2.0",
  "scripts": {
    "postinstall": "npm run build",
    "build": "node esbuild.mjs",
    "prettier:check": "prettier -c web/ internal/**/*.html",
    "prettier:write": "prettier -w web/ internal/**/*.html"
  },
  "dependencies": {
    "@glasskube/theme": "0.1.2",
    "bootstrap": "5.3.3",
    "giscus": "^1.5.0",
    "htmx.org": "2.0.4",
    "htmx-ext-sse": "2.2.2",
    "htmx-ext-response-targets": "2.0.2"
  },
  "devDependencies": {
    "@commitlint/cli": "19.6.1",
    "@commitlint/config-conventional": "19.6.0",
    "esbuild": "0.24.2",
    "prettier": "3.4.2",
    "prettier-plugin-go-template": "0.0.15"
  },
  "prettier": {
    "plugins": [
      "prettier-plugin-go-template"
    ],
    "bracketSameLine": true,
    "bracketSpacing": false,
    "singleQuote": true,
    "semi": true,
    "overrides": [
      {
        "files": "*.css",
        "options": {
          "singleQuote": false
        }
      },
      {
        "files": "*.html",
        "options": {
          "printWidth": 120,
          "parser": "go-template"
        }
      }
    ]
  },
  "commitlint": {
    "extends": [
      "@commitlint/config-conventional"
    ],
    "rules": {
      "body-max-line-length": [
        2,
        "always",
        1000
      ]
    },
    "defaultIgnores": false
  }
}

{
  "packages": {
    ".": {
      "initial-version": "0.0.1",
      "changelog-path": "CHANGELOG.md",
      "release-type": "go",
      "prerelease": false,
      "bump-minor-pre-major": true,
      "bump-patch-for-minor-pre-major": false,
      "draft": false,
      "extra-files": [
        "README.md",
        "config/autoupdater/kustomization.yaml",
        "config/default/kustomization.yaml",
        {
          "type": "json",
          "path": "release.json",
          "jsonpath": "$.version"
        }
      ],
      "changelog-sections": [
        {
          "type": "feat",
          "section": "Features"
        },
        {
          "type": "fix",
          "section": "Bug Fixes"
        },
        {
          "type": "chore",
          "section": "Other"
        },
        {
          "type": "docs",
          "section": "Docs"
        },
        {
          "type": "perf",
          "section": "Performance"
        },
        {
          "type": "build",
          "hidden": true,
          "section": "Build"
        },
        {
          "type": "deps",
          "section": "Dependency Updates"
        },
        {
          "type": "ci",
          "hidden": true,
          "section": "CI"
        },
        {
          "type": "refactor",
          "section": "Refactoring"
        },
        {
          "type": "revert",
          "hidden": true,
          "section": "Reverts"
        },
        {
          "type": "style",
          "hidden": true,
          "section": "Styling"
        },
        {
          "type": "test",
          "hidden": true,
          "section": "Tests"
        }
      ]
    }
  },
  "$schema": "https://raw.githubusercontent.com/googleapis/release-please/main/schemas/config.json"
}

{
  "$schema": "https://docs.renovatebot.com/renovate-schema.json",
  "postUpdateOptions": [
    "gomodTidy"
  ],
  "extends": [
    "config:recommended"
  ],
  "packageRules": [
    {
      "matchManagers": [
        "npm",
        "github-actions"
      ],
      "matchDepTypes": [
        "devDependencies"
      ],
      "matchUpdateTypes": [
        "minor",
        "patch"
      ],
      "matchCurrentVersion": "!/^0/",
      "automerge": true
    },
    {
      "matchManagers": [
        "npm",
        "github-actions"
      ],
      "matchUpdateTypes": [
        "patch"
      ],
      "matchCurrentVersion": "!/^0/",
