package main

import (
	"os"

	"github.com/vmware-tanzu/sonobuoy/cmd/sonobuoy/app"
	"github.com/vmware-tanzu/sonobuoy/pkg/errlog"
)

func main() {
	err := app.NewSonobuoyCommand().Execute()
	if err != nil {
		errlog.LogError(err)
		os.Exit(1)
	}
}
