{
  "settingsInheritedFrom": "hellofresh/whitesource-config@master"
}

NO_COLOR=\033[0m
OK_COLOR=\033[32;01m
ERROR_COLOR=\033[31;01m
WARN_COLOR=\033[33;01m

VERSION ?= "dev-$(shell git rev-parse --short HEAD)"
GO_LINKER_FLAGS=-ldflags="-s -w -X main.version=$(VERSION)"

.PHONY: all lint test-unit test-integration test-features build

all: test-unit build

build:
	@echo "$(OK_COLOR)==> Building default binary... $(NO_COLOR)"
	@CGO_ENABLED=0 go build ${GO_LINKER_FLAGS} -o "dist/janus"

test-unit:
	@echo "$(OK_COLOR)==> Running unit tests$(NO_COLOR)"
	@go test ./...

test-integration: _mocks
	@echo "$(OK_COLOR)==> Running integration tests$(NO_COLOR)"
	@go test -cover -tags=integration -coverprofile=coverage.txt -covermode=atomic ./...

test-features: build _mocks
	@/bin/sh -c "./build/features.sh"

lint:
	@echo "$(OK_COLOR)==> Linting with golangci-lint running in docker container$(NO_COLOR)"
	@docker run --rm -v $(PWD):/app -w /app golangci/golangci-lint:v1.30.0 golangci-lint run -v

_mocks:
	@/bin/sh -c "./build/mocks.sh"

{
    "root": "docs",
    "title": "Janus docs",
    "plugins": ["codetabs"]
}

package main

import (
	"github.com/hellofresh/janus/cmd"
	log "github.com/sirupsen/logrus"
)

var version = "0.0.0-dev"

func main() {
	rootCmd := cmd.NewRootCmd(version)

	if err := rootCmd.Execute(); err != nil {
		log.WithError(err).Fatal("Could not run command")
	}
}

package main

import (
	"context"
	"net/url"
	"os"
	"strconv"
	"testing"

	"github.com/cucumber/godog"
	"github.com/stretchr/testify/assert"

	"github.com/hellofresh/janus/features/bootstrap"
	"github.com/hellofresh/janus/pkg/api"
	"github.com/hellofresh/janus/pkg/config"
)

const defaultUpstreamsPort = 9089

var (
	apiRepo          api.Repository
	cfg              *config.Specification
	cfgChan          chan api.ConfigurationMessage
	portSecondary    int
	apiPortSecondary int
	upstreamsPort    int
)

func InitializeTestSuite(ctx *godog.TestSuiteContext) {
	var err error

	ctx.BeforeSuite(func() {
		cfg, err = config.LoadEnv()
		if err != nil {
			panic(err)
		}

		dsnURL, err := url.Parse(cfg.Database.DSN)
		if nil != err {
			panic(err)
		}

		switch dsnURL.Scheme {
		case "mongodb":
			apiRepo, err = api.NewMongoAppRepository(cfg.Database.DSN, cfg.BackendFlushInterval)
			if err != nil {
				panic(err)
			}
		case "file":
			var apiPath = dsnURL.Path + "/apis"

			apiRepo, err = api.NewFileSystemRepository(apiPath)
			if err != nil {
				panic(err)
			}
		default:
			panic("invalid database")
		}

		portSecondary, err = strconv.Atoi(os.Getenv("PORT_SECONDARY"))
		if err != nil {
			panic(err)
		}

		apiPortSecondary, err = strconv.Atoi(os.Getenv("API_PORT_SECONDARY"))
		if err != nil {
			panic(err)
		}

		upstreamsPort = defaultUpstreamsPort
		if dynamicUpstreamsPort, exists := os.LookupEnv("DYNAMIC_UPSTREAMS_PORT"); exists {
			upstreamsPort, err = strconv.Atoi(dynamicUpstreamsPort)
			if err != nil {
				panic(err)
			}
		}

		cfgChan = make(chan api.ConfigurationMessage, 100)
		if listener, ok := apiRepo.(api.Listener); ok {
			listener.Listen(context.Background(), cfgChan)
		}
	})
}

func InitializeScenario(ctx *godog.ScenarioContext) {
	bootstrap.RegisterRequestContext(ctx, cfg.Port, cfg.Web.Port, portSecondary, apiPortSecondary, defaultUpstreamsPort, upstreamsPort, cfg.Web.Credentials)
	bootstrap.RegisterAPIContext(ctx, apiRepo, cfgChan)
	bootstrap.RegisterMiscContext(ctx)
}

func Test_Fake(t *testing.T) {
	assert.True(t, true)
}
