package containerssh_test

import (
	"fmt"
	"testing"

    "go.containerssh.io/containerssh/config"
)

// TestBackends tests all possible backends for basic functionality.
func TestBackends(t *testing.T) {
	for _, backend := range config.BackendValues() {
		t.Run(
			fmt.Sprintf("backend=%s", backend),
			func(t *testing.T) {
				testBackend(NewT(t), backend)
			},
		)
	}
}

// testBackend tests a single backend.
func testBackend(t T, backend config.Backend) {
	t.ConfigureBackend(backend)
	t.StartContainerSSH()
	t.LoginViaSSH()

	t.Run("CommandExecution", func(t T) {
		t.StartSessionChannel()
		t.RequestCommandExecution("echo 'Hello world!' && sleep 3")
		t.AssertStdoutHas("Hello world!")
		t.CloseChannel()
	})

	t.Run("Shell", func(t T) {
		t.StartSessionChannel()
		t.RequestShell()
		t.SendStdin("echo 'Hello world!' && sleep 3\n")
		t.AssertStdoutHas("Hello world!")
		t.SendStdin("exit\n")
		t.CloseChannel()
	})
}

package containerssh_test

import (
	"bytes"
	"context"
	"crypto/rand"
	"crypto/rsa"
	"crypto/x509"
	"encoding/pem"
	"errors"
	"fmt"
	"io"
	"net"
	"strings"
	"sync"
	"testing"
	"time"

    containerssh "go.containerssh.io/containerssh"
    auth2 "go.containerssh.io/containerssh/auth"
    "go.containerssh.io/containerssh/auth/webhook"
    "go.containerssh.io/containerssh/config"
    internalssh "go.containerssh.io/containerssh/internal/ssh"
    "go.containerssh.io/containerssh/internal/test"
    "go.containerssh.io/containerssh/log"
    "go.containerssh.io/containerssh/message"
    "go.containerssh.io/containerssh/metadata"
    "go.containerssh.io/containerssh/service"
	"golang.org/x/crypto/ssh"
)

func NewT(t *testing.T) T {
	cfg := config.AppConfig{}
	cfg.Default()
	return &testContext{
		t,
		cfg,
		&sync.Mutex{},
		NewAuthUserStorage(),
		0,
		0,
		nil,
		nil,
		nil,
		nil,
	}
}

type T interface {
	// StartContainerSSH starts a ContainerSSH instance on a random port. If no authentication has been previously
	// configured, this configures ContainerSSH for webhook authentication with an internal user database.
	StartContainerSSH()
	// ConfigureBackend configures ContainerSSH to use a specific backend.
	ConfigureBackend(backend config.Backend)
	// LoginViaSSH logs creates a temporary user and logs in via SSH. After this
	// has been called new session channels can be requested.
	LoginViaSSH()
	// StartSessionChannel starts a new session channel in ContainerSSH. Later commands will
	// run in the context of this session channel. If multiple session channels are desired,
	// each one should be run in a separate subtest.
	StartSessionChannel()
	// RequestCommandExecution attempts to run the specified command in a previously-opened
	// session channel. If no session channel has been opened, this command will fail.
	RequestCommandExecution(cmd string)
	// RequestShell requests a shell to be executed.
	RequestShell()
	// AssertStdoutHas waits for the specified output string to be sent from the output.
	AssertStdoutHas(output string)
	// SendStdin sends the specified string to the SSH server via the standard input.
	SendStdin(data string)
	// CloseChannel closes the current channel.
	CloseChannel()

	Parallel()
	Run(name string, f func(t T)) bool
	Cleanup(func())
	Error(args ...interface{})
	Errorf(format string, args ...interface{})
	Fail()
	FailNow()
	Failed() bool
	Fatal(args ...interface{})
	Fatalf(format string, args ...interface{})
	Log(args ...interface{})
	Logf(format string, args ...interface{})
	Skip(args ...interface{})
	SkipNow()
	Skipf(format string, args ...interface{})
}

type testContext struct {
	*testing.T
	cfg       config.AppConfig
	lock      *sync.Mutex
	users     AuthUserStorage
	authPort  int
	sshPort   int
	lifecycle service.Lifecycle
	sshConn   *ssh.Client
	channel   ssh.Channel
	requests  <-chan *ssh.Request
}

func (c *testContext) StartContainerSSH() {
	t := c.T
	t.Helper()
	c.lock.Lock()
	defer c.lock.Unlock()

	if c.lifecycle != nil {
		t.Fatalf("ContainerSSH is already running.")
	}

	t.Logf("Starting ContainerSSH...")

	c.authPort = test.GetNextPort(c.T, "auth server")
	c.authServer(c.T, c.users, c.authPort)
	c.cfg.Auth.PasswordAuth.Method = config.PasswordAuthMethodWebhook
	c.cfg.Auth.PasswordAuth.Webhook.URL = fmt.Sprintf("http://127.0.0.1:%d", c.authPort)
	c.cfg.Auth.PublicKeyAuth.Method = config.PubKeyAuthMethodWebhook
	c.cfg.Auth.PublicKeyAuth.Webhook.URL = fmt.Sprintf("http://127.0.0.1:%d", c.authPort)

	c.sshPort = test.GetNextPort(c.T, "ContainerSSH")
	c.cfg.SSH.Listen = fmt.Sprintf("127.0.0.1:%d", c.sshPort)
	if err := c.cfg.SSH.GenerateHostKey(); err != nil {
		t.Fatalf("Failed to generate host keys (%v)", err)
	}
	c.cfg.Log.T = c.T
	c.cfg.Log.Destination = config.LogDestinationTest

	cssh, lifecycle, err := containerssh.New(c.cfg, log.NewLoggerFactory())
	if err != nil {
		t.Fatalf("Failed to start ContainerSSH (%v)", err)
	}
	c.lifecycle = lifecycle
	running := make(chan struct{})
	stopped := make(chan struct{})
	crashed := make(chan struct{})
	lifecycle.OnRunning(
		func(s service.Service, l service.Lifecycle) {
			close(running)
		},
	)
	lifecycle.OnStopping(
		func(s service.Service, l service.Lifecycle, shutdownContext context.Context) {
			close(stopped)
		},
	)
	lifecycle.OnCrashed(
		func(s service.Service, l service.Lifecycle, err error) {
			close(crashed)
		},
	)
	go func() {
		_ = cssh.RunWithLifecycle(lifecycle)
	}()
	c.T.Cleanup(func() {
		lifecycle.Stop(context.Background())
	})

	select {
	case <-running:
		t.Logf("ContainerSSH is now running.")
	case <-stopped:
		t.Fatalf("ContainerSSH unexpectedly stopped.")
	case <-crashed:
		t.Fatalf("ContainerSSH unexpectedly crashed.")
	}

	t.Logf("Started ContainerSSH.")
}

func (c *testContext) ConfigureBackend(backend config.Backend) {
	t := c.T
	t.Helper()

	t.Logf("Configuring %s backend...", backend)
	c.cfg.Backend = backend
	switch backend {
	case config.BackendKubernetes:
		kube := test.Kubernetes(t)
		c.cfg.Kubernetes.Connection.ServerName = kube.ServerName
		c.cfg.Kubernetes.Connection.Host = kube.Host
		c.cfg.Kubernetes.Connection.CAData = kube.CACert
		c.cfg.Kubernetes.Connection.KeyData = kube.UserKey
		c.cfg.Kubernetes.Connection.CertData = kube.UserCert
	case config.BackendSSHProxy:
		proxy := test.SSH(t)
		c.cfg.SSHProxy.Server = proxy.Host()
		c.cfg.SSHProxy.Port = uint16(proxy.Port())
		c.cfg.SSHProxy.Username = proxy.Username()
		c.cfg.SSHProxy.Password = proxy.Password()
		c.cfg.SSHProxy.AllowedHostKeyFingerprints = []string{
			proxy.FingerprintSHA256(),
		}
		c.cfg.SSHProxy.HostKeyAlgorithms = config.MustSSHKeyAlgoListFromStringList(
			proxy.HostKeyAlgorithms(),
		)
	}
	t.Logf("Configured %s backend.", backend)
}

func (c *testContext) LoginViaSSH() {
	t := c.T
	t.Helper()
	c.lock.Lock()
	defer c.lock.Unlock()
	if c.sshConn != nil {
		t.Fatalf("Already logged in via SSH.")
	}

	t.Logf("Logging in via SSH...")

	cfg := ssh.ClientConfig{}
	cfg.SetDefaults()
	username := c.T.Name()
	user := c.users.AddUser(username)
	password := "test-login"
	user.SetPassword(password)
	cfg.Auth = append(cfg.Auth, ssh.Password(password))

	hostKeys, err := c.cfg.SSH.LoadHostKeys()
	if err != nil {
		t.Fatalf("Failed to read back the host keys (%v)", err)
	}
	hostKeyAlgorithms := make([]string, len(hostKeys))
	marshalledHostKeys := make([]string, len(hostKeys))
	for i, hostKey := range hostKeys {
		hostKeyAlgorithms[i] = hostKey.PublicKey().Type()
		marshalledHostKeys[i] = string(ssh.MarshalAuthorizedKey(hostKey.PublicKey()))
	}
	cfg.HostKeyAlgorithms = hostKeyAlgorithms
	cfg.HostKeyCallback = func(hostname string, remote net.Addr, key ssh.PublicKey) error {
		marshalledHostKey := string(ssh.MarshalAuthorizedKey(key))
		for _, hostKey := range marshalledHostKeys {
			if hostKey == marshalledHostKey {
				return nil
			}
		}
		return fmt.Errorf("invalid host key: %s", marshalledHostKey)
	}
	cfg.User = username

	sshConn, err := ssh.Dial("tcp", fmt.Sprintf("127.0.0.1:%d", c.sshPort), &cfg)
	if err != nil {
		t.Fatalf("Failed to log in via SSH (%v)", err)
	}
	c.sshConn = sshConn

	t.Logf("SSH login successful.")
}

func (c *testContext) StartSessionChannel() {
	t := c.T
	t.Helper()
	c.lock.Lock()
	defer c.lock.Unlock()
	if c.sshConn == nil {
		t.Fatalf("SSH connection is not running.")
	}
	if c.channel != nil {
		t.Fatalf("A channel is already open.")
	}

	t.Logf("Starting a new session channel...")

	channel, requests, err := c.sshConn.OpenChannel("session", nil)
	if err != nil {
		t.Fatalf("Failed to open channel (%v)", err)
	}
	c.channel = channel
	c.requests = requests
	// We use c.T here so the cleanup happens in the parent test.
	c.T.Cleanup(func() {
		_ = channel.Close()
		c.channel = nil
	})

	t.Logf("Started a new session channel.")
}

func (c *testContext) RequestCommandExecution(cmd string) {
	t := c.T
	t.Helper()
	c.lock.Lock()
	defer c.lock.Unlock()
	if c.channel == nil {
		t.Fatalf("No channel opened.")
	}
	t.Logf("Starting program %s ...", cmd)

	success, err := c.channel.SendRequest(
		string(internalssh.RequestTypeExec),
		true,
		ssh.Marshal(internalssh.ExecRequestPayload{Exec: cmd}),
	)
	if err != nil {
		t.Fatalf("Failed to send exec request. (%v)", err)
	}
	if !success {
		t.Fatalf("Server rejected exec request. (%v)", err)
	}
	t.Logf("Started program.")
}

func (c *testContext) RequestShell() {
	t := c.T
	t.Helper()
	c.lock.Lock()
	defer c.lock.Unlock()
	if c.channel == nil {
		t.Fatalf("No channel opened.")
	}
	t.Logf("Starting shell...")

	success, err := c.channel.SendRequest(
		string(internalssh.RequestTypeShell),
		true,
		nil,
	)
	if err != nil {
		t.Fatalf("Failed to send exec request. (%v)", err)
	}
	if !success {
		t.Fatalf("Server rejected exec request. (%v)", err)
	}
	t.Logf("Started shell.")
}

func (c *testContext) AssertStdoutHas(output string) {
	t := c.T
	t.Helper()
	c.lock.Lock()
	defer c.lock.Unlock()
	if c.channel == nil {
		t.Fatalf("No channel opened.")
	}
	t.Logf("Waiting for output...")

	// Wait a second to allow the output to arrive
	time.Sleep(1 * time.Second)
	data := make([]byte, 16*1024)
	n, err := c.channel.Read(data)
	if err != nil {
		t.Fatalf("Failed to read channel stdout. (%v)", err)
	}

	if !strings.Contains(string(data[:n]), output) {
		t.Fatalf("Output does not contain '%s' (output was: %s)", output, string(data[:n]))
	}

	t.Logf("Output check complete.")
}

func (c *testContext) SendStdin(data string) {
	t := c.T
	t.Helper()
	c.lock.Lock()
	defer c.lock.Unlock()
	if c.channel == nil {
		t.Fatalf("No channel opened.")
	}
	t.Logf("Sending stdin data...")

	if _, err := c.channel.Write([]byte(data)); err != nil {
		t.Fatalf("Failed to send stdin data (%v)", err)
	}

	t.Logf("Sent stdin data.")
}

func (c *testContext) CloseChannel() {
	t := c.T
	t.Helper()
	c.lock.Lock()
	defer c.lock.Unlock()
	if c.channel == nil {
		t.Fatalf("No channel opened.")
	}
	t.Logf("Closing channel...")

	if err := c.channel.Close(); err != nil && !errors.Is(err, io.EOF) {
		t.Fatalf("Failed to close channel. (%v)", err)
	}

	c.channel = nil

	t.Logf("Closed channel.")
}

func (c *testContext) Run(name string, f func(t T)) bool {
	c.T.Helper()
	return c.T.Run(name, func(t *testing.T) {
		t.Helper()
		f(&testContext{
			t,
			c.cfg,
			c.lock,
			c.users,
			c.authPort,
			c.sshPort,
			c.lifecycle,
			c.sshConn,
			nil,
			nil,
		})
	})
}

type authHandler struct {
	userdb AuthUserStorage
}

func (a *authHandler) OnPassword(meta metadata.ConnectionAuthPendingMetadata, Password []byte) (
	bool,
	metadata.ConnectionAuthenticatedMetadata,
	error,
) {
	user, err := a.userdb.GetUser(meta.Username)
	if err != nil {
		return false, meta.AuthFailed(), err
	}
	if pw := user.GetPassword(); pw != nil && *pw == string(Password) {
		return true, meta.Authenticated(meta.Username), nil
	}
	return false, meta.AuthFailed(), fmt.Errorf("incorrect password")
}

func (a *authHandler) OnPubKey(meta metadata.ConnectionAuthPendingMetadata, publicKey auth2.PublicKey) (
	bool,
	metadata.ConnectionAuthenticatedMetadata,
	error,
) {
	user, err := a.userdb.GetUser(meta.Username)
	if err != nil {
		return false, meta.AuthFailed(), err
	}
	for _, key := range user.GetAuthorizedKeys() {
		if key == publicKey.PublicKey {
			return true, meta.Authenticated(meta.Username), nil
		}
	}
	return false, meta.AuthFailed(), fmt.Errorf("authentication failed")
}

func (a *authHandler) OnAuthorization(meta metadata.ConnectionAuthenticatedMetadata) (
	bool,
	metadata.ConnectionAuthenticatedMetadata,
	error,
) {
	return true, meta, nil
}

func (c *testContext) authServer(t *testing.T, userdb AuthUserStorage, port int) {
	srv, err := webhook.NewServer(
		config.HTTPServerConfiguration{
			Listen: fmt.Sprintf("127.0.0.1:%d", port),
		},
		&authHandler{
			userdb: userdb,
		},
		log.NewTestLogger(t),
	)
	if err != nil {
		t.Fatalf("Failed to start authentication webhook server. (%v)", err)
	}
	lifecycle := service.NewLifecycle(srv)
	go func() {
		_ = lifecycle.Run()
	}()

	t.Cleanup(func() {
		shutdownContext, cancel := context.WithTimeout(context.Background(), 30*time.Second)
		defer cancel()
		lifecycle.Stop(shutdownContext)

		lastError := lifecycle.Wait()
		if lastError != nil {
			t.Fatalf("Failed to stop authentication webhook server. (%v)", lastError)
		}
	})
}

// AuthUser is an entry in the in-memory AuthUserStorage. It can be used to modify the user used for testing.
type AuthUser interface {
	SetPassword(password string)
	// GetPassword
	GetPassword() *string
	// AddKey adds a new private and public key to this user.
	AddKey() ssh.Signer
	// GetKeys returns a list of signers containing the private and public key for this user.
	GetKeys() []ssh.Signer
	// GetAuthorizedKeys returns a list of public keys in the OpenSSH Authorized Keys format.
	GetAuthorizedKeys() []string
}

// AuthUserStorage is a storage interface for creating and managing in-memory users used for test
// authentications.
type AuthUserStorage interface {
	// AddUser adds a new user to the in-memory database. You can then add credentials for the
	// SSH connection to the user. If the user already exists, this function throws a panic.
	AddUser(username string) AuthUser
	// GetUser returns a user with a specific username. If the user is not found, this function
	// throws a panic.
	GetUser(username string) (AuthUser, error)
	// RemoveUser removes a user from the internal database. If the user is not found, this
	// function throws a panic.
	RemoveUser(username string)
}

type authUserStorage struct {
	lock  *sync.Mutex
	users map[string]AuthUser
}

func (a *authUserStorage) AddUser(username string) AuthUser {
	a.lock.Lock()
	defer a.lock.Unlock()
	if _, ok := a.users[username]; ok {
		panic(message.NewMessage(message.MTest, "User %s already exists in test user database.", username))
	}
	a.users[username] = &authUser{
		lock: &sync.Mutex{},
	}
	return a.users[username]
}

func (a *authUserStorage) GetUser(username string) (AuthUser, error) {
	a.lock.Lock()
	defer a.lock.Unlock()
	if user, ok := a.users[username]; ok {
		return user, nil
	}
	return nil, message.NewMessage(message.MTest, "User %s not found in test database.", username)
}

func (a *authUserStorage) RemoveUser(username string) {
	a.lock.Lock()
	defer a.lock.Unlock()
	delete(a.users, username)
}

type authUser struct {
	lock     *sync.Mutex
	password *string
	keys     []ssh.Signer
}

func (a *authUser) SetPassword(password string) {
	a.password = &password
}

func (a *authUser) GetPassword() *string {
	return a.password
}

func (a *authUser) AddKey() ssh.Signer {
	a.lock.Lock()
	defer a.lock.Unlock()
	reader := rand.Reader
	bitSize := 4096
	key, err := rsa.GenerateKey(reader, bitSize)
	if err != nil {
		panic(message.Wrap(err, message.MTest, "Failed to generate RSA key."))
	}
	var pemBlock = &pem.Block{
		Type:  "RSA PRIVATE KEY",
		Bytes: x509.MarshalPKCS1PrivateKey(key),
	}
	var pemBytes bytes.Buffer
	if err := pem.Encode(&pemBytes, pemBlock); err != nil {
		panic(message.Wrap(err, message.MTest, "Failed to marshal private key."))
	}

	sshPrivateKey, err := ssh.ParsePrivateKey(pemBytes.Bytes())
	if err != nil {
		panic(message.Wrap(err, message.MTest, "Failed to parse SSH private key."))
	}

	a.keys = append(a.keys, sshPrivateKey)
	return sshPrivateKey
}

func (a *authUser) GetKeys() []ssh.Signer {
	a.lock.Lock()
	defer a.lock.Unlock()
	result := make([]ssh.Signer, len(a.keys))
	copy(result, a.keys)
	return result
}

func (a *authUser) GetAuthorizedKeys() []string {
	a.lock.Lock()
	defer a.lock.Unlock()
	result := make([]string, len(a.keys))
	for i, key := range a.keys {
		result[i] = fmt.Sprintf("ssh-rsa %s", ssh.MarshalAuthorizedKey(key.PublicKey()))
	}
	return result
}

// NewAuthUserStorage creates a new in-memory user storage for authentication.
func NewAuthUserStorage() AuthUserStorage {
	return &authUserStorage{
		lock:  &sync.Mutex{},
		users: map[string]AuthUser{},
	}
}

package containerssh

import (
	"context"

	"go.containerssh.io/containerssh/config"
	"go.containerssh.io/containerssh/internal/auditlogintegration"
	"go.containerssh.io/containerssh/internal/authintegration"
	"go.containerssh.io/containerssh/internal/backend"
	"go.containerssh.io/containerssh/internal/geoip"
	"go.containerssh.io/containerssh/internal/geoip/geoipprovider"
	"go.containerssh.io/containerssh/internal/health"
	"go.containerssh.io/containerssh/internal/metrics"
	"go.containerssh.io/containerssh/internal/metricsintegration"
	"go.containerssh.io/containerssh/internal/sshserver"
	"go.containerssh.io/containerssh/log"
	"go.containerssh.io/containerssh/message"
	"go.containerssh.io/containerssh/service"
)

// New creates a new instance of ContainerSSH.
func New(cfg config.AppConfig, factory log.LoggerFactory) (Service, service.Lifecycle, error) {
	if err := cfg.Validate(false); err != nil {
		return nil, nil, message.Wrap(err, message.ECoreConfig, "invalid ContainerSSH configuration")
	}

	logger, err := factory.Make(cfg.Log)
	if err != nil {
		return nil, nil, err
	}

	pool := service.NewPool(
		service.NewLifecycleFactory(),
		logger.WithLabel("module", "service"),
	)

	healthService, err := health.New(cfg.Health, logger.WithLabel("module", "health"))
	if err != nil {
		return nil, nil, err
	}
	pool.Add(healthService)

	geoIPLookupProvider, err := geoip.New(cfg.GeoIP)
	if err != nil {
		return nil, nil, err
	}

	metricsCollector := metrics.New(geoIPLookupProvider)

	if err := createMetricsServer(cfg, logger, metricsCollector, pool); err != nil {
		return nil, nil, err
	}

	containerBackend, err := createBackend(cfg, logger, metricsCollector)
	if err != nil {
		return nil, nil, err
	}

	authHandler, err := createAuthHandler(cfg, logger, containerBackend, metricsCollector, pool)
	if err != nil {
		return nil, nil, err
	}

	auditLogHandler, err := createAuditLogHandler(cfg, logger, authHandler, geoIPLookupProvider)
	if err != nil {
		return nil, nil, err
	}

	metricsHandler, err := createMetricsBackend(cfg, metricsCollector, auditLogHandler)
	if err != nil {
		return nil, nil, err
	}

	if err := createSSHServer(cfg, logger, metricsHandler, pool); err != nil {
		return nil, nil, err
	}

	return setUpService(pool, logger, healthService)
}

func setUpService(pool service.Pool, logger log.Logger, healthService health.Service) (
	Service,
	service.Lifecycle,
	error,
) {
	poolWrapper := &servicePool{
		pool,
		logger,
	}
	lifecycle := service.NewLifecycle(poolWrapper)
	lifecycle.OnRunning(
		func(s service.Service, l service.Lifecycle) {
			healthService.ChangeStatus(true)
		},
	).OnStopping(
		func(s service.Service, l service.Lifecycle, shutdownContext context.Context) {
			healthService.ChangeStatus(false)
		},
	).OnCrashed(
		func(s service.Service, l service.Lifecycle, err error) {
			healthService.ChangeStatus(false)
		},
	)

	return poolWrapper, lifecycle, nil
}

type servicePool struct {
	service.Pool
	logger log.Logger
}

func (s servicePool) RotateLogs() error {
	return s.logger.Rotate()
}

func createMetricsBackend(
	cfg config.AppConfig,
	collector metrics.Collector,
	handler sshserver.Handler,
) (sshserver.Handler, error) {
	return metricsintegration.NewHandler(
		cfg.Metrics,
		collector,
		handler,
	)
}

func createMetricsServer(
	cfg config.AppConfig,
	logger log.Logger,
	metricsCollector metrics.Collector,
	pool service.Pool,
) error {
	metricsLogger := logger.WithLabel("module", "metrics")
	metricsServer, err := metrics.NewServer(cfg.Metrics, metricsCollector, metricsLogger)
	if err != nil {
		return err
	}
	if metricsServer == nil {
		return nil
	}
	pool.Add(metricsServer)
	return nil
}

func createSSHServer(
	cfg config.AppConfig,
	logger log.Logger,
	auditLogHandler sshserver.Handler,
	pool service.Pool,
) error {
	sshLogger := logger.WithLabel("module", "ssh")
	sshServer, err := sshserver.New(
		cfg.SSH,
		auditLogHandler,
		sshLogger,
	)
	if err != nil {
		return err
	}
	pool.Add(sshServer)
	return nil
}

func createAuditLogHandler(
	cfg config.AppConfig,
	logger log.Logger,
	authHandler sshserver.Handler,
	geoIPLookupProvider geoipprovider.LookupProvider,
) (sshserver.Handler, error) {
	auditLogger := logger.WithLabel("module", "audit")
	return auditlogintegration.New(
		cfg.Audit,
		authHandler,
		geoIPLookupProvider,
		auditLogger,
	)
}

func createAuthHandler(
	cfg config.AppConfig,
	logger log.Logger,
	backend sshserver.Handler,
	metricsCollector metrics.Collector,
	pool service.Pool,
) (sshserver.Handler, error) {
	authLogger := logger.WithLabel("module", "auth")
	handler, services, err := authintegration.New(
		cfg.Auth,
		backend,
		authLogger,
		metricsCollector,
		authintegration.BehaviorNoPassthrough,
	)
	if err != nil {
		return nil, err
	}
	for _, svc := range services {
		pool.Add(svc)
	}
	return handler, nil
}

func createBackend(cfg config.AppConfig, logger log.Logger, metricsCollector metrics.Collector) (sshserver.Handler, error) {
	backendLogger := logger.WithLabel("module", "backend")
	containerBackend, err := backend.New(cfg, backendLogger, metricsCollector, sshserver.AuthResponseUnavailable)
	if err != nil {
		return nil, err
	}
	return containerBackend, nil
}

package containerssh

import (
	"bytes"
	"context"
	"flag"
	"fmt"
	"io"
	"os"
	"os/signal"
	"path/filepath"
	"strings"
	"syscall"
	"time"

	"go.containerssh.io/containerssh/config"
	internalConfig "go.containerssh.io/containerssh/internal/config"
	"go.containerssh.io/containerssh/internal/health"
	"go.containerssh.io/containerssh/log"
	"go.containerssh.io/containerssh/message"
	"go.containerssh.io/containerssh/service"
)

// Main is a helper function to start a standard ContainerSSH instance. It should be used as the outer-most function
// and should never be used as an embedding technique.
func Main() {
	cfg := config.AppConfig{}
	cfg.Default()

	loggerFactory := log.NewLoggerFactory()
	logger, err := loggerFactory.Make(
		cfg.Log,
	)
	if err != nil {
		panic(err)
	}

	logger = logger.WithLabel("module", "core")

	configFile, actionDumpConfig, actionLicenses, actionHealthCheck := getArguments()

	if configFile == "" {
		configFile = "config.yaml"
	}
	realConfigFile, err := filepath.Abs(configFile)
	if err != nil {
		logger.Critical(
			message.Wrap(
				err,
				message.ECoreConfig,
				"Failed to fetch absolute path for configuration file %s",
				configFile,
			))
		os.Exit(1)
	}
	configFile = realConfigFile
	if err = readConfigFile(configFile, loggerFactory, &cfg); err != nil {
		logger.Critical(
			message.Wrap(
				err,
				message.ECoreConfig,
				"Invalid configuration in file %s",
				configFile,
			))
		os.Exit(1)
	}

	configuredLogger, err := loggerFactory.Make(
		cfg.Log,
	)
	if err != nil {
		logger.Critical(err)
		os.Exit(1)
	}
	configuredLogger.Debug(message.NewMessage(message.MCoreConfigFile, "Using configuration file %s...", configFile))

	switch {
	case actionDumpConfig:
		runDumpConfig(cfg, configuredLogger)
	case actionLicenses:
		runActionLicenses(configuredLogger)
	case actionHealthCheck:
		runHealthCheck(cfg, configuredLogger)
	default:
		runContainerSSH(loggerFactory, configuredLogger, cfg, configFile)
	}
}

func runHealthCheck(cfg config.AppConfig, logger log.Logger) {
	if err := healthCheck(cfg, logger); err != nil {
		logger.Critical(err)
		os.Exit(1)
	}
	logger.Info(message.NewMessage(message.MCoreHealthCheckSuccessful, "Health check successful."))
	os.Exit(0)
}

func runActionLicenses(logger log.Logger) {
	if err := printLicenses(os.Stdout); err != nil {
		logger.Critical(err)
		os.Exit(1)
	}
	os.Exit(0)
}

func runDumpConfig(cfg config.AppConfig, logger log.Logger) {
	if err := dumpConfig(os.Stdout, logger, &cfg); err != nil {
		logger.Critical(err)
		os.Exit(1)
	}
	os.Exit(0)
}

func runContainerSSH(
	loggerFactory log.LoggerFactory,
	logger log.Logger,
	cfg config.AppConfig,
	configFile string,
) {
	if len(cfg.SSH.HostKeys) == 0 {
		logger.Warning(
			message.NewMessage(
				message.ECoreNoHostKeys,
				"No host keys found in configuration, generating temporary host keys and updating configuration...",
			),
		)
		if err := generateHostKeys(configFile, &cfg, logger); err != nil {
			logger.Critical(
				message.Wrap(
					err,
					message.ECoreHostKeyGenerationFailed,
					"failed to generate host keys",
				))
			os.Exit(1)
		}
	}

	if err := startServices(cfg, loggerFactory); err != nil {
		logger.Critical(err)
		os.Exit(1)
	}
	os.Exit(0)
}

func getArguments() (string, bool, bool, bool) {
	configFile := ""
	actionDumpConfig := false
	actionLicenses := false
	healthCheck := false
	flag.StringVar(
		&configFile,
		"config",
		"",
		"Configuration file to load (has to end in .yaml, .yml, or .json)",
	)
	flag.BoolVar(
		&actionDumpConfig,
		"dump-config",
		false,
		"Dump configuration and exit",
	)
	flag.BoolVar(
		&actionLicenses,
		"licenses",
		false,
		"Print license information",
	)
	flag.BoolVar(
		&healthCheck,
		"healthcheck",
		false,
		"Run health check",
	)
	flag.Parse()
	return configFile, actionDumpConfig, actionLicenses, healthCheck
}

func startServices(cfg config.AppConfig, loggerFactory log.LoggerFactory) error {
	pool, lifecycle, err := New(cfg, loggerFactory)
	if err != nil {
		return err
	}

	return startPool(pool, lifecycle)
}

func startPool(pool Service, lifecycle service.Lifecycle) error {
	starting := make(chan struct{})
	lifecycle.OnStarting(
		func(s service.Service, l service.Lifecycle) {
			starting <- struct{}{}
		},
	)
	go func() {
		_ = lifecycle.Run()
	}()

	<-starting

	exitSignalList := []os.Signal{os.Interrupt, os.Kill, syscall.SIGINT, syscall.SIGTERM}
	rotateSignalList := []os.Signal{syscall.SIGHUP}
	exitSignals := make(chan os.Signal, 1)
	rotateSignals := make(chan os.Signal, 1)
	signal.Notify(exitSignals, exitSignalList...)
	signal.Notify(rotateSignals, rotateSignalList...)
	go func() {
		if _, ok := <-exitSignals; ok {
			// ok means the channel wasn't closed
			shutdownContext, cancelFunc := context.WithTimeout(
				context.Background(),
				20*time.Second,
			)
			defer cancelFunc()
			lifecycle.Stop(
				shutdownContext,
			)
		}
	}()
	go func() {
		for {
			if _, ok := <-rotateSignals; ok {
				err := pool.RotateLogs()
				if err != nil {
					panic(err)
				}
			} else {
				break
			}
		}
	}()
	err := lifecycle.Wait()
	signal.Ignore(rotateSignalList...)
	signal.Ignore(exitSignalList...)
	close(exitSignals)
	return err
}

func generateHostKeys(configFile string, cfg *config.AppConfig, logger log.Logger) error {
	if err := cfg.SSH.GenerateHostKey(); err != nil {
		return err
	}

	tmpFile := fmt.Sprintf("%s~", configFile)
	fh, err := os.Create(tmpFile)
	if err != nil {
		logger.Warning(
			message.Wrap(
				err,
				message.ECannotWriteConfigFile,
				"Cannot create temporary configuration file at %s with updated host keys.",
				tmpFile,
			).Label("tmpFile", configFile))
		return nil
	}
	format := getConfigFileFormat(configFile)
	saver, err := internalConfig.NewWriterSaver(fh, logger, format)
	if err != nil {
		_ = fh.Close()
		logger.Warning(
			message.Wrap(
				err,
				message.ECannotWriteConfigFile,
				"Cannot initialize temporary configuration file at %s with updated host keys.",
				tmpFile,
			).Label("tmpFile", configFile))
		return nil
	}
	if err := saver.Save(cfg); err != nil {
		_ = fh.Close()
		logger.Warning(
			message.Wrap(
				err,
				message.ECannotWriteConfigFile,
				"Cannot save temporary configuration file at %s with updated host keys.",
				tmpFile,
			).Label("tmpFile", configFile))
		return nil
	}
	if err := fh.Close(); err != nil {
		logger.Warning(message.Wrap(err,
			message.ECannotWriteConfigFile, "Cannot close temporary configuration file at %s with updated host keys.", tmpFile).Label("tmpFile", configFile))
		return nil
	}

	if err := os.Rename(tmpFile, configFile); err != nil {
		logger.Warning(message.Wrap(err,
			message.ECannotWriteConfigFile, "Failed to rename temporary file %s to %s with updated host keys.", tmpFile, configFile).Label("file", configFile).Label("tmpFile", tmpFile))
		return fmt.Errorf("failed to rename temporary file %s to %s (%w)", tmpFile, configFile, err)
	}

	return nil
}

func healthCheck(cfg config.AppConfig, logger log.Logger) error {
	healthClient, err := health.NewClient(cfg.Health, logger)
	if err != nil {
		return err
	}
	if healthClient == nil {
		return nil
	}
	if !healthClient.Run() {
		return message.NewMessage(message.ECoreHealthCheckFailed, "Health check failed")
	}
	return nil
}

func printLicenses(writer io.Writer) error {
	var buffer bytes.Buffer

	buffer.WriteString("# The ContainerSSH license\n\n")
	licenseData, err := os.ReadFile("LICENSE.md")
	if err != nil {
		return fmt.Errorf("failed to read LICENSE.md (%w)", err)
	}
	buffer.Write(licenseData)
	buffer.WriteString("\n")
	noticeData, err := os.ReadFile("NOTICE.md")
	if err != nil {
		return fmt.Errorf("failed to read NOTICE.md (%w)", err)
	}
	buffer.Write(noticeData)
	buffer.WriteString("\n")
	if _, err := writer.Write(buffer.Bytes()); err != nil {
		return fmt.Errorf("failed to write licenes information (%w)", err)
	}
	return nil
}

func dumpConfig(writer io.Writer, logger log.Logger, cfg *config.AppConfig) error {
	saver, err := internalConfig.NewWriterSaver(writer, logger, internalConfig.FormatYAML)
	if err != nil {
		return err
	}
	if err := saver.Save(cfg); err != nil {
		return err
	}
	return nil
}

func readConfigFile(
	configFile string,
	loggerFactory log.LoggerFactory,
	cfg *config.AppConfig,
) error {
	configLogger, err := loggerFactory.Make(
		cfg.Log,
	)
	if err != nil {
		return err
	}
	// File inclusion is desired here, no gosec issue.
	configFH, err := os.Open(configFile) //nolint:gosec
	if err != nil {
		return err
	}
	format := getConfigFileFormat(configFile)
	configLoader, err := internalConfig.NewReaderLoader(configFH, configLogger, format)
	if err != nil {
		return err
	}
	if err := configLoader.Load(context.Background(), cfg); err != nil {
		return fmt.Errorf("failed to read configuration file %s (%w)", configFile, err)
	}
	return nil
}

func getConfigFileFormat(configFile string) internalConfig.Format {
	var format internalConfig.Format
	if strings.HasSuffix(configFile, ".json") {
		format = internalConfig.FormatJSON
	} else {
		format = internalConfig.FormatYAML
	}
	return format
}

