func Example() {
	gen := NewGenerator(NewLogger(os.Stdout, os.Stderr, false, true),
		&Config{
			Bin:        "git",
			WorkingDir: ".",
			Template:   "CHANGELOG.tpl.md",
			Info: &Info{
				Title:         "CHANGELOG",
				RepositoryURL: "https://github.com/git-chglog/git-chglog",
			},
			Options: &Options{
				CommitFilters: map[string][]string{
					"Type": {
						"feat",
						"fix",
					},
				},
				CommitSortBy:      "Scope",
				CommitGroupBy:     "Type",
				CommitGroupSortBy: "Title",
				CommitGroupTitleMaps: map[string]string{
					"feat": "Features",
					"fix":  "Bug Fixes",
				},
				HeaderPattern: "^(\\w*)(?:\\(([\\w\\$\\.\\-\\*\\s]*)\\))?\\:\\s(.*)$",
				HeaderPatternMaps: []string{
					"Type",
					"Scope",
					"Subject",
				},
				IssuePrefix: []string{
					"#",
					"gh-",
				},
				RefActions:   []string{},
				MergePattern: "^Merge pull request #(\\d+) from (.*)$",
				MergePatternMaps: []string{
					"Ref",
					"Source",
				},
				RevertPattern: "^Revert \"([\\s\\S]*)\"$",
				RevertPatternMaps: []string{
					"Header",
				},
				NoteKeywords: []string{
					"BREAKING CHANGE",
				},
			},
		})

	buf := &bytes.Buffer{}
	err := gen.Generate(buf, "")

	if err != nil {
		log.Fatalln(err)
	}

	fmt.Println(buf.String())
}

package chglog

import "time"

// Hash of commit
type Hash struct {
	Long  string
	Short string
}

// Contact of co-authors and signers
type Contact struct {
	Name  string
	Email string
}

// Author of commit
type Author struct {
	Name  string
	Email string
	Date  time.Time
}

// Committer of commit
type Committer struct {
	Name  string
	Email string
	Date  time.Time
}

// Merge info for commit
type Merge struct {
	Ref    string
	Source string
}

// Revert info for commit
type Revert struct {
	Header string
}

// Ref is abstract data related to commit. (e.g. `Issues`, `Pull Request`)
type Ref struct {
	Action string // (e.g. `Closes`)
	Ref    string // (e.g. `123`)
	Source string // (e.g. `owner/repository`)
}

// Note of commit
type Note struct {
	Title string // (e.g. `BREAKING CHANGE`)
	Body  string // `Note` content body
}

// NoteGroup is a collection of `Note` grouped by titles
type NoteGroup struct {
	Title string
	Notes []*Note
}

// JiraIssue is information about a jira ticket (type, summary, description, and labels)
type JiraIssue struct {
	Type        string
	Summary     string
	Description string
	Labels      []string
}

// Commit data
type Commit struct {
	Hash        *Hash
	Author      *Author
	Committer   *Committer
	Merge       *Merge  // If it is not a merge commit, `nil` is assigned
	Revert      *Revert // If it is not a revert commit, `nil` is assigned
	Refs        []*Ref
	Notes       []*Note
	Mentions    []string   // Name of the user included in the commit header or body
	CoAuthors   []Contact  // (e.g. `Co-authored-by: user <user@email>`)
	Signers     []Contact  // (e.g. `Signed-off-by: user <user@email>`)
	JiraIssue   *JiraIssue // If no issue id found in header, `nil` is assigned
	Header      string     // (e.g. `feat(core)[RNWY-310]: Add new feature`)
	Type        string     // (e.g. `feat`)
	Scope       string     // (e.g. `core`)
	Subject     string     // (e.g. `Add new feature`)
	JiraIssueID string     // (e.g. `RNWY-310`)
	Body        string
	TrimmedBody string // Body without any Notes/Refs/Mentions/CoAuthors/Signers
}

// CommitGroup is a collection of commits grouped according to the `CommitGroupBy` option
type CommitGroup struct {
	RawTitle string // Raw title before conversion (e.g. `build`)
	Title    string // Conversion by `CommitGroupTitleMaps` option, or title converted in title case (e.g. `Build`)
	Commits  []*Commit
}

// RelateTag is sibling tag data of `Tag`.
// If you give `Tag`, the reference hierarchy will be deepened.
// This struct is used to minimize the hierarchy of references
type RelateTag struct {
	Name    string
	Subject string
	Date    time.Time
}

// Tag is data of git-tag
type Tag struct {
	Name     string
	Subject  string
	Date     time.Time
	Next     *RelateTag
	Previous *RelateTag
}

// Version is a tag-separeted datset to be included in CHANGELOG
type Version struct {
	Tag           *Tag
	CommitGroups  []*CommitGroup
	Commits       []*Commit
	MergeCommits  []*Commit
	RevertCommits []*Commit
	NoteGroups    []*NoteGroup
}

// Unreleased is unreleased commit dataset
type Unreleased struct {
	CommitGroups  []*CommitGroup
	Commits       []*Commit
	MergeCommits  []*Commit
	RevertCommits []*Commit
	NoteGroups    []*NoteGroup
}

package chglog

import (
	agjira "github.com/andygrunwald/go-jira"
)

// JiraClient is an HTTP client for Jira
type JiraClient interface {
	GetJiraIssue(id string) (*agjira.Issue, error)
}

type jiraClient struct {
	username string
	token    string
	url      string
}

// NewJiraClient returns an instance of JiraClient
func NewJiraClient(config *Config) JiraClient {
	return jiraClient{
		username: config.Options.JiraUsername,
		token:    config.Options.JiraToken,
		url:      config.Options.JiraURL,
	}
}

func (jira jiraClient) GetJiraIssue(id string) (*agjira.Issue, error) {
	tp := agjira.BasicAuthTransport{
		Username: jira.username,
		Password: jira.token,
	}
	client, err := agjira.NewClient(tp.Client(), jira.url)
	if err != nil {
		return nil, err
	}
	issue, _, err := client.Issue.Get(id, nil)
	return issue, err
}

package chglog

import (
	"fmt"
	"io"
	"log"
	"regexp"

	"github.com/fatih/color"
	"github.com/kyokomi/emoji/v2"
)

// Logger ...
type Logger struct {
	stdout  io.Writer
	stderr  io.Writer
	silent  bool
	noEmoji bool
	reEmoji *regexp.Regexp
}

// NewLogger ...
func NewLogger(stdout, stderr io.Writer, silent, noEmoji bool) *Logger {
	return &Logger{
		stdout:  stdout,
		stderr:  stderr,
		silent:  silent,
		noEmoji: noEmoji,
		reEmoji: regexp.MustCompile(`:[\w\+_\-]+:\s?`),
	}
}

// Log ...
func (l *Logger) Log(msg string) {
	if !l.silent {
		l.log(l.stdout, msg+"\n")
	}
}

// Error ...
func (l *Logger) Error(msg string) {
	prefix := color.New(color.FgWhite, color.BgRed, color.Bold).SprintFunc()
	l.log(l.stderr, fmt.Sprintf("%s %s\n", prefix(" ERROR "), color.RedString(msg)))
}

func (l *Logger) log(w io.Writer, msg string) {
	var printer func(io.Writer, ...interface{}) (int, error)

	if l.noEmoji {
		msg = l.reEmoji.ReplaceAllString(msg, "")
		printer = fmt.Fprint
	} else {
		printer = emoji.Fprint
	}

	if _, err := printer(w, msg); err != nil {
		log.Fatalln(err)
	}
}

package chglog

import (
