			"v2.0.0-beta.1",
			"v1.2.9",
			"v1.0.0",
			"",
		},
		// <tag> ~ <tag>
		"v1.0.0..2.2.12-rc.12": {
			"2.2.12-rc.12",
			"2.1.0",
			"v2.0.0-beta.1",
			"v1.2.9",
			"v1.0.0",
			"",
		},
		"v1.0.0..v2.0.0-beta.1": {
			"v2.0.0-beta.1",
