			actual[i] = tag.Name
		}

		assert.Nil(err)
		assert.Equal(expected[0:len(expected)-1], actual)
		assert.Equal(expected[len(expected)-1], from)
	}
}

package chglog

import (
	"fmt"
	"reflect"
	"strings"
	"time"
)

func dotGet(target interface{}, prop string) (interface{}, bool) {
	path := strings.Split(prop, ".")

	if len(path) == 0 {
		return nil, false
	}

	for _, key := range path {
		var value reflect.Value

		if reflect.TypeOf(target).Kind() == reflect.Ptr {
			value = reflect.ValueOf(target).Elem()
		} else {
			value = reflect.ValueOf(target)
		}

		//nolint:staticcheck
		field := value.FieldByName(strings.Title(key))
		if !field.IsValid() {
			return nil, false
		}

		target = field.Interface()
	}

	return target, true
}

// TODO: dotSet ...

func assignDynamicValues(target interface{}, attrs []string, values []string) {
	rv := reflect.ValueOf(target).Elem()
	rt := rv.Type()

	for i, field := range attrs {
		if f, ok := rt.FieldByName(field); ok {
			rv.FieldByIndex(f.Index).SetString(values[i])
		}
	}
}

func compare(a interface{}, operator string, b interface{}) (bool, error) {
	at := reflect.TypeOf(a).String()
	bt := reflect.TypeOf(a).String()
	if at != bt {
		return false, fmt.Errorf("\"%s\" and \"%s\" can not be compared", at, bt)
	}

	switch at {
	case "string":
		aa := a.(string)
		bb := b.(string)
		return compareString(aa, operator, bb), nil
	case "int":
		aa := a.(int)
		bb := b.(int)
		return compareInt(aa, operator, bb), nil
	case "time.Time":
		aa := a.(time.Time)
		bb := b.(time.Time)
		return compareTime(aa, operator, bb), nil
	}

	return false, nil
}

func compareString(a string, operator string, b string) bool {
	switch operator {
	case "<":
		return a < b
	case ">":
		return a > b
	default:
		return false
	}
}

func compareInt(a int, operator string, b int) bool {
	switch operator {
	case "<":
		return a < b
	case ">":
		return a > b
	default:
		return false
	}
}

func compareTime(a time.Time, operator string, b time.Time) bool {
	switch operator {
	case "<":
		return !a.After(b)
	case ">":
		return a.After(b)
	default:
		return false
	}
}

func convNewline(str, nlcode string) string {
	return strings.NewReplacer(
		"\r\n", nlcode,
		"\r", nlcode,
		"\n", nlcode,
	).Replace(str)
}

package chglog

import (
	"testing"
	"time"

	"github.com/stretchr/testify/assert"
)

func TestDotGet(t *testing.T) {
	assert := assert.New(t)
	now := time.Now()

	type Nest struct {
		Str  string
		Int  int
		Time time.Time
	}

	type Sample struct {
		Str  string
		Int  int
		Date time.Time
		Nest Nest
	}

	sample := Sample{
		Str:  "sample_string",
		Int:  12,
		Date: now,
		Nest: Nest{
			Str:  "nest_string",
			Int:  34,
			Time: now,
		},
	}

	var val interface{}
	var ok bool

	// .Str
	val, ok = dotGet(&sample, "Str")
	assert.True(ok)
	assert.Equal(val, "sample_string")

	// Lowercase
	val, ok = dotGet(&sample, "str")
	assert.True(ok)
	assert.Equal(val, "sample_string")

	// Int
	val, ok = dotGet(&sample, "Int")
	assert.True(ok)
	assert.Equal(val, 12)

	// Time
	val, ok = dotGet(&sample, "Date")
	assert.True(ok)
	assert.Equal(val, now)

	// Nest
	val, ok = dotGet(&sample, "Nest.Str")
	assert.True(ok)
	assert.Equal(val, "nest_string")

	val, ok = dotGet(&sample, "Nest.Int")
	assert.True(ok)
	assert.Equal(val, 34)

	val, ok = dotGet(&sample, "Nest.Time")
	assert.True(ok)
	assert.Equal(val, now)

	val, ok = dotGet(&sample, "nest.int")
	assert.True(ok)
	assert.Equal(val, 34)

	// Notfound
	val, ok = dotGet(&sample, "not.found")
	assert.False(ok)
	assert.Nil(val)
}

func TestCompare(t *testing.T) {
	assert := assert.New(t)

	type sample struct {
		a        interface{}
		op       string
		b        interface{}
		expected bool
	}

	table := []sample{
		{0, "<", 1, true},
		{0, ">", 1, false},
		{1, ">", 0, true},
		{1, "<", 0, false},
		{"a", "<", "b", true},
		{"a", ">", "b", false},
		{time.Unix(1518018017, 0), "<", time.Unix(1518018043, 0), true},
		{time.Unix(1518018017, 0), ">", time.Unix(1518018043, 0), false},
	}

	for _, sa := range table {
		actual, err := compare(sa.a, sa.op, sa.b)
		assert.Nil(err)
		assert.Equal(sa.expected, actual)
	}
}
