package roaring

import (
	"bytes"
	"testing"

	"github.com/RoaringBitmap/roaring/v2/internal"
	"github.com/stretchr/testify/assert"
	"github.com/stretchr/testify/require"
)

func TestByteInputFlow(t *testing.T) {
	t.Run("Test should be an error on empty data", func(t *testing.T) {
		buf := bytes.NewBuffer([]byte{})

		instances := []internal.ByteInput{
			internal.NewByteInput(buf.Bytes()),
			internal.NewByteInputFromReader(buf),
		}

		for _, input := range instances {
			n, err := input.ReadUInt16()

			assert.EqualValues(t, 0, n)
			assert.Error(t, err)

			p, err := input.ReadUInt32()
			assert.EqualValues(t, 0, p)
			assert.Error(t, err)

			b, err := input.Next(10)
			assert.Nil(t, b)
			assert.Error(t, err)

			err = input.SkipBytes(10)
			assert.Error(t, err)
		}
	})

	t.Run("Test on nonempty data", func(t *testing.T) {
		buf := bytes.NewBuffer(uint16SliceAsByteSlice([]uint16{1, 10, 32, 66, 23}))

		instances := []internal.ByteInput{
			internal.NewByteInput(buf.Bytes()),
			internal.NewByteInputFromReader(buf),
		}

		for _, input := range instances {
			n, err := input.ReadUInt16()
			assert.EqualValues(t, 1, n)
			require.NoError(t, err)

			p, err := input.ReadUInt32()
			assert.EqualValues(t, 2097162, p) // 32 << 16 | 10
			require.NoError(t, err)

			b, err := input.Next(2)
			assert.EqualValues(t, []byte{66, 0}, b)
			require.NoError(t, err)

			err = input.SkipBytes(2)
			require.NoError(t, err)

			b, err = input.Next(1)
			assert.Nil(t, b)
			assert.Error(t, err)
		}
	})
}

//go:build go1.9
// +build go1.9

// "go1.9", from Go version 1.9 onward
// See https://golang.org/pkg/go/build/#hdr-Build_Constraints

package roaring

import "math/bits"

// countLeadingOnes returns the number of leading zeros bits in x; the result is 64 for x == 0.
func countLeadingZeros(x uint64) int {
	return bits.LeadingZeros64(x)
}

// countLeadingOnes returns the number of leading ones bits in x; the result is 0 for x == 0.
func countLeadingOnes(x uint64) int {
	return bits.LeadingZeros64(^x)
}

//go:build !go1.9
// +build !go1.9

package roaring

// LeadingZeroBits returns the number of consecutive most significant zero
// bits of x.
func countLeadingZeros(i uint64) int {
	if i == 0 {
		return 64
	}
	n := 1
	x := uint32(i >> 32)
	if x == 0 {
		n += 32
		x = uint32(i)
	}
	if (x >> 16) == 0 {
		n += 16
		x <<= 16
	}
	if (x >> 24) == 0 {
		n += 8
		x <<= 8
	}
	if x>>28 == 0 {
		n += 4
		x <<= 4
	}
	if x>>30 == 0 {
		n += 2
		x <<= 2

	}
	n -= int(x >> 31)
	return n
}

