		bm2 := NewBitmap()
		start := uint64(2 << 16)
		bm2.AddRange(start, start+3)

		assert.Equal(t, uint64(1027), bm.OrCardinality(bm2))
	})

	t.Run("Merge large into small same keyrange start", func(t *testing.T) {
