		assert.Equal(t, int64(64), bmp.NextValue(0))
		assert.Equal(t, int64(64), bmp.NextValue(63))
		assert.Equal(t, int64(64), bmp.NextValue(64))
		assert.Equal(t, int64(65), bmp.NextValue(65))
