	// missort
	rc = &runContainer16{}
	rc.iv = append(rc.iv, b, a)
	assert.ErrorIs(t, rc.validate(), ErrRunNonSorted)

	rc = &runContainer16{}
	start := -4
	for i := 0; i < MaxNumIntervals; i++ {
		start += 4
		end := start + 2
		a := newInterval16Range(uint16(start), uint16(end))
