func TestArray_IsOrdered(t *testing.T) {
	type args struct {
		values      []interface{}
		less        []func(x, y *Value) bool
		chainFailed bool
	}
	cases := []struct {
		name        string
		args        args
		isInvalid   bool
		isOrdered   bool
		isUnordered bool
	}{
		{
			name: "booleans ordered",
			args: args{
				values: []interface{}{false, false, true, true},
			},
			isInvalid:   false,
			isOrdered:   true,
			isUnordered: false,
		},
		{
			name: "booleans unordered",
			args: args{
				values: []interface{}{true, false, true, false},
			},
			isInvalid:   false,
			isOrdered:   false,
			isUnordered: true,
		},
		{
			name: "numbers ordered",
			args: args{
				values: []interface{}{1, 1, 2, 3},
			},
			isInvalid:   false,
			isOrdered:   true,
			isUnordered: false,
		},
		{
			name: "numbers unordered",
			args: args{
				values: []interface{}{3, 1, 1, 2},
			},
			isInvalid:   false,
			isOrdered:   false,
			isUnordered: true,
		},
		{
			name: "strings ordered",
			args: args{
				values: []interface{}{"", "a", "b", "ba"},
			},
			isInvalid:   false,
			isOrdered:   true,
			isUnordered: false,
		},
		{
			name: "strings unordered",
			args: args{
				values: []interface{}{"z", "y", "x", ""},
			},
			isInvalid:   false,
			isOrdered:   false,
			isUnordered: true,
		},
		{
			name: "all nils",
			args: args{
				values: []interface{}{nil, nil, nil},
			},
			isInvalid:   false,
			isOrdered:   true,
			isUnordered: false,
		},
		{
			name: "reversed",
			args: args{
				values: []interface{}{3, 2, 1},
			},
			isInvalid:   false,
			isOrdered:   false,
			isUnordered: true,
		},
		{
			name: "user-defined less function",
			args: args{
				values: []interface{}{1, 2, 3},
				less: []func(x, y *Value) bool{
					func(x, y *Value) bool {
						valX := x.Number().Raw()
						valY := y.Number().Raw()
						return valX < valY
					},
				},
			},
			isInvalid:   false,
			isOrdered:   true,
			isUnordered: false,
		},
		{
			name: "user-defined less function, negated",
			args: args{
				values: []interface{}{1, 2, 3},
				less: []func(x, y *Value) bool{
					func(x, y *Value) bool {
						valX := x.Number().Raw()
						valY := y.Number().Raw()
						return valX >= valY
					},
				},
			},
			isInvalid:   false,
			isOrdered:   false,
			isUnordered: true,
		},
		{
			name: "empty array",
			args: args{
				values: []interface{}{},
			},
			isInvalid:   false,
			isOrdered:   true,
			isUnordered: true,
		},
		{
			name: "one element",
			args: args{
				values: []interface{}{1},
			},
			isInvalid:   false,
			isOrdered:   true,
			isUnordered: true,
		},
		{
			name: "empty array, custom func",
			args: args{
				values: []interface{}{},
				less: []func(x, y *Value) bool{
					func(x, y *Value) bool {
						panic("test")
					},
				},
			},
			isInvalid:   false,
			isOrdered:   true,
			isUnordered: true,
		},
		{
			name: "one element, custom func",
			args: args{
				values: []interface{}{1},
				less: []func(x, y *Value) bool{
					func(x, y *Value) bool {
						panic("test")
					},
				},
			},
			isInvalid:   false,
			isOrdered:   true,
			isUnordered: true,
		},
		{
			name: "invalid, assertion failed",
			args: args{
				values: []interface{}{1, 2, 3},
				less: []func(x, y *Value) bool{
					func(x, y *Value) bool {
						x.String()
						y.String()
						return false
					},
				},
			},
			isInvalid: true,
		},
		{
			name: "invalid, multiple arguments",
			args: args{
				values: []interface{}{1, 2, 3},
				less: []func(x, y *Value) bool{
					func(x, y *Value) bool {
						return false
					},
					func(x, y *Value) bool {
						return true
					},
				},
			},
			isInvalid: true,
		},
		{
			name: "invalid, nil argument",
			args: args{
				values: []interface{}{1, 2, 3},
				less: []func(x, y *Value) bool{
					nil,
				},
			},
			isInvalid: true,
		},
		{
			name: "invalid, unsupported type",
			args: args{
				values: []interface{}{[]int{1, 2}, []int{3, 4}, []int{5, 6}},
				less:   []func(x, y *Value) bool{},
			},
			isInvalid: true,
		},
		{
			name: "invalid, multiple types",
			args: args{
				values: []interface{}{1, "abc", true},
				less:   []func(x, y *Value) bool{},
			},
			isInvalid: true,
		},
		{
			name: "invalid, failed chain",
			args: args{
				chainFailed: true,
			},
			isInvalid: true,
		},
	}
	for _, tc := range cases {
		t.Run(tc.name, func(t *testing.T) {
			reporter := newMockReporter(t)

			if tc.isInvalid {
				t.Run("normal", func(t *testing.T) {
					NewArray(reporter, tc.args.values).IsOrdered(tc.args.less...).
						chain.assert(t, failure)

					NewArray(reporter, tc.args.values).NotOrdered(tc.args.less...).
						chain.assert(t, failure)
				})

				t.Run("reversed", func(t *testing.T) {
					// reverse slice
					sort.SliceStable(tc.args.values, func(i, j int) bool {
						return i > j
					})

					NewArray(reporter, tc.args.values).IsOrdered(tc.args.less...).
						chain.assert(t, failure)

					NewArray(reporter, tc.args.values).NotOrdered(tc.args.less...).
						chain.assert(t, failure)
				})
			} else {
				t.Run("is ordered", func(t *testing.T) {
					if tc.isOrdered {
						NewArray(reporter, tc.args.values).IsOrdered(tc.args.less...).
							chain.assert(t, success)
					} else {
						NewArray(reporter, tc.args.values).IsOrdered(tc.args.less...).
							chain.assert(t, failure)
					}
				})

				t.Run("not ordered", func(t *testing.T) {
					if tc.isUnordered {
						NewArray(reporter, tc.args.values).NotOrdered(tc.args.less...).
							chain.assert(t, success)
					} else {
						NewArray(reporter, tc.args.values).NotOrdered(tc.args.less...).
							chain.assert(t, failure)
					}
				})
			}
		})
	}
}

func TestArray_ComparatorErrors(t *testing.T) {
	t.Run("nil slice", func(t *testing.T) {
		chain := newMockChain(t).enter("test")

		fn := builtinComparator(chain, nil)

		assert.Nil(t, fn)
		chain.assert(t, success)
	})

	t.Run("0 elements", func(t *testing.T) {
		chain := newMockChain(t).enter("test")

		fn := builtinComparator(chain, []interface{}{})

		assert.Nil(t, fn)
		chain.assert(t, success)
	})

	t.Run("1 element", func(t *testing.T) {
		chain := newMockChain(t).enter("test")

		fn := builtinComparator(chain, []interface{}{
			"test",
		})

		assert.Nil(t, fn)
		chain.assert(t, success)
	})

	t.Run("2 elements, bad_type", func(t *testing.T) {
		chain := newMockChain(t).enter("test")

		fn := builtinComparator(chain, []interface{}{
			"test",
			make(chan int), // bad type
		})

