package httpexpect

import (
	"bytes"
	"crypto/tls"
	"fmt"
	"io"
	"net"
	"net/http"
	"net/http/httptest"
	"strconv"
	"strings"

	"github.com/valyala/fasthttp"
)

// Binder implements networkless http.RoundTripper attached directly to
// http.Handler.
//
// Binder emulates network communication by invoking given http.Handler
// directly. It passes httptest.ResponseRecorder as http.ResponseWriter
// to the handler, and then constructs http.Response from recorded data.
type Binder struct {
	// HTTP handler invoked for every request.
	Handler http.Handler
	// TLS connection state used for https:// requests.
	TLS *tls.ConnectionState
}

// NewBinder returns a new Binder given a http.Handler.
//
// Example:
//
//	client := &http.Client{
//		Transport: NewBinder(handler),
//	}
func NewBinder(handler http.Handler) Binder {
	return Binder{Handler: handler}
}

// RoundTrip implements http.RoundTripper.RoundTrip.
func (binder Binder) RoundTrip(origReq *http.Request) (*http.Response, error) {
	req := *origReq

	if req.Proto == "" {
		req.Proto = fmt.Sprintf("HTTP/%d.%d", req.ProtoMajor, req.ProtoMinor)
	}

	if req.Body != nil && req.Body != http.NoBody {
		if req.ContentLength == -1 {
			req.TransferEncoding = []string{"chunked"}
		}
	} else {
		req.Body = http.NoBody
	}

	if req.URL != nil && req.URL.Scheme == "https" && binder.TLS != nil {
		req.TLS = binder.TLS
	}

	if req.RequestURI == "" {
		req.RequestURI = req.URL.RequestURI()
	}

	recorder := httptest.NewRecorder()

	binder.Handler.ServeHTTP(recorder, &req)

	resp := http.Response{
		Request:    &req,
		StatusCode: recorder.Code,
		Status:     http.StatusText(recorder.Code),
		Header:     recorder.Result().Header,
	}

	if recorder.Flushed {
		resp.TransferEncoding = []string{"chunked"}
	}

	if recorder.Body != nil {
		resp.Body = io.NopCloser(recorder.Body)
	}

	return &resp, nil
}

// FastBinder implements networkless http.RoundTripper attached directly
// to fasthttp.RequestHandler.
//
// FastBinder emulates network communication by invoking given fasthttp.RequestHandler
// directly. It converts http.Request to fasthttp.Request, invokes handler, and then
// converts fasthttp.Response to http.Response.
type FastBinder struct {
	// FastHTTP handler invoked for every request.
	Handler fasthttp.RequestHandler
	// TLS connection state used for https:// requests.
	TLS *tls.ConnectionState
	// If non-nil, fasthttp.RequestCtx.Logger() will print messages to it.
	Logger Logger
}

// NewFastBinder returns a new FastBinder given a fasthttp.RequestHandler.
//
// Example:
//
//	client := &http.Client{
//		Transport: NewFastBinder(fasthandler),
//	}
func NewFastBinder(handler fasthttp.RequestHandler) FastBinder {
	return FastBinder{Handler: handler}
}

// RoundTrip implements http.RoundTripper.RoundTrip.
func (binder FastBinder) RoundTrip(stdreq *http.Request) (*http.Response, error) {
	fastreq := std2fast(stdreq)

	var conn net.Conn
	if stdreq.URL != nil && stdreq.URL.Scheme == "https" && binder.TLS != nil {
		conn = connTLS{state: binder.TLS}
	} else {
		conn = connNonTLS{}
	}

	ctx := fasthttp.RequestCtx{}
	log := fastLogger{binder.Logger}
	ctx.Init2(conn, log, true)

	fastreq.CopyTo(&ctx.Request)

	if stdreq.RemoteAddr != "" {
		var parts = strings.SplitN(stdreq.RemoteAddr, ":", 2)
		host := parts[0]
		port := 0
		if len(parts) > 1 {
			port, _ = strconv.Atoi(parts[1])
		}
		ctx.SetRemoteAddr(&net.TCPAddr{
			IP:   net.ParseIP(host),
			Port: port,
		})
	}

	if stdreq.ContentLength >= 0 {
		ctx.Request.Header.SetContentLength(int(stdreq.ContentLength))
	} else {
		ctx.Request.Header.SetContentLength(-1)
		ctx.Request.Header.Add("Transfer-Encoding", "chunked")
	}

	if stdreq.Body != nil {
		b, err := io.ReadAll(stdreq.Body)
		if err == nil {
			ctx.Request.SetBody(b)
		}
	}

	binder.Handler(&ctx)

	return fast2std(stdreq, &ctx.Response), nil
}

func std2fast(stdreq *http.Request) *fasthttp.Request {
	fastreq := &fasthttp.Request{}

	fastreq.SetRequestURI(stdreq.URL.String())

	if stdreq.Proto != "" {
		fastreq.Header.SetProtocol(stdreq.Proto)
	} else if stdreq.ProtoMajor != 0 || stdreq.ProtoMinor != 0 {
		fastreq.Header.SetProtocol(
			fmt.Sprintf("HTTP/%d.%d", stdreq.ProtoMajor, stdreq.ProtoMinor))
	}

	fastreq.Header.SetMethod(stdreq.Method)

	if stdreq.Host != "" {
		fastreq.Header.SetHost(stdreq.Host)
	}

	for k, a := range stdreq.Header {
		for n, v := range a {
			if n == 0 {
				fastreq.Header.Set(k, v)
			} else {
				fastreq.Header.Add(k, v)
			}
		}
	}

	fastreq.Header.SetContentLength(int(stdreq.ContentLength))

	return fastreq
}

func fast2std(stdreq *http.Request, fastresp *fasthttp.Response) *http.Response {
	status := fastresp.Header.StatusCode()
	body := fastresp.Body()

	stdresp := &http.Response{
		StatusCode: status,
		Status:     http.StatusText(status),
		Request:    stdreq,
	}

	fastresp.Header.VisitAll(func(k, v []byte) {
		sk := string(k)
		sv := string(v)
		if stdresp.Header == nil {
			stdresp.Header = make(http.Header)
		}
		stdresp.Header.Add(sk, sv)
	})

	if fastresp.Header.ContentLength() >= 0 {
		stdresp.ContentLength = int64(fastresp.Header.ContentLength())
	} else {
		stdresp.ContentLength = -1
		stdresp.TransferEncoding = []string{"chunked"}
	}

	if body != nil {
		stdresp.Body = io.NopCloser(bytes.NewReader(body))
	} else {
		stdresp.Body = io.NopCloser(bytes.NewReader(nil))
	}

	return stdresp
}

type fastLogger struct {
	logger Logger
}

func (f fastLogger) Printf(format string, args ...interface{}) {
	if f.logger != nil {
		f.logger.Logf(format, args...)
	}
}

type connNonTLS struct {
	net.Conn
}

func (connNonTLS) RemoteAddr() net.Addr {
	return &net.TCPAddr{IP: net.IPv4zero}
}

func (connNonTLS) LocalAddr() net.Addr {
	return &net.TCPAddr{IP: net.IPv4zero}
}

type connTLS struct {
	connNonTLS
	state *tls.ConnectionState
}

func (c connTLS) Handshake() error {
	return nil
}

func (c connTLS) ConnectionState() tls.ConnectionState {
	return *c.state
}

package httpexpect

import (
	"bufio"
	"crypto/tls"
	"io"
	"net/http"
	"strings"
	"testing"

	"github.com/stretchr/testify/assert"
	"github.com/valyala/fasthttp"
)

type mockHandler struct {
	t       *testing.T
	https   bool
	http10  bool
	chunked bool
}

func (c *mockHandler) ServeHTTP(w http.ResponseWriter, req *http.Request) {
	body, err := io.ReadAll(req.Body)
	assert.True(c.t, err == nil)

	if c.http10 {
		assert.Equal(c.t, "HTTP/1.0", req.Proto)
		assert.Equal(c.t, 1, req.ProtoMajor)
		assert.Equal(c.t, 0, req.ProtoMinor)
	} else {
		assert.Equal(c.t, "HTTP/1.1", req.Proto)
		assert.Equal(c.t, 1, req.ProtoMajor)
		assert.Equal(c.t, 1, req.ProtoMinor)
	}
	if c.https {
		assert.Equal(c.t, "https://example.com/path", req.URL.String())
	} else {
		assert.Equal(c.t, "http://example.com/path", req.URL.String())
	}
	assert.Equal(c.t, "/path", req.URL.RequestURI())
	assert.Equal(c.t, "/path", req.RequestURI)
	assert.Equal(c.t, "GET", req.Method)
	assert.Equal(c.t, "body", string(body))

	w.Header().Set("Content-Type", "application/json")
	w.WriteHeader(http.StatusOK)
	_, err = w.Write([]byte(`{"hello":"world"}`))
	if c.chunked {
		w.(http.Flusher).Flush()
	}

	assert.True(c.t, err == nil)
}

func TestBinder_Basic(t *testing.T) {
	handler := &mockHandler{
		t:       t,
		http10:  true,
		chunked: false,
	}

	client := &http.Client{
		Transport: NewBinder(handler),
	}

	req, err := http.NewRequest("GET", "http://example.com/path", strings.NewReader("body"))
	if err != nil {
		t.Fatal(err)
	}

	req.ProtoMajor = 1
	req.ProtoMinor = 0
	req.Proto = ""

	resp, err := client.Do(req)
	if err != nil {
		t.Fatal(err)
	}

	header := http.Header{
		"Content-Type": {"application/json"},
	}

	b, err := io.ReadAll(resp.Body)
	if err != nil {
		t.Fatal(err)
	}

	assert.Equal(t, header, resp.Header)
	assert.Equal(t, `{"hello":"world"}`, string(b))

	assert.Equal(t, []string(nil), resp.TransferEncoding)
}

func TestBinder_TLS(t *testing.T) {
	handler := &mockHandler{t: t}

	httpClient := &http.Client{
		Transport: &Binder{
			Handler: handler,
			TLS:     nil,
		},
	}

	tlsState := &tls.ConnectionState{}

	httpsClient := &http.Client{
		Transport: &Binder{
			Handler: handler,
			TLS:     tlsState,
		},
	}

	t.Run("handler=no url=no client=no", func(t *testing.T) {
		handler.https = false
		req, _ := http.NewRequest("GET", "http://example.com/path", strings.NewReader("body"))
		resp, err := httpClient.Do(req)
		assert.Nil(t, err)
		assert.NotNil(t, resp)
		assert.Nil(t, resp.Request.TLS)
	})

	t.Run("handler=yes url=yes client=no", func(t *testing.T) {
		handler.https = true
		req, _ := http.NewRequest("GET", "https://example.com/path", strings.NewReader("body"))
		resp, err := httpClient.Do(req)
		assert.Nil(t, err)
		assert.NotNil(t, resp)
		assert.Nil(t, resp.Request.TLS)
	})

	t.Run("handler=no url=no client=yes", func(t *testing.T) {
		handler.https = false
		req, _ := http.NewRequest("GET", "http://example.com/path", strings.NewReader("body"))
		resp, err := httpsClient.Do(req)
		assert.Nil(t, err)
		assert.NotNil(t, resp)
		assert.Nil(t, resp.Request.TLS)
	})

	t.Run("handler=yes url=yes client=yes", func(t *testing.T) {
		handler.https = true
		req, _ := http.NewRequest("GET", "https://example.com/path", strings.NewReader("body"))
		resp, err := httpsClient.Do(req)
		assert.Nil(t, err)
		assert.NotNil(t, resp)
		assert.NotNil(t, resp.Request.TLS)
		assert.Same(t, tlsState, resp.Request.TLS)
	})
}

func TestBinder_Chunked(t *testing.T) {
	handler := &mockHandler{
		t:       t,
		chunked: true,
	}

	client := &http.Client{
		Transport: NewBinder(handler),
	}

	req, err := http.NewRequest("GET", "http://example.com/path", strings.NewReader("body"))
	if err != nil {
		t.Fatal(err)
	}

	req.ContentLength = -1

	resp, err := client.Do(req)
	if err != nil {
		t.Fatal(err)
	}

	assert.Equal(t, []string{"chunked"}, resp.Request.TransferEncoding)
	assert.Equal(t, []string{"chunked"}, resp.TransferEncoding)
}

func TestFastBinder_Basic(t *testing.T) {
	handler := func(ctx *fasthttp.RequestCtx) {
		assert.Equal(t, "POST", string(ctx.Request.Header.Method()))
		assert.Equal(t, "http://example.com/path", string(ctx.Request.Header.RequestURI()))

		assert.Equal(t, "application/x-www-form-urlencoded",
			string(ctx.Request.Header.ContentType()))

		assert.NotNil(t, ctx.LocalAddr())
		assert.Equal(t, "tcp", ctx.LocalAddr().Network())
		assert.Equal(t, "0.0.0.0:0", ctx.LocalAddr().String())

		assert.NotNil(t, ctx.RemoteAddr())
		assert.Equal(t, "tcp", ctx.RemoteAddr().Network())
		assert.Equal(t, "0.0.0.0:0", ctx.RemoteAddr().String())

		assert.False(t, ctx.IsTLS())
		assert.Nil(t, ctx.TLSConnectionState())

		headers := map[string][]string{}

		ctx.Request.Header.VisitAll(func(k, v []byte) {
			headers[string(k)] = append(headers[string(k)], string(v))
		})

		expected := map[string][]string{
			"Host":           {"example.com"},
			"Content-Type":   {"application/x-www-form-urlencoded"},
			"Content-Length": {"7"},
			"Some-Header":    {"foo", "bar"},
		}

		assert.Equal(t, expected, headers)

		assert.Equal(t, "bar", string(ctx.FormValue("foo")))
		assert.Equal(t, "foo=bar", string(ctx.Request.Body()))

		ctx.Response.Header.Set("Content-Type", "application/json")
		ctx.Response.SetBody([]byte(`{"hello":"world"}`))
	}

	client := &http.Client{
		Transport: NewFastBinder(handler),
	}

	req, err := http.NewRequest(
		"POST", "http://example.com/path", strings.NewReader("foo=bar"))

	if err != nil {
		t.Fatal(err)
	}

	req.Header.Set("Content-Type", "application/x-www-form-urlencoded")
	req.Header.Add("Some-Header", "foo")
	req.Header.Add("Some-Header", "bar")

	resp, err := client.Do(req)
	if err != nil {
		t.Fatal(err)
	}

	header := http.Header{
		"Content-Type": {"application/json"},
	}

	b, err := io.ReadAll(resp.Body)
	if err != nil {
		t.Fatal(err)
	}

	assert.Equal(t, header, resp.Header)
	assert.Equal(t, `{"hello":"world"}`, string(b))

	assert.Equal(t, []string(nil), resp.TransferEncoding)
}

func TestFastBinder_RemoteAddr(t *testing.T) {
	handler := func(ctx *fasthttp.RequestCtx) {
		assert.NotNil(t, ctx.RemoteAddr())
		assert.Equal(t, "tcp", ctx.RemoteAddr().Network())
		assert.Equal(t, "8.8.8.8:88", ctx.RemoteAddr().String())

		ctx.Response.SetBody([]byte(`ok`))
	}

	client := &http.Client{
		Transport: NewFastBinder(handler),
	}

	req, err := http.NewRequest(
		"POST", "http://example.com/path", strings.NewReader("foo=bar"))

	if err != nil {
		t.Fatal(err)
	}

	req.RemoteAddr = "8.8.8.8:88"

	resp, err := client.Do(req)
	if err != nil {
		t.Fatal(err)
	}

	b, err := io.ReadAll(resp.Body)
	if err != nil {
		t.Fatal(err)
	}

	assert.Equal(t, `ok`, string(b))
}

func TestFastBinder_Protocol(t *testing.T) {
	test := func(setProto func(req *http.Request)) {
		handler := func(ctx *fasthttp.RequestCtx) {
			assert.Equal(t, "HTTP/1.0", string(ctx.Request.Header.Protocol()))

			ctx.Response.SetBody([]byte(`ok`))
		}

		client := &http.Client{
			Transport: NewFastBinder(handler),
		}

		req, err := http.NewRequest(
			"POST", "http://example.com/path", strings.NewReader("foo=bar"))

		if err != nil {
			t.Fatal(err)
		}

		setProto(req)

		resp, err := client.Do(req)
		if err != nil {
			t.Fatal(err)
		}

		b, err := io.ReadAll(resp.Body)
		if err != nil {
			t.Fatal(err)
		}

		assert.Equal(t, `ok`, string(b))
	}

	t.Run("from string", func(t *testing.T) {
		test(func(req *http.Request) {
			req.Proto = "HTTP/1.0"
		})
	})

	t.Run("from components", func(t *testing.T) {
		test(func(req *http.Request) {
			req.Proto = ""
			req.ProtoMajor = 1
			req.ProtoMinor = 0
		})
	})
}

func TestFastBinder_TLS(t *testing.T) {
	var isHTTPS, isTLS bool

	tlsState := &tls.ConnectionState{
		Version: tls.VersionTLS12,
	}

	handler := func(ctx *fasthttp.RequestCtx) {
		isHTTPS = strings.HasPrefix(string(ctx.Request.Header.RequestURI()), "https://")
		isTLS = ctx.IsTLS()
		if isTLS {
			assert.Equal(t, *tlsState, *ctx.TLSConnectionState())
		}
	}

	httpClient := &http.Client{
		Transport: &FastBinder{
			Handler: handler,
			TLS:     nil,
		},
	}

	httpsClient := &http.Client{
		Transport: &FastBinder{
			Handler: handler,
			TLS:     tlsState,
		},
	}

	t.Run("url=no client=no", func(t *testing.T) {
		req, _ := http.NewRequest("GET", "http://example.com/path", strings.NewReader("body"))
		resp, err := httpClient.Do(req)
		assert.Nil(t, err)
		assert.NotNil(t, resp)
		assert.False(t, isHTTPS)
		assert.False(t, isTLS)
	})

	t.Run("url=yes client=no", func(t *testing.T) {
		req, _ := http.NewRequest("GET", "https://example.com/path", strings.NewReader("body"))
		resp, err := httpClient.Do(req)
		assert.Nil(t, err)
		assert.NotNil(t, resp)
		assert.True(t, isHTTPS)
		assert.False(t, isTLS)
	})

	t.Run("url=no client=yes", func(t *testing.T) {
		req, _ := http.NewRequest("GET", "http://example.com/path", strings.NewReader("body"))
		resp, err := httpsClient.Do(req)
		assert.Nil(t, err)
		assert.NotNil(t, resp)
		assert.False(t, isHTTPS)
		assert.False(t, isTLS)
	})

	t.Run("url=yes client=yes", func(t *testing.T) {
		req, _ := http.NewRequest("GET", "https://example.com/path", strings.NewReader("body"))
		resp, err := httpsClient.Do(req)
		assert.Nil(t, err)
		assert.NotNil(t, resp)
		assert.True(t, isHTTPS)
		assert.True(t, isTLS)
	})
}

func TestFastBinder_Chunked(t *testing.T) {
	handler := func(ctx *fasthttp.RequestCtx) {
		assert.Equal(t, "POST", string(ctx.Request.Header.Method()))
		assert.Equal(t, "http://example.com/path", string(ctx.Request.Header.RequestURI()))

		assert.Equal(t, "application/x-www-form-urlencoded",
			string(ctx.Request.Header.ContentType()))

		headers := map[string][]string{}

		ctx.Request.Header.VisitAll(func(k, v []byte) {
			headers[string(k)] = append(headers[string(k)], string(v))
		})

		expected := map[string][]string{
			"Host":              {"example.com"},
			"Content-Type":      {"application/x-www-form-urlencoded"},
			"Transfer-Encoding": {"chunked"},
		}

		assert.Equal(t, expected, headers)

		assert.Equal(t, "bar", string(ctx.FormValue("foo")))
		assert.Equal(t, "foo=bar", string(ctx.Request.Body()))

		ctx.Response.Header.Set("Content-Type", "application/json")
		ctx.Response.SetBodyStreamWriter(func(w *bufio.Writer) {
			_, _ = w.WriteString(`[1, `)
			_ = w.Flush()
			_, _ = w.WriteString(`2]`)
		})
	}

	client := &http.Client{
		Transport: NewFastBinder(handler),
	}

	req, err := http.NewRequest(
		"POST", "http://example.com/path", strings.NewReader("foo=bar"))

	if err != nil {
		t.Fatal(err)
	}

	req.Header.Set("Content-Type", "application/x-www-form-urlencoded")

	req.ContentLength = -1

	resp, err := client.Do(req)
	if err != nil {
		t.Fatal(err)
	}

	assert.Equal(t, []string{"chunked"}, resp.TransferEncoding)
}

func TestFastBinder_EmptyResponse(t *testing.T) {
	handler := func(*fasthttp.RequestCtx) {}

	client := &http.Client{
		Transport: NewFastBinder(handler),
	}

	req, err := http.NewRequest("POST", "http://example.com/path", nil)
	if err != nil {
		t.Fatal(err)
	}

	resp, err := client.Do(req)
	if err != nil {
		t.Fatal(err)
	}

	assert.False(t, resp.Body == nil)

	b, err := io.ReadAll(resp.Body)
	if err != nil {
		t.Fatal(err)
	}

	assert.Equal(t, "", string(b))
}

func TestFastBinder_Logger(t *testing.T) {
	handler := func(ctx *fasthttp.RequestCtx) {
		ctx.Logger().Printf("test_message")
	}

	logger := newMockLogger(t)

	client := &http.Client{
		Transport: &FastBinder{
			Handler: handler,
			Logger:  logger,
		},
	}

	req, err := http.NewRequest(
		"POST", "http://example.com/path", strings.NewReader("foo=bar"))

	if err != nil {
		t.Fatal(err)
	}

	_, err = client.Do(req)
	if err != nil {
		t.Fatal(err)
	}

	assert.True(t, logger.logged)
	assert.Contains(t, logger.lastMessage, "test_message")
}

package httpexpect

import (
	"bytes"
	"context"
	"errors"
	"io"
	"runtime"
	"sync"
)

// Wrapper for request or response body reader.
//
// Allows to read body multiple times using two approaches:
//   - use Read to read body contents and Rewind to restart reading from beginning
//   - use GetBody to get new reader for body contents
//
// When bodyWrapper is created, it does not read anything. Also, until anything is
// read, rewind operations are no-op.
//
// When the user starts reading body, bodyWrapper automatically copies retrieved
// content in memory. Then, when the body is fully read and Rewind is requested,
// it will close original body and switch to reading body from memory.
//
// If Rewind, GetBody, or Close is invoked before the body is fully read first time,
// bodyWrapper automatically performs full read.
//
// At any moment, the user can call DisableRewinds. In this case, Rewind and GetBody
// functionality is disabled, memory cache is cleared, and bodyWrapper switches to
// reading original body (if it's not fully read yet).
//
// bodyWrapper automatically creates finalizer that will close original body if the
// user never reads it fully or calls Closes.
type bodyWrapper struct {
	// Protects all operations.
	mu sync.Mutex

	// Original reader of HTTP response body.
	httpReader io.ReadCloser

	// Cancellation function for original HTTP response.
	// If set, called after HTTP response is fully read into memory.
	httpCancelFunc context.CancelFunc

	// Reader for HTTP response body stored in memory.
	// Rewind() resets this reader to start from the beginning.
	memReader *bytes.Reader

	// HTTP response body stored in memory.
	memBytes []byte

	// Cached read and close errors.
	readErr  error
	closeErr error

	// If true, Read will not store bytes in memory, and memBytes and memReader
	// won't be used.
	isRewindDisabled bool

	// True means that HTTP response was fully read into memory already.
	isFullyRead bool

	// True means that a read operation of any type was called at least once.
	isReadBefore bool
}

func newBodyWrapper(reader io.ReadCloser, cancelFunc context.CancelFunc) *bodyWrapper {
	bw := &bodyWrapper{
		httpReader:     reader,
		httpCancelFunc: cancelFunc,
	}

	// Finalizer will close body if closeAndCancel was never called.
	runtime.SetFinalizer(bw, (*bodyWrapper).Close)

	return bw
}

// Read body contents.
func (bw *bodyWrapper) Read(p []byte) (int, error) {
	bw.mu.Lock()
	defer bw.mu.Unlock()

	bw.isReadBefore = true

	if bw.isRewindDisabled && !bw.isFullyRead {
		// Regular read from original HTTP response.
		return bw.httpReader.Read(p)
	} else if !bw.isFullyRead {
		// Read from original HTTP response + store into memory.
		return bw.httpReadNext(p)
	} else {
		// Read from memory.
		return bw.memReadNext(p)
	}
}

// Close body.
func (bw *bodyWrapper) Close() error {
	bw.mu.Lock()
	defer bw.mu.Unlock()

	// Preserve original reader error.
	err := bw.closeErr

	// Rewind or GetBody may be called later, so be sure to
	// read body into memory before closing.
	if !bw.isRewindDisabled && !bw.isFullyRead {
		bw.isReadBefore = true

		if readErr := bw.httpReadFull(); readErr != nil {
			err = readErr
		}
	}

	// Close original reader.
	closeErr := bw.closeAndCancel()
	if closeErr != nil {
		err = closeErr
	}

	// Reset memory reader.
	bw.memReader = bytes.NewReader(nil)

	// Free memory when rewind is disabled.
	if bw.isRewindDisabled {
		bw.memBytes = nil
	}

	return err
}

// Rewind reading to the beginning.
func (bw *bodyWrapper) Rewind() {
	bw.mu.Lock()
	defer bw.mu.Unlock()

	// Rewind is no-op if disabled.
	if bw.isRewindDisabled {
		return
	}

	// Rewind is no-op until first read operation.
	if !bw.isReadBefore {
		return
	}

	// If HTTP response is not fully read yet, do it now.
	// If error occurs, it will be reported next read operation.
	if !bw.isFullyRead {
		_ = bw.httpReadFull()
	}

	// Reset memory reader.
	bw.memReader = bytes.NewReader(bw.memBytes)
}

// Create new reader to retrieve body contents.
// New reader always reads body from the beginning.
// Does not affected by Rewind().
func (bw *bodyWrapper) GetBody() (io.ReadCloser, error) {
	bw.mu.Lock()
	defer bw.mu.Unlock()

	bw.isReadBefore = true

	// Preserve original reader error.
	if bw.readErr != nil {
		return nil, bw.readErr
	}

	// GetBody() requires rewinds to be enabled.
	if bw.isRewindDisabled {
		return nil, errors.New("rewinds are disabled, cannot get body")
	}

	// If HTTP response is not fully read yet, do it now.
	if !bw.isFullyRead {
		if err := bw.httpReadFull(); err != nil {
			return nil, err
		}
	}

	// Return fresh reader for memory chunk.
	return io.NopCloser(bytes.NewReader(bw.memBytes)), nil
}

// Disables storing body contents in memory and clears the cache.
func (bw *bodyWrapper) DisableRewinds() {
	bw.mu.Lock()
	defer bw.mu.Unlock()

	// Free memory if reading from original HTTP response, or reading from memory
	// and memory reader has nothing left to read.
	// Otherwise, i.e. when we're reading from memory, and there is more to read,
	// memReadNext() will free memory later when it hits EOF.
	if !bw.isFullyRead || bw.memReader.Len() == 0 {
		bw.memReader = bytes.NewReader(nil)
		bw.memBytes = nil
	}

	bw.isRewindDisabled = true
}

func (bw *bodyWrapper) memReadNext(p []byte) (int, error) {
	n, err := bw.memReader.Read(p)

	if err == io.EOF {
		// Free memory after we hit EOF when reading from memory,
		// if rewinds were disabled while we were reading from it.
		if bw.isRewindDisabled {
			bw.memReader = bytes.NewReader(nil)
			bw.memBytes = nil
		}
		if bw.readErr != nil {
			err = bw.readErr
		}
	}

	return n, err
}

func (bw *bodyWrapper) httpReadNext(p []byte) (int, error) {
	n, err := bw.httpReader.Read(p)

	if n > 0 {
		bw.memBytes = append(bw.memBytes, p[:n]...)
	}

	if err != nil {
		if err != io.EOF {
			bw.readErr = err
		}
		if closeErr := bw.closeAndCancel(); closeErr != nil && err == io.EOF {
			err = closeErr
		}

		// Switch to reading from memory.
		bw.isFullyRead = true
		bw.memReader = bytes.NewReader(nil)
	}

	return n, err
}

func (bw *bodyWrapper) httpReadFull() error {
	b, err := io.ReadAll(bw.httpReader)

	// Switch to reading from memory.
	bw.isFullyRead = true
	bw.memBytes = append(bw.memBytes, b...)
	bw.memReader = bytes.NewReader(bw.memBytes[len(bw.memBytes)-len(b):])

	if err != nil {
		bw.readErr = err
	}

	if closeErr := bw.closeAndCancel(); closeErr != nil && err == nil {
		err = closeErr
	}

	return err
}

func (bw *bodyWrapper) closeAndCancel() error {
	if bw.httpReader == nil && bw.httpCancelFunc == nil {
		return bw.closeErr
	}

	if bw.httpReader != nil {
		err := bw.httpReader.Close()
		bw.httpReader = nil

		if bw.readErr == nil {
			bw.readErr = err
		}

		if bw.closeErr == nil {
			bw.closeErr = err
		}
	}

	if bw.httpCancelFunc != nil {
		bw.httpCancelFunc()
		bw.httpCancelFunc = nil
	}

	// Finalizer is not needed anymore.
	runtime.SetFinalizer(bw, nil)

	return bw.closeErr
}

package httpexpect

import (
	"errors"
)

// Boolean provides methods to inspect attached bool value
// (Go representation of JSON boolean).
type Boolean struct {
	noCopy noCopy
	chain  *chain
	value  bool
}

// NewBoolean returns a new Boolean instance.
//
// If reporter is nil, the function panics.
//
// Example:
//
//	boolean := NewBoolean(t, true)
//	boolean.IsTrue()
func NewBoolean(reporter Reporter, value bool) *Boolean {
	return newBoolean(newChainWithDefaults("Boolean()", reporter), value)
}

// NewBooleanC returns a new Boolean instance with config.
//
// Requirements for config are same as for WithConfig function.
//
// Example:
//
//	boolean := NewBooleanC(config, true)
//	boolean.IsTrue()
func NewBooleanC(config Config, value bool) *Boolean {
	return newBoolean(newChainWithConfig("Boolean()", config.withDefaults()), value)
}

func newBoolean(parent *chain, val bool) *Boolean {
	return &Boolean{chain: parent.clone(), value: val}
}

// Raw returns underlying value attached to Boolean.
// This is the value originally passed to NewBoolean.
//
// Example:
//
//	boolean := NewBoolean(t, true)
//	assert.Equal(t, true, boolean.Raw())
func (b *Boolean) Raw() bool {
	return b.value
}

// Decode unmarshals the underlying value attached to the Boolean to a target variable.
// target should be one of these:
//
//   - pointer to an empty interface
//   - pointer to a boolean
//
// Example:
//
//	value := NewBoolean(t, true)
//
//	var target bool
//	value.Decode(&target)
//
//	assert.Equal(t, true, target)
func (b *Boolean) Decode(target interface{}) *Boolean {
	opChain := b.chain.enter("Decode()")
	defer opChain.leave()

	if opChain.failed() {
		return b
	}

	canonDecode(opChain, b.value, target)
	return b
}

// Alias is similar to Value.Alias.
func (b *Boolean) Alias(name string) *Boolean {
	opChain := b.chain.enter("Alias(%q)", name)
	defer opChain.leave()

	b.chain.setAlias(name)
	return b
}

// Path is similar to Value.Path.
func (b *Boolean) Path(path string) *Value {
	opChain := b.chain.enter("Path(%q)", path)
	defer opChain.leave()

	return jsonPath(opChain, b.value, path)
}

// Schema is similar to Value.Schema.
func (b *Boolean) Schema(schema interface{}) *Boolean {
	opChain := b.chain.enter("Schema()")
	defer opChain.leave()

	jsonSchema(opChain, b.value, schema)
	return b
}

// IsTrue succeeds if boolean is true.
//
// Example:
//
//	boolean := NewBoolean(t, true)
//	boolean.IsTrue()
func (b *Boolean) IsTrue() *Boolean {
	opChain := b.chain.enter("IsTrue()")
	defer opChain.leave()

	if opChain.failed() {
		return b
	}

	if !(b.value == true) {
		opChain.fail(AssertionFailure{
			Type:     AssertEqual,
			Actual:   &AssertionValue{b.value},
			Expected: &AssertionValue{true},
			Errors: []error{
				errors.New("expected: boolean is true"),
			},
		})
	}

	return b
}

// IsFalse succeeds if boolean is false.
//
// Example:
//
//	boolean := NewBoolean(t, false)
//	boolean.IsFalse()
func (b *Boolean) IsFalse() *Boolean {
	opChain := b.chain.enter("IsFalse()")
	defer opChain.leave()

	if opChain.failed() {
		return b
	}

	if !(b.value == false) {
		opChain.fail(AssertionFailure{
			Type:     AssertEqual,
			Actual:   &AssertionValue{b.value},
			Expected: &AssertionValue{false},
			Errors: []error{
				errors.New("expected: boolean is false"),
			},
		})
	}

	return b
}

// Deprecated: use IsTrue instead.
func (b *Boolean) True() *Boolean {
	return b.IsTrue()
}

// Deprecated: use IsFalse instead.
func (b *Boolean) False() *Boolean {
	return b.IsFalse()
}

// IsEqual succeeds if boolean is equal to given value.
//
// Example:
//
//	boolean := NewBoolean(t, true)
//	boolean.IsEqual(true)
func (b *Boolean) IsEqual(value bool) *Boolean {
	opChain := b.chain.enter("IsEqual()")
	defer opChain.leave()

	if opChain.failed() {
		return b
	}

	if !(b.value == value) {
		opChain.fail(AssertionFailure{
			Type:     AssertEqual,
			Actual:   &AssertionValue{b.value},
			Expected: &AssertionValue{value},
			Errors: []error{
				errors.New("expected: booleans are equal"),
			},
		})
	}

	return b
}

// NotEqual succeeds if boolean is not equal to given value.
//
// Example:
//
//	boolean := NewBoolean(t, true)
//	boolean.NotEqual(false)
func (b *Boolean) NotEqual(value bool) *Boolean {
	opChain := b.chain.enter("NotEqual()")
	defer opChain.leave()

	if opChain.failed() {
		return b
	}

	if b.value == value {
		opChain.fail(AssertionFailure{
			Type:     AssertNotEqual,
			Actual:   &AssertionValue{b.value},
			Expected: &AssertionValue{value},
			Errors: []error{
				errors.New("expected: booleans are non-equal"),
			},
		})
	}

	return b
}

// Deprecated: use IsEqual instead.
func (b *Boolean) Equal(value bool) *Boolean {
	return b.IsEqual(value)
}

// InList succeeds if boolean is equal to one of the values from given
// list of booleans.
//
// Example:
//
//	boolean := NewBoolean(t, true)
//	boolean.InList(true, false)
func (b *Boolean) InList(values ...bool) *Boolean {
	opChain := b.chain.enter("InList()")
	defer opChain.leave()

	if opChain.failed() {
		return b
	}

	if len(values) == 0 {
		opChain.fail(AssertionFailure{
			Type: AssertUsage,
			Errors: []error{
				errors.New("unexpected empty list argument"),
			},
		})
		return b
	}

	var isListed bool
	for _, v := range values {
		if b.value == v {
			isListed = true
			break
		}
	}

	if !isListed {
		valueList := make([]interface{}, 0, len(values))
		for _, v := range values {
			valueList = append(valueList, v)
		}

		opChain.fail(AssertionFailure{
			Type:     AssertBelongs,
			Actual:   &AssertionValue{b.value},
			Expected: &AssertionValue{AssertionList(valueList)},
			Errors: []error{
				errors.New("expected: boolean is equal to one of the values"),
			},
		})
	}

	return b
}

// NotInList succeeds if boolean is not equal to any of the values from
// given list of booleans.
//
// Example:
//
//	boolean := NewBoolean(t, true)
//	boolean.NotInList(true, false) // failure
func (b *Boolean) NotInList(values ...bool) *Boolean {
	opChain := b.chain.enter("NotInList()")
	defer opChain.leave()

	if opChain.failed() {
		return b
	}

	if len(values) == 0 {
		opChain.fail(AssertionFailure{
			Type: AssertUsage,
			Errors: []error{
				errors.New("unexpected empty list argument"),
			},
		})
		return b
	}

	for _, v := range values {
		if b.value == v {
			valueList := make([]interface{}, 0, len(values))
			for _, v := range values {
				valueList = append(valueList, v)
			}

			opChain.fail(AssertionFailure{
				Type:     AssertNotBelongs,
				Actual:   &AssertionValue{b.value},
				Expected: &AssertionValue{AssertionList(valueList)},
				Errors: []error{
					errors.New("expected: boolean is not equal to any of the values"),
				},
			})

			return b
		}
	}

	return b
}

package httpexpect

