func TestBoolean_Decode(t *testing.T) {
	t.Run("target is empty interface", func(t *testing.T) {
		reporter := newMockReporter(t)

		value := NewBoolean(reporter, true)

		var target interface{}
		value.Decode(&target)

		value.chain.assert(t, success)
		assert.Equal(t, true, target)
	})

	t.Run("target is bool", func(t *testing.T) {
		reporter := newMockReporter(t)

		value := NewBoolean(reporter, true)

		var target bool
		value.Decode(&target)

		value.chain.assert(t, success)
		assert.Equal(t, true, target)
	})

	t.Run("target is nil", func(t *testing.T) {
		reporter := newMockReporter(t)

		value := NewBoolean(reporter, true)

		value.Decode(nil)

		value.chain.assert(t, failure)
	})

	t.Run("target is unmarshable", func(t *testing.T) {
		reporter := newMockReporter(t)

		value := NewBoolean(reporter, true)

		value.Decode(123)

		value.chain.assert(t, failure)
	})
}

func TestBoolean_Alias(t *testing.T) {
	reporter := newMockReporter(t)

	value := NewBoolean(reporter, true)
	assert.Equal(t, []string{"Boolean()"}, value.chain.context.Path)
	assert.Equal(t, []string{"Boolean()"}, value.chain.context.AliasedPath)

	value.Alias("foo")
	assert.Equal(t, []string{"Boolean()"}, value.chain.context.Path)
	assert.Equal(t, []string{"foo"}, value.chain.context.AliasedPath)
}

func TestBoolean_Path(t *testing.T) {
	reporter := newMockReporter(t)

	value := NewBoolean(reporter, true)

	assert.Equal(t, true, value.Path("$").Raw())
	value.chain.assert(t, success)
}

func TestBoolean_Schema(t *testing.T) {
	reporter := newMockReporter(t)

	NewBoolean(reporter, true).Schema(`{"type": "boolean"}`).
		chain.assert(t, success)

	NewBoolean(reporter, true).Schema(`{"type": "object"}`).
		chain.assert(t, failure)
}

func TestBoolean_IsEqual(t *testing.T) {
	for _, data := range []bool{true, false} {
		t.Run(fmt.Sprintf("%v", data), func(t *testing.T) {
			reporter := newMockReporter(t)

			NewBoolean(reporter, data).IsEqual(data).
				chain.assert(t, success)

			NewBoolean(reporter, data).IsEqual(!data).
				chain.assert(t, failure)

			NewBoolean(reporter, data).NotEqual(data).
				chain.assert(t, failure)

			NewBoolean(reporter, data).NotEqual(!data).
				chain.assert(t, success)
		})
	}
}

func TestBoolean_IsValue(t *testing.T) {
	for _, data := range []bool{true, false} {
		t.Run(fmt.Sprintf("%v", data), func(t *testing.T) {
			reporter := newMockReporter(t)

			if data {
				NewBoolean(reporter, data).IsTrue().
					chain.assert(t, success)

				NewBoolean(reporter, data).IsFalse().
					chain.assert(t, failure)
			} else {
				NewBoolean(reporter, data).IsTrue().
					chain.assert(t, failure)

				NewBoolean(reporter, data).IsFalse().
					chain.assert(t, success)
			}
		})
	}
}

func TestBoolean_InList(t *testing.T) {
	t.Run("basic", func(t *testing.T) {
		for _, data := range []bool{true, false} {
			t.Run(fmt.Sprintf("%v", data), func(t *testing.T) {
				reporter := newMockReporter(t)

				NewBoolean(reporter, data).InList(data).
					chain.assert(t, success)

				NewBoolean(reporter, data).InList(!data, data).
					chain.assert(t, success)

				NewBoolean(reporter, data).InList(!data, !data).
					chain.assert(t, failure)

				NewBoolean(reporter, data).NotInList(data).
					chain.assert(t, failure)

				NewBoolean(reporter, data).NotInList(!data, data).
					chain.assert(t, failure)

				NewBoolean(reporter, data).NotInList(!data, !data).
					chain.assert(t, success)
			})
		}
	})

	t.Run("invalid argument", func(t *testing.T) {
		for _, data := range []bool{true, false} {
			reporter := newMockReporter(t)

			NewBoolean(reporter, data).InList().
				chain.assert(t, failure)

			NewBoolean(reporter, data).NotInList().
				chain.assert(t, failure)
		}
	})
}

package httpexpect

import (
	"encoding/json"
	"errors"
	"fmt"
	"reflect"
)

func canonNumber(opChain *chain, in interface{}) (out float64, ok bool) {
	ok = true
	defer func() {
		if err := recover(); err != nil {
			opChain.fail(AssertionFailure{
				Type:   AssertValid,
				Actual: &AssertionValue{in},
				Errors: []error{
					errors.New("expected: valid number"),
					fmt.Errorf("%s", err),
				},
			})
			ok = false
		}
	}()
	out = reflect.ValueOf(in).Convert(reflect.TypeOf(float64(0))).Float()
	return
}

func canonArray(opChain *chain, in interface{}) ([]interface{}, bool) {
	var out []interface{}
	data, ok := canonValue(opChain, in)
	if ok {
		out, ok = data.([]interface{})
		if !ok {
			opChain.fail(AssertionFailure{
				Type:   AssertValid,
				Actual: &AssertionValue{in},
				Errors: []error{
					errors.New("expected: valid array"),
				},
			})
		}
	}
	return out, ok
}

func canonMap(opChain *chain, in interface{}) (map[string]interface{}, bool) {
	var out map[string]interface{}
	data, ok := canonValue(opChain, in)
	if ok {
		out, ok = data.(map[string]interface{})
		if !ok {
			opChain.fail(AssertionFailure{
				Type:   AssertValid,
				Actual: &AssertionValue{in},
				Errors: []error{
					errors.New("expected: valid map"),
				},
			})
		}
	}
	return out, ok
}

func canonValue(opChain *chain, in interface{}) (interface{}, bool) {
	b, err := json.Marshal(in)
	if err != nil {
		opChain.fail(AssertionFailure{
			Type:   AssertValid,
			Actual: &AssertionValue{in},
			Errors: []error{
				errors.New("expected: marshalable value"),
				err,
			},
		})
		return nil, false
	}

	var out interface{}
	if err := json.Unmarshal(b, &out); err != nil {
		opChain.fail(AssertionFailure{
			Type:   AssertValid,
			Actual: &AssertionValue{in},
			Errors: []error{
				errors.New("expected: unmarshalable value"),
				err,
			},
		})
		return nil, false
	}

	return out, true
}

func canonDecode(opChain *chain, value interface{}, target interface{}) {
	if target == nil {
		opChain.fail(AssertionFailure{
			Type: AssertUsage,
			Errors: []error{
				errors.New("unexpected nil target argument"),
			},
		})
		return
	}

	b, err := json.Marshal(value)
	if err != nil {
		opChain.fail(AssertionFailure{
			Type:   AssertValid,
			Actual: &AssertionValue{value},
			Errors: []error{
				errors.New("expected: marshable value"),
			},
		})
		return
	}

	if err := json.Unmarshal(b, target); err != nil {
		opChain.fail(AssertionFailure{
			Type:   AssertValid,
			Actual: &AssertionValue{target},
			Errors: []error{
				errors.New("expected: value can be unmarshaled into target argument"),
			},
		})
		return
	}
}

