}

// Le succeeds if Duration is lesser than or equal to given value.
//
// Example:
//
//	d := NewDuration(t, time.Second)
//	d.Le(time.Minute)
func (d *Duration) Le(value time.Duration) *Duration {
	opChain := d.chain.enter("Le()")
	defer opChain.leave()

	if opChain.failed() {
		return d
	}

	if d.value == nil {
