	failure       *AssertionFailure
	successCalled int
	failureCalled int
	assertionCb   func()
}

func (mh *mockAssertionHandler) Success(ctx *AssertionContext) {
	mh.ctx = ctx
	mh.successCalled++

	if mh.assertionCb != nil {
		mh.assertionCb()
	}
}

func (mh *mockAssertionHandler) Failure(
	ctx *AssertionContext, failure *AssertionFailure,
) {
	mh.ctx = ctx
	mh.failure = failure
	mh.failureCalled++

	if mh.assertionCb != nil {
		mh.assertionCb()
	}
}

