package httpexpect

import (
	"bytes"
	"fmt"
	"net/http"
	"net/http/httputil"
	"strings"
	"time"

	"github.com/gorilla/websocket"
	"moul.io/http2curl/v2"
)

// Printer is used to print requests and responses.
// CompactPrinter, DebugPrinter, and CurlPrinter implement this interface.
type Printer interface {
	// Request is called before request is sent.
	// It is allowed to read and close request body, or ignore it.
	Request(*http.Request)

	// Response is called after response is received.
	// It is allowed to read and close response body, or ignore it.
	Response(*http.Response, time.Duration)
}

// WebsocketPrinter is used to print writes and reads of WebSocket connection.
//
// If WebSocket connection is used, all Printers that also implement WebsocketPrinter
// are invoked on every WebSocket message read or written.
//
// DebugPrinter implements this interface.
type WebsocketPrinter interface {
	Printer

	// WebsocketWrite is called before writes to WebSocket connection.
	WebsocketWrite(typ int, content []byte, closeCode int)

	// WebsocketRead is called after reads from WebSocket connection.
	WebsocketRead(typ int, content []byte, closeCode int)
}

// CompactPrinter implements Printer.
// Prints requests in compact form. Does not print responses.
type CompactPrinter struct {
	logger Logger
}

// NewCompactPrinter returns a new CompactPrinter given a logger.
func NewCompactPrinter(logger Logger) CompactPrinter {
	return CompactPrinter{logger}
}

// Request implements Printer.Request.
func (p CompactPrinter) Request(req *http.Request) {
	if req != nil {
		p.logger.Logf("%s %s", req.Method, req.URL)
	}
}

// Response implements Printer.Response.
func (CompactPrinter) Response(*http.Response, time.Duration) {
}

// CurlPrinter implements Printer.
// Uses http2curl to dump requests as curl commands that can be inserted
// into terminal.
type CurlPrinter struct {
	logger Logger
}

// NewCurlPrinter returns a new CurlPrinter given a logger.
func NewCurlPrinter(logger Logger) CurlPrinter {
	return CurlPrinter{logger}
}

// Request implements Printer.Request.
func (p CurlPrinter) Request(req *http.Request) {
	if req != nil {
		cmd, err := http2curl.GetCurlCommand(req)
		if err != nil {
			panic(err)
		}
		p.logger.Logf("%s", cmd.String())
	}
}

// Response implements Printer.Response.
func (CurlPrinter) Response(*http.Response, time.Duration) {
}

// DebugPrinter implements Printer and WebsocketPrinter.
// Uses net/http/httputil to dump both requests and responses.
// Also prints all websocket messages.
type DebugPrinter struct {
	logger Logger
	body   bool
}

// NewDebugPrinter returns a new DebugPrinter given a logger and body
// flag. If body is true, request and response body is also printed.
func NewDebugPrinter(logger Logger, body bool) DebugPrinter {
	return DebugPrinter{logger, body}
}

// Request implements Printer.Request.
func (p DebugPrinter) Request(req *http.Request) {
	if req == nil {
		return
	}

	dump, err := httputil.DumpRequest(req, p.body)
	if err != nil {
		panic(err)
	}
	p.logger.Logf("%s", dump)
}

// Response implements Printer.Response.
func (p DebugPrinter) Response(resp *http.Response, duration time.Duration) {
	if resp == nil {
		return
	}

	dump, err := httputil.DumpResponse(resp, p.body)
	if err != nil {
		panic(err)
	}

	text := strings.Replace(string(dump), "\r\n", "\n", -1)
	lines := strings.SplitN(text, "\n", 2)

	p.logger.Logf("%s %s\n%s", lines[0], duration, lines[1])
}

// WebsocketWrite implements WebsocketPrinter.WebsocketWrite.
func (p DebugPrinter) WebsocketWrite(typ int, content []byte, closeCode int) {
	b := &bytes.Buffer{}
	fmt.Fprintf(b, "-> Sent: %s", wsMessageType(typ))
	if typ == websocket.CloseMessage {
		fmt.Fprintf(b, " %s", wsCloseCode(closeCode))
	}
	fmt.Fprint(b, "\n")
	if len(content) > 0 {
		if typ == websocket.BinaryMessage {
			fmt.Fprintf(b, "%v\n", content)
		} else {
			fmt.Fprintf(b, "%s\n", content)
		}
	}
	fmt.Fprintf(b, "\n")
	p.logger.Logf(b.String())
}

// WebsocketRead implements WebsocketPrinter.WebsocketRead.
func (p DebugPrinter) WebsocketRead(typ int, content []byte, closeCode int) {
	b := &bytes.Buffer{}
	fmt.Fprintf(b, "<- Received: %s", wsMessageType(typ))
	if typ == websocket.CloseMessage {
		fmt.Fprintf(b, " %s", wsCloseCode(closeCode))
	}
	fmt.Fprint(b, "\n")
	if len(content) > 0 {
		if typ == websocket.BinaryMessage {
			fmt.Fprintf(b, "%v\n", content)
		} else {
			fmt.Fprintf(b, "%s\n", content)
		}
	}
	fmt.Fprintf(b, "\n")
	p.logger.Logf(b.String())
}

package httpexpect

import (
	"bytes"
	"errors"
	"io"
	"net/http"
	"testing"

	"github.com/stretchr/testify/assert"
)

type failingReader struct{}

func (failingReader) Read(_ []byte) (n int, err error) {
	return 0, errors.New("error")
}

func (failingReader) Close() error {
	return errors.New("error")
}

func TestPrinter_Compact(t *testing.T) {
	printer := NewCompactPrinter(t)

	body1 := bytes.NewBufferString("body1")
	body2 := bytes.NewBufferString("body2")

	req1, _ := http.NewRequest("GET", "http://example.com", body1)
	req2, _ := http.NewRequest("GET", "http://example.com", nil)

	printer.Request(req1)
	printer.Request(req2)
	printer.Request(nil)

	printer.Response(&http.Response{Body: io.NopCloser(body2)}, 0)
	printer.Response(&http.Response{}, 0)
	printer.Response(nil, 0)
}

func TestPrinter_Debug(t *testing.T) {
	printer := NewDebugPrinter(t, true)

	body1 := bytes.NewBufferString("body1")
	body2 := bytes.NewBufferString("body2")

	req1, _ := http.NewRequest("GET", "http://example.com", body1)
	req2, _ := http.NewRequest("GET", "http://example.com", nil)

	printer.Request(req1)
	printer.Request(req2)
	printer.Request(nil)

	printer.Response(&http.Response{Body: io.NopCloser(body2)}, 0)
	printer.Response(&http.Response{}, 0)
	printer.Response(nil, 0)
}

func TestPrinter_Panics(t *testing.T) {
	t.Run("CurlPrinter", func(t *testing.T) {
		curl := NewCurlPrinter(t)

		assert.Panics(t, func() {
			curl.Request(&http.Request{})
		})
	})

	t.Run("DebugPrinter", func(t *testing.T) {
		curl := NewDebugPrinter(t, true)

		assert.Panics(t, func() {
			curl.Request(&http.Request{
				Body: failingReader{},
			})
		})

		assert.Panics(t, func() {
			curl.Response(&http.Response{
				Body: failingReader{},
			}, 0)
		})
	})
}

package httpexpect

import (
	"net/http"
	"reflect"
)

func refIsNil(value interface{}) bool {
	defer func() {
		_ = recover()
	}()
	return value == nil || reflect.ValueOf(value).IsNil()
}

func refIsNum(value interface{}) bool {
	defer func() {
		_ = recover()
	}()
	reflect.ValueOf(value).Convert(reflect.TypeOf(float64(0))).Float()
	return true
}

func refIsHTTP(value interface{}) bool {
	switch value.(type) {
	case *http.Client, http.Client,
		*http.Transport, http.Transport,
		*http.Request, http.Request,
		*http.Response, http.Response,
		*http.Header, http.Header,
		*http.Cookie, http.Cookie:
		return true
	default:
		return false
	}
}

package httpexpect

import (
	"fmt"
	"testing"

	"github.com/stretchr/testify/assert"
	"github.com/stretchr/testify/require"
)

// AssertReporter implements Reporter interface using `testify/assert'
// package. Failures are non-fatal with this reporter.
type AssertReporter struct {
	backend *assert.Assertions
}

// NewAssertReporter returns a new AssertReporter object.
func NewAssertReporter(t assert.TestingT) *AssertReporter {
	return &AssertReporter{assert.New(t)}
}

// Errorf implements Reporter.Errorf.
func (r *AssertReporter) Errorf(message string, args ...interface{}) {
	r.backend.Fail(fmt.Sprintf(message, args...))
}

// RequireReporter implements Reporter interface using `testify/require'
// package. Failures are fatal with this reporter.
type RequireReporter struct {
	backend *require.Assertions
}

// NewRequireReporter returns a new RequireReporter object.
func NewRequireReporter(t require.TestingT) *RequireReporter {
	return &RequireReporter{require.New(t)}
}

// Errorf implements Reporter.Errorf.
func (r *RequireReporter) Errorf(message string, args ...interface{}) {
	r.backend.FailNow(fmt.Sprintf(message, args...))
}

// FatalReporter is a struct that implements the Reporter interface
// and calls t.Fatalf() when a test fails.
type FatalReporter struct {
	backend testing.TB
}

// NewFatalReporter returns a new FatalReporter object.
func NewFatalReporter(t testing.TB) *FatalReporter {
	return &FatalReporter{t}
}

// Errorf implements Reporter.Errorf.
func (r *FatalReporter) Errorf(message string, args ...interface{}) {
	r.backend.Fatalf(fmt.Sprintf(message, args...))
}

// PanicReporter is a struct that implements the Reporter interface
// and panics when a test fails.
// Useful for multithreaded tests when you want to report fatal
// failures from goroutines other than the main goroutine, because
// the main goroutine is forbidden to call t.Fatal.
type PanicReporter struct{}

// NewPanicReporter returns a new PanicReporter object.
func NewPanicReporter() *PanicReporter {
	return &PanicReporter{}
}

// Errorf implements Reporter.Errorf
func (r *PanicReporter) Errorf(message string, args ...interface{}) {
	panic(fmt.Sprintf(message, args...))
}

