		}

		for _, tc := range cases {
			t.Run(tc.name, func(t *testing.T) {
				reporter := newMockReporter(t)

				NewValue(reporter, tc.value).InList(tc.list...).
					chain.assert(t, tc.wantInList)

				NewValue(reporter, tc.value).NotInList(tc.list...).
					chain.assert(t, tc.wantNotInList)
			})
		}
	})
}

func TestValue_PathTypes(t *testing.T) {
	t.Run("object", func(t *testing.T) {
		user0 := map[string]interface{}{"name": "john"}
		user1 := map[string]interface{}{"name": "bob"}

		data := map[string]interface{}{
			"users": []interface{}{
				user0,
				user1,
			},
		}

		t.Run("queries", func(t *testing.T) {
			reporter := newMockReporter(t)

			value := NewValue(reporter, data)

			assert.Equal(t, data, value.Path("$").Raw())
			assert.Equal(t, data["users"], value.Path("$.users").Raw())
			assert.Equal(t, user0, value.Path("$.users[0]").Raw())
			assert.Equal(t, "john", value.Path("$.users[0].name").Raw())
			assert.Equal(t, []interface{}{"john", "bob"}, value.Path("$.users[*].name").Raw())
			assert.Equal(t, []interface{}{"john", "bob"}, value.Path("$..name").Raw())
			value.chain.assert(t, success)

			names := value.Path("$..name").Array().Iter()
			names[0].String().IsEqual("john").chain.assert(t, success)
			names[1].String().IsEqual("bob").chain.assert(t, success)
			value.chain.assert(t, success)
		})

		t.Run("bad key", func(t *testing.T) {
			reporter := newMockReporter(t)

			value := NewValue(reporter, data)

			bad := value.Path("$.bad")
			assert.True(t, bad != nil)
			assert.True(t, bad.Raw() == nil)
			value.chain.assert(t, failure)
		})

		t.Run("invalid query", func(t *testing.T) {
			reporter := newMockReporter(t)

			value := NewValue(reporter, data)

			bad := value.Path("!")
			assert.True(t, bad != nil)
			assert.True(t, bad.Raw() == nil)
			value.chain.assert(t, failure)
		})
	})

	t.Run("array", func(t *testing.T) {
		user0 := map[string]interface{}{"name": "john"}
		user1 := map[string]interface{}{"name": "bob"}

		data := []interface{}{
			user0,
			user1,
		}

		reporter := newMockReporter(t)

		value := NewValue(reporter, data)

		assert.Equal(t, data, value.Path("$").Raw())
		assert.Equal(t, user0, value.Path("$[0]").Raw())
		assert.Equal(t, "john", value.Path("$[0].name").Raw())
		assert.Equal(t, []interface{}{"john", "bob"}, value.Path("$[*].name").Raw())
		assert.Equal(t, []interface{}{"john", "bob"}, value.Path("$..name").Raw())
		value.chain.assert(t, success)
	})

	t.Run("string", func(t *testing.T) {
		data := "foo"

		reporter := newMockReporter(t)

		value := NewValue(reporter, data)

		assert.Equal(t, data, value.Path("$").Raw())
		value.chain.assert(t, success)
	})

	t.Run("number", func(t *testing.T) {
		data := 123

		reporter := newMockReporter(t)

		value := NewValue(reporter, data)

		assert.Equal(t, float64(data), value.Path("$").Raw())
		value.chain.assert(t, success)
	})

	t.Run("boolean", func(t *testing.T) {
		data := true

		reporter := newMockReporter(t)

		value := NewValue(reporter, data)

		assert.Equal(t, data, value.Path("$").Raw())
		value.chain.assert(t, success)
	})

	t.Run("null", func(t *testing.T) {
		reporter := newMockReporter(t)

		value := NewValue(reporter, nil)

		assert.Equal(t, nil, value.Path("$").Raw())
		value.chain.assert(t, success)
	})

	t.Run("error", func(t *testing.T) {
		data := "foo"

		reporter := newMockReporter(t)

		value := NewValue(reporter, data)

		for _, key := range []string{"$.bad", "!"} {
			bad := value.Path(key)
			assert.True(t, bad != nil)
			assert.True(t, bad.Raw() == nil)
			value.chain.assert(t, failure)
		}
	})

	t.Run("int float", func(t *testing.T) {
		data := map[string]interface{}{
			"A": 123,
			"B": 123.0,
		}

		reporter := newMockReporter(t)

		value := NewValue(reporter, data)
		value.chain.assert(t, success)

		a := value.Path(`$["A"]`)
		a.chain.assert(t, success)
		assert.Equal(t, 123.0, a.Raw())

		b := value.Path(`$["B"]`)
		b.chain.assert(t, success)
		assert.Equal(t, 123.0, b.Raw())
	})
}

// based on github.com/yalp/jsonpath
func TestValue_PathExpressions(t *testing.T) {
	data := map[string]interface{}{
		"A": []interface{}{
			"string",
			23.3,
			3.0,
			true,
			false,
			nil,
		},
		"B": "value",
		"C": 3.14,
		"D": map[string]interface{}{
			"C": 3.1415,
			"V": []interface{}{
				"string2a",
				"string2b",
				map[string]interface{}{
					"C": 3.141592,
				},
			},
		},
		"E": map[string]interface{}{
			"A": []interface{}{"string3"},
			"D": map[string]interface{}{
				"V": map[string]interface{}{
					"C": 3.14159265,
				},
			},
		},
		"F": map[string]interface{}{
			"V": []interface{}{
				"string4a",
				"string4b",
				map[string]interface{}{
					"CC": 3.1415926535,
				},
				map[string]interface{}{
					"CC": "hello",
				},
				[]interface{}{
					"string5a",
					"string5b",
				},
				[]interface{}{
					"string6a",
					"string6b",
				},
			},
		},
	}

	runTests := func(tests map[string]interface{}) {
		reporter := newMockReporter(t)

		value := NewValue(reporter, data)
		value.chain.assert(t, success)

		for path, expected := range tests {
			actual := value.Path(path)
			actual.chain.assert(t, success)

			assert.Equal(t, expected, actual.Raw())
		}
	}

	t.Run("pick", func(t *testing.T) {
		runTests(map[string]interface{}{
			"$":         data,
			"$.A[0]":    "string",
			`$["A"][0]`: "string",
			"$.A":       []interface{}{"string", 23.3, 3.0, true, false, nil},
			"$.A[*]":    []interface{}{"string", 23.3, 3.0, true, false, nil},
			"$.A.*":     []interface{}{"string", 23.3, 3.0, true, false, nil},
			"$.A.*.a":   []interface{}{},
		})
	})

	t.Run("slice", func(t *testing.T) {
		runTests(map[string]interface{}{
			"$.A[1,4,2]":      []interface{}{23.3, false, 3.0},
			`$["B","C"]`:      []interface{}{"value", 3.14},
			`$["C","B"]`:      []interface{}{3.14, "value"},
			"$.A[1:4]":        []interface{}{23.3, 3.0, true},
			"$.A[::2]":        []interface{}{"string", 3.0, false},
			"$.A[-2:]":        []interface{}{false, nil},
			"$.A[:-1]":        []interface{}{"string", 23.3, 3.0, true, false},
			"$.A[::-1]":       []interface{}{nil, false, true, 3.0, 23.3, "string"},
			"$.F.V[4:5][0,1]": []interface{}{"string5a", "string5b"},
			"$.F.V[4:6][1]":   []interface{}{"string5b", "string6b"},
			"$.F.V[4:6][0,1]": []interface{}{"string5a", "string5b", "string6a", "string6b"},
			"$.F.V[4,5][0:2]": []interface{}{"string5a", "string5b", "string6a", "string6b"},
			"$.F.V[4:6]": []interface{}{
				[]interface{}{
					"string5a",
					"string5b",
				},
				[]interface{}{
					"string6a",
					"string6b",
				},
			},
		})
	})

	t.Run("quote", func(t *testing.T) {
		runTests(map[string]interface{}{
			`$[A][0]`:    "string",
			`$["A"][0]`:  "string",
			`$[B,C]`:     []interface{}{"value", 3.14},
			`$["B","C"]`: []interface{}{"value", 3.14},
		})
	})

	t.Run("search", func(t *testing.T) {
		runTests(map[string]interface{}{
			"$..C":       []interface{}{3.14, 3.1415, 3.141592, 3.14159265},
			`$..["C"]`:   []interface{}{3.14, 3.1415, 3.141592, 3.14159265},
			"$.D.V..C":   []interface{}{3.141592},
			"$.D.V.*.C":  []interface{}{3.141592},
			"$.D.V..*.C": []interface{}{3.141592},
			"$.D.*..C":   []interface{}{3.141592},
			"$.*.V..C":   []interface{}{3.141592},
			"$.*.D.V.C":  []interface{}{3.14159265},
			"$.*.D..C":   []interface{}{3.14159265},
			"$.*.D.V..*": []interface{}{3.14159265},
			"$..D..V..C": []interface{}{3.141592, 3.14159265},
			"$.*.*.*.C":  []interface{}{3.141592, 3.14159265},
			"$..V..C":    []interface{}{3.141592, 3.14159265},
			"$.D.V..*": []interface{}{
				"string2a",
				"string2b",
				map[string]interface{}{
					"C": 3.141592,
				},
				3.141592,
			},
			"$..A": []interface{}{
				[]interface{}{"string", 23.3, 3.0, true, false, nil},
				[]interface{}{"string3"},
			},
			"$..A..*":      []interface{}{"string", 23.3, 3.0, true, false, nil, "string3"},
			"$.A..*":       []interface{}{"string", 23.3, 3.0, true, false, nil},
			"$.A.*":        []interface{}{"string", 23.3, 3.0, true, false, nil},
			"$..A[0,1]":    []interface{}{"string", 23.3},
			"$..A[0]":      []interface{}{"string", "string3"},
			"$.*.V[0]":     []interface{}{"string2a", "string4a"},
			"$.*.V[1]":     []interface{}{"string2b", "string4b"},
			"$.*.V[0,1]":   []interface{}{"string2a", "string2b", "string4a", "string4b"},
			"$.*.V[0:2]":   []interface{}{"string2a", "string2b", "string4a", "string4b"},
			"$.*.V[2].C":   []interface{}{3.141592},
			"$..V[2].C":    []interface{}{3.141592},
			"$..V[*].C":    []interface{}{3.141592},
			"$.*.V[2].*":   []interface{}{3.141592, 3.1415926535},
			"$.*.V[2:3].*": []interface{}{3.141592, 3.1415926535},
			"$.*.V[2:4].*": []interface{}{3.141592, 3.1415926535, "hello"},
			"$..V[2,3].CC": []interface{}{3.1415926535, "hello"},
			"$..V[2:4].CC": []interface{}{3.1415926535, "hello"},
			"$..V[*].*": []interface{}{
				3.141592,
				3.1415926535,
				"hello",
				"string5a",
				"string5b",
				"string6a",
				"string6b",
			},
			"$..[0]": []interface{}{
				"string",
				"string2a",
				"string3",
				"string4a",
				"string5a",
				"string6a",
			},
			"$..ZZ": []interface{}{},
		})
	})
}

func TestValue_Schema(t *testing.T) {
	schema := `{
		"type": "object",
		"properties": {
			"foo": {
				"type": "string"
			},
			"bar": {
				"type": "integer"
			}
		},
		"require": ["foo", "bar"]
	}`

	t.Run("schema with valid data", func(t *testing.T) {
		reporter := newMockReporter(t)
		data := map[string]interface{}{
			"foo": "a",
			"bar": 1,
		}
		NewValue(reporter, data).Schema(schema).
			chain.assert(t, success)
	})

	t.Run("byte slice schema with valid data", func(t *testing.T) {
		reporter := newMockReporter(t)
		data := map[string]interface{}{
			"foo": "a",
			"bar": 1,
		}
		NewValue(reporter, data).Schema([]byte(schema)).
			chain.assert(t, success)
	})

	t.Run("schema with invalid data", func(t *testing.T) {
		reporter := newMockReporter(t)
		data := map[string]interface{}{
			"foo": "a",
			"bar": "b",
		}
		NewValue(reporter, data).Schema(schema).
			chain.assert(t, failure)
	})

	t.Run("byte slice schema with invalid data", func(t *testing.T) {
		reporter := newMockReporter(t)
		data := map[string]interface{}{
			"foo": "a",
			"bar": "b",
		}
		NewValue(reporter, data).Schema([]byte(schema)).
			chain.assert(t, failure)
	})

	t.Run("schema from unmarshalled JSON with valid data", func(t *testing.T) {
		reporter := newMockReporter(t)
		data := map[string]interface{}{
			"foo": "a",
			"bar": 1,
		}
		var b interface{}
		err := json.Unmarshal([]byte(schema), &b)
		require.Nil(t, err)
		NewValue(reporter, data).Schema(b).
			chain.assert(t, success)
	})

	t.Run("schema from unmarshalled JSON with invalid data", func(t *testing.T) {
		reporter := newMockReporter(t)
		data := map[string]interface{}{
			"foo": "a",
			"bar": "b",
		}
		var b interface{}
		err := json.Unmarshal([]byte(schema), &b)
		require.Nil(t, err)
		NewValue(reporter, data).Schema(b).
			chain.assert(t, failure)
	})

	t.Run("schema with valid file url with valid data", func(t *testing.T) {
		reporter := newMockReporter(t)
		data := map[string]interface{}{
			"foo": "a",
			"bar": 1,
		}
		tmp, _ := os.CreateTemp("", "httpexpect")
		defer os.Remove(tmp.Name())

		_, err := tmp.Write([]byte(schema))
		require.Nil(t, err)

		err = tmp.Close()
		require.Nil(t, err)

		url := "file://"
		if runtime.GOOS == "windows" {
			url = url + "/"
		}
		url = url + tmp.Name()
		NewValue(reporter, data).Schema(url).
			chain.assert(t, success)
	})

	t.Run("schema with valid file url with invalid data", func(t *testing.T) {
		reporter := newMockReporter(t)
		data := map[string]interface{}{
			"foo": "a",
			"bar": "b",
		}

		tmp, _ := os.CreateTemp("", "httpexpect")
		defer os.Remove(tmp.Name())

		_, err := tmp.Write([]byte(schema))
		require.Nil(t, err)

		err = tmp.Close()
		require.Nil(t, err)

		url := "file://"
		if runtime.GOOS == "windows" {
			url = url + "/"
		}
		url = url + tmp.Name()
		NewValue(reporter, data).Schema(url).
			chain.assert(t, failure)
	})

	t.Run("invalid schema file url", func(t *testing.T) {
		reporter := newMockReporter(t)
		data := map[string]interface{}{
			"foo": "a",
			"bar": 1,
		}
		NewValue(reporter, data).Schema("file:///bad/path").
			chain.assert(t, failure)
	})

	t.Run("invalid schema json", func(t *testing.T) {
		reporter := newMockReporter(t)
		data := map[string]interface{}{
			"foo": "a",
			"bar": 1,
		}
		NewValue(reporter, data).Schema("{ bad json").
			chain.assert(t, failure)
	})
}

package httpexpect

import (
	"encoding/json"
	"errors"
	"fmt"
	"time"

	"github.com/gorilla/websocket"
)

var (
	noDuration   = time.Duration(0)
	infiniteTime = time.Time{}
)

// WebsocketConn is used by Websocket to communicate with actual WebSocket connection.
type WebsocketConn interface {
	ReadMessage() (messageType int, p []byte, err error)
	WriteMessage(messageType int, data []byte) error
	Close() error
	SetReadDeadline(t time.Time) error
	SetWriteDeadline(t time.Time) error
	Subprotocol() string
}

// Websocket provides methods to read from, write into and close WebSocket
// connection.
type Websocket struct {
	noCopy noCopy
	config Config
	chain  *chain

	conn WebsocketConn

	readTimeout  time.Duration
	writeTimeout time.Duration

	isClosed bool
}

// Deprecated: use NewWebsocketC instead.
func NewWebsocket(config Config, conn WebsocketConn) *Websocket {
	return NewWebsocketC(config, conn)
}

// NewWebsocketC returns a new Websocket instance.
//
// Requirements for config are same as for WithConfig function.
func NewWebsocketC(config Config, conn WebsocketConn) *Websocket {
	config = config.withDefaults()

	return newWebsocket(
		newChainWithConfig("Websocket()", config),
		config,
		conn,
	)
}

func newWebsocket(parent *chain, config Config, conn WebsocketConn) *Websocket {
	config.validate()

	return &Websocket{
		config: config,
		chain:  parent.clone(),
		conn:   conn,
	}
}

// Conn returns underlying WebsocketConn object.
// This is the value originally passed to NewConnection.
func (ws *Websocket) Conn() WebsocketConn {
	return ws.conn
}

// Deprecated: use Conn instead.
func (ws *Websocket) Raw() *websocket.Conn {
	if ws.conn == nil {
		return nil
	}
	conn, ok := ws.conn.(*websocket.Conn)
	if !ok {
		return nil
	}
	return conn
}

// Alias is similar to Value.Alias.
func (ws *Websocket) Alias(name string) *Websocket {
	opChain := ws.chain.enter("Alias(%q)", name)
	defer opChain.leave()

	ws.chain.setAlias(name)
	return ws
}

// WithReadTimeout sets timeout duration for WebSocket connection reads.
//
// By default no timeout is used.
func (ws *Websocket) WithReadTimeout(timeout time.Duration) *Websocket {
	opChain := ws.chain.enter("WithReadTimeout()")
	defer opChain.leave()

	if opChain.failed() {
		return ws
	}

	ws.readTimeout = timeout

	return ws
}

// WithoutReadTimeout removes timeout for WebSocket connection reads.
func (ws *Websocket) WithoutReadTimeout() *Websocket {
	opChain := ws.chain.enter("WithoutReadTimeout()")
	defer opChain.leave()

	if opChain.failed() {
		return ws
	}

	ws.readTimeout = noDuration

	return ws
}

// WithWriteTimeout sets timeout duration for WebSocket connection writes.
//
// By default no timeout is used.
func (ws *Websocket) WithWriteTimeout(timeout time.Duration) *Websocket {
	opChain := ws.chain.enter("WithWriteTimeout()")
	defer opChain.leave()

	if opChain.failed() {
		return ws
	}

	ws.writeTimeout = timeout

	return ws
}

// WithoutWriteTimeout removes timeout for WebSocket connection writes.
//
// If not used then DefaultWebsocketTimeout will be used.
func (ws *Websocket) WithoutWriteTimeout() *Websocket {
	opChain := ws.chain.enter("WithoutWriteTimeout()")
	defer opChain.leave()

	if opChain.failed() {
		return ws
	}

	ws.writeTimeout = noDuration

	return ws
}

// Subprotocol returns a new String instance with negotiated protocol
// for the connection.
func (ws *Websocket) Subprotocol() *String {
	opChain := ws.chain.enter("Subprotocol()")
	defer opChain.leave()

	if opChain.failed() {
		return newString(opChain, "")
	}

	if ws.conn == nil {
		return newString(opChain, "")
	}

	return newString(opChain, ws.conn.Subprotocol())
}

// Expect reads next message from WebSocket connection and
// returns a new WebsocketMessage instance.
//
// Example:
//
//	msg := conn.Expect()
//	msg.JSON().Object().HasValue("message", "hi")
func (ws *Websocket) Expect() *WebsocketMessage {
	opChain := ws.chain.enter("Expect()")
	defer opChain.leave()

	if ws.checkUnusable(opChain, "Expect()") {
		return newEmptyWebsocketMessage(opChain)
	}

	m := ws.readMessage(opChain)
	if m == nil {
		return newEmptyWebsocketMessage(opChain)
	}

	return m
}

// Disconnect closes the underlying WebSocket connection without sending or
// waiting for a close message.
//
// It's okay to call this function multiple times.
//
// It's recommended to always call this function after connection usage is over
// to ensure that no resource leaks will happen.
//
// Example:
//
//	conn := resp.Connection()
//	defer conn.Disconnect()
func (ws *Websocket) Disconnect() *Websocket {
	opChain := ws.chain.enter("Disconnect()")
	defer opChain.leave()

	if ws.conn == nil || ws.isClosed {
		return ws
	}

	ws.isClosed = true

	if err := ws.conn.Close(); err != nil {
		opChain.fail(AssertionFailure{
			Type: AssertOperation,
			Errors: []error{
				errors.New("got close error when disconnecting websocket"),
				err,
			},
		})
	}

	return ws
}

// Close cleanly closes the underlying WebSocket connection
// by sending an empty close message and then waiting (with timeout)
// for the server to close the connection.
//
// WebSocket close code may be optionally specified.
// If not, then "1000 - Normal Closure" will be used.
//
// WebSocket close codes are defined in RFC 6455, section 11.7.
// See also https://godoc.org/github.com/gorilla/websocket#pkg-constants
//
// It's okay to call this function multiple times.
//
// Example:
//
//	conn := resp.Connection()
//	conn.Close(websocket.CloseUnsupportedData)
func (ws *Websocket) Close(code ...int) *Websocket {
	opChain := ws.chain.enter("Close()")
	defer opChain.leave()

	switch {
	case ws.checkUnusable(opChain, "Close()"):
		return ws

	case len(code) > 1:
		opChain.fail(AssertionFailure{
			Type: AssertUsage,
			Errors: []error{
				errors.New("unexpected multiple code arguments"),
			},
		})
		return ws
	}

	ws.writeMessage(opChain, websocket.CloseMessage, nil, code...)

	return ws
}

// CloseWithBytes cleanly closes the underlying WebSocket connection
// by sending given slice of bytes as a close message and then waiting
// (with timeout) for the server to close the connection.
//
// WebSocket close code may be optionally specified.
// If not, then "1000 - Normal Closure" will be used.
//
// WebSocket close codes are defined in RFC 6455, section 11.7.
// See also https://godoc.org/github.com/gorilla/websocket#pkg-constants
//
// It's okay to call this function multiple times.
//
// Example:
//
//	conn := resp.Connection()
//	conn.CloseWithBytes([]byte("bye!"), websocket.CloseGoingAway)
func (ws *Websocket) CloseWithBytes(b []byte, code ...int) *Websocket {
	opChain := ws.chain.enter("CloseWithBytes()")
	defer opChain.leave()

	switch {
	case ws.checkUnusable(opChain, "CloseWithBytes()"):
		return ws

	case len(code) > 1:
		opChain.fail(AssertionFailure{
			Type: AssertUsage,
			Errors: []error{
				errors.New("unexpected multiple code arguments"),
			},
		})
		return ws
	}

	ws.writeMessage(opChain, websocket.CloseMessage, b, code...)

	return ws
}

// CloseWithJSON cleanly closes the underlying WebSocket connection
// by sending given object (marshaled using json.Marshal()) as a close message
// and then waiting (with timeout) for the server to close the connection.
//
// WebSocket close code may be optionally specified.
// If not, then "1000 - Normal Closure" will be used.
//
// WebSocket close codes are defined in RFC 6455, section 11.7.
// See also https://godoc.org/github.com/gorilla/websocket#pkg-constants
//
// It's okay to call this function multiple times.
//
// Example:
//
//	type MyJSON struct {
//	  Foo int `json:"foo"`
//	}
//
//	conn := resp.Connection()
//	conn.CloseWithJSON(MyJSON{Foo: 123}, websocket.CloseUnsupportedData)
func (ws *Websocket) CloseWithJSON(
	object interface{}, code ...int,
) *Websocket {
	opChain := ws.chain.enter("CloseWithJSON()")
	defer opChain.leave()

	switch {
	case ws.checkUnusable(opChain, "CloseWithJSON()"):
		return ws

	case len(code) > 1:
		opChain.fail(AssertionFailure{
			Type: AssertUsage,
			Errors: []error{
				errors.New("unexpected multiple code arguments"),
			},
		})
		return ws
	}

	b, err := json.Marshal(object)

	if err != nil {
		opChain.fail(AssertionFailure{
			Type:   AssertValid,
			Actual: &AssertionValue{object},
			Errors: []error{
				errors.New("invalid json object"),
				err,
			},
		})
		return ws
	}

	ws.writeMessage(opChain, websocket.CloseMessage, b, code...)

	return ws
}

// CloseWithText cleanly closes the underlying WebSocket connection
// by sending given text as a close message and then waiting (with timeout)
// for the server to close the connection.
//
// WebSocket close code may be optionally specified.
// If not, then "1000 - Normal Closure" will be used.
//
// WebSocket close codes are defined in RFC 6455, section 11.7.
// See also https://godoc.org/github.com/gorilla/websocket#pkg-constants
//
// It's okay to call this function multiple times.
//
// Example:
//
//	conn := resp.Connection()
//	conn.CloseWithText("bye!")
func (ws *Websocket) CloseWithText(s string, code ...int) *Websocket {
	opChain := ws.chain.enter("CloseWithText()")
	defer opChain.leave()

	switch {
	case ws.checkUnusable(opChain, "CloseWithText()"):
		return ws

	case len(code) > 1:
		opChain.fail(AssertionFailure{
			Type: AssertUsage,
			Errors: []error{
				errors.New("unexpected multiple code arguments"),
			},
		})
		return ws
	}

	ws.writeMessage(opChain, websocket.CloseMessage, []byte(s), code...)

	return ws
}

// WriteMessage writes to the underlying WebSocket connection a message
// of given type with given content.
// Additionally, WebSocket close code may be specified for close messages.
//
// WebSocket message types are defined in RFC 6455, section 11.8.
// See also https://godoc.org/github.com/gorilla/websocket#pkg-constants
//
// WebSocket close codes are defined in RFC 6455, section 11.7.
// See also https://godoc.org/github.com/gorilla/websocket#pkg-constants
//
// Example:
//
//	conn := resp.Connection()
//	conn.WriteMessage(websocket.CloseMessage, []byte("Namri..."))
func (ws *Websocket) WriteMessage(typ int, content []byte, closeCode ...int) *Websocket {
	opChain := ws.chain.enter("WriteMessage()")
	defer opChain.leave()

	if ws.checkUnusable(opChain, "WriteMessage()") {
		return ws
	}

	ws.writeMessage(opChain, typ, content, closeCode...)

	return ws
}

// WriteBytesBinary is a shorthand for c.WriteMessage(websocket.BinaryMessage, b).
func (ws *Websocket) WriteBytesBinary(b []byte) *Websocket {
	opChain := ws.chain.enter("WriteBytesBinary()")
	defer opChain.leave()

	if ws.checkUnusable(opChain, "WriteBytesBinary()") {
		return ws
	}

	ws.writeMessage(opChain, websocket.BinaryMessage, b)

	return ws
}

// WriteBytesText is a shorthand for c.WriteMessage(websocket.TextMessage, b).
func (ws *Websocket) WriteBytesText(b []byte) *Websocket {
	opChain := ws.chain.enter("WriteBytesText()")
	defer opChain.leave()

	if ws.checkUnusable(opChain, "WriteBytesText()") {
		return ws
	}

	ws.writeMessage(opChain, websocket.TextMessage, b)

	return ws
}

// WriteText is a shorthand for
// c.WriteMessage(websocket.TextMessage, []byte(s)).
func (ws *Websocket) WriteText(s string) *Websocket {
	opChain := ws.chain.enter("WriteText()")
	defer opChain.leave()

	if ws.checkUnusable(opChain, "WriteText()") {
		return ws
	}

	return ws.WriteMessage(websocket.TextMessage, []byte(s))
}

// WriteJSON writes to the underlying WebSocket connection given object,
// marshaled using json.Marshal().
func (ws *Websocket) WriteJSON(object interface{}) *Websocket {
	opChain := ws.chain.enter("WriteJSON()")
	defer opChain.leave()

	if ws.checkUnusable(opChain, "WriteJSON()") {
		return ws
	}

	b, err := json.Marshal(object)

	if err != nil {
		opChain.fail(AssertionFailure{
			Type:   AssertValid,
			Actual: &AssertionValue{object},
			Errors: []error{
				errors.New("invalid json object"),
				err,
			},
		})
		return ws
	}

	ws.writeMessage(opChain, websocket.TextMessage, b)

	return ws
}

func (ws *Websocket) checkUnusable(opChain *chain, where string) bool {
	switch {
	case opChain.failed():
		return true

	case ws.conn == nil:
		opChain.fail(AssertionFailure{
			Type: AssertUsage,
			Errors: []error{
				fmt.Errorf("unexpected %s call for failed websocket connection", where),
			},
		})
		return true

	case ws.isClosed:
		opChain.fail(AssertionFailure{
			Type: AssertUsage,
			Errors: []error{
				fmt.Errorf("unexpected %s call for closed websocket connection", where),
			},
		})
		return true
	}

	return false
}

func (ws *Websocket) readMessage(opChain *chain) *WebsocketMessage {
	wm := newEmptyWebsocketMessage(opChain)

	if !ws.setReadDeadline(opChain) {
		return nil
	}

	var err error
	wm.typ, wm.content, err = ws.conn.ReadMessage()

	if err != nil {
		closeErr, ok := err.(*websocket.CloseError)
		if !ok {
			opChain.fail(AssertionFailure{
				Type: AssertOperation,
				Errors: []error{
					errors.New("failed to read from websocket"),
					err,
				},
			})
			return nil
		}

		wm.typ = websocket.CloseMessage
		wm.closeCode = closeErr.Code
		wm.content = []byte(closeErr.Text)
	}

	ws.printRead(wm.typ, wm.content, wm.closeCode)

	return wm
}

func (ws *Websocket) writeMessage(
	opChain *chain, typ int, content []byte, closeCode ...int,
) {
	switch typ {
	case websocket.TextMessage, websocket.BinaryMessage:
		ws.printWrite(typ, content, 0)

	case websocket.CloseMessage:
		if len(closeCode) > 1 {
			opChain.fail(AssertionFailure{
				Type: AssertUsage,
				Errors: []error{
					errors.New("unexpected multiple closeCode arguments"),
				},
			})
			return
		}

		code := websocket.CloseNormalClosure
		if len(closeCode) > 0 {
			code = closeCode[0]
		}

		ws.printWrite(typ, content, code)

		content = websocket.FormatCloseMessage(code, string(content))

	default:
		opChain.fail(AssertionFailure{
			Type: AssertUsage,
			Errors: []error{
				fmt.Errorf("unexpected websocket message type %s",
					wsMessageType(typ)),
			},
		})
		return
	}

	if !ws.setWriteDeadline(opChain) {
		return
	}

	if err := ws.conn.WriteMessage(typ, content); err != nil {
		opChain.fail(AssertionFailure{
			Type: AssertOperation,
			Errors: []error{
				errors.New("failed to write to websocket"),
				err,
			},
		})
		return
	}
}

func (ws *Websocket) setReadDeadline(opChain *chain) bool {
	deadline := infiniteTime
	if ws.readTimeout != noDuration {
		deadline = time.Now().Add(ws.readTimeout)
	}

	if err := ws.conn.SetReadDeadline(deadline); err != nil {
		opChain.fail(AssertionFailure{
			Type: AssertOperation,
			Errors: []error{
				errors.New("failed to set read deadline for websocket"),
				err,
			},
		})
		return false
	}

	return true
}

func (ws *Websocket) setWriteDeadline(opChain *chain) bool {
	deadline := infiniteTime
	if ws.writeTimeout != noDuration {
		deadline = time.Now().Add(ws.writeTimeout)
	}

	if err := ws.conn.SetWriteDeadline(deadline); err != nil {
		opChain.fail(AssertionFailure{
			Type: AssertOperation,
			Errors: []error{
				errors.New("failed to set write deadline for websocket"),
				err,
			},
		})
		return false
	}

	return true
}

func (ws *Websocket) printRead(typ int, content []byte, closeCode int) {
	for _, printer := range ws.config.Printers {
		if p, ok := printer.(WebsocketPrinter); ok {
			p.WebsocketRead(typ, content, closeCode)
		}
	}
}

func (ws *Websocket) printWrite(typ int, content []byte, closeCode int) {
	for _, printer := range ws.config.Printers {
		if p, ok := printer.(WebsocketPrinter); ok {
			p.WebsocketWrite(typ, content, closeCode)
		}
	}
}

package httpexpect

import (
	"bufio"
	"net"
	"net/http"
	"net/http/httptest"
	"sync"

	"github.com/gorilla/websocket"
	"github.com/valyala/fasthttp"
)

// NewWebsocketDialer produces new websocket.Dialer which dials to bound
// http.Handler without creating a real net.Conn.
func NewWebsocketDialer(handler http.Handler) *websocket.Dialer {
	return &websocket.Dialer{
		NetDial: func(network, addr string) (net.Conn, error) {
			hc := newHandlerConn()
			hc.runHandler(handler)
			return hc, nil
		},
	}
}

// NewFastWebsocketDialer produces new websocket.Dialer which dials to bound
// fasthttp.RequestHandler without creating a real net.Conn.
func NewFastWebsocketDialer(handler fasthttp.RequestHandler) *websocket.Dialer {
	return &websocket.Dialer{
		NetDial: func(network, addr string) (net.Conn, error) {
			hc := newHandlerConn()
			hc.runFastHandler(handler)
			return hc, nil
		},
	}
}

type handlerConn struct {
	net.Conn          // returned from dialer
	backConn net.Conn // passed to the background goroutine

	wg sync.WaitGroup
}

func newHandlerConn() *handlerConn {
	dialConn, backConn := net.Pipe()

	return &handlerConn{
		Conn:     dialConn,
		backConn: backConn,
	}
}

func (hc *handlerConn) Close() error {
	err := hc.Conn.Close()
	hc.wg.Wait() // wait the background goroutine
	return err
}

func (hc *handlerConn) runHandler(handler http.Handler) {
	hc.wg.Add(1)

	go func() {
		defer hc.wg.Done()

		recorder := &hijackRecorder{conn: hc.backConn}

		for {
			req, err := http.ReadRequest(bufio.NewReader(hc.backConn))
			if err != nil {
				return
			}
			handler.ServeHTTP(recorder, req)
		}
	}()
}

func (hc *handlerConn) runFastHandler(handler fasthttp.RequestHandler) {
	hc.wg.Add(1)

	go func() {
		defer hc.wg.Done()

		_ = fasthttp.ServeConn(hc.backConn, handler)
	}()
}

// hijackRecorder it similar to httptest.ResponseRecorder,
// but with Hijack capabilities.
//
// Original idea is stolen from https://github.com/posener/wstest
type hijackRecorder struct {
	httptest.ResponseRecorder
	conn net.Conn
}

// Hijack the connection for caller.
//
// Implements http.Hijacker interface.
func (r *hijackRecorder) Hijack() (net.Conn, *bufio.ReadWriter, error) {
	rw := bufio.NewReadWriter(bufio.NewReader(r.conn), bufio.NewWriter(r.conn))
	return r.conn, rw, nil
}

// WriteHeader write HTTP header to the client and closes the connection
//
// Implements http.ResponseWriter interface.
func (r *hijackRecorder) WriteHeader(code int) {
	resp := http.Response{StatusCode: code, Header: r.Header()}
	_ = resp.Write(r.conn)
}

package httpexpect

import (
	"encoding/json"
	"errors"
	"fmt"

	"github.com/gorilla/websocket"
)

// WebsocketMessage provides methods to inspect message read from WebSocket connection.
type WebsocketMessage struct {
	noCopy noCopy
	chain  *chain

	typ       int
	content   []byte
	closeCode int
}

// NewWebsocketMessage returns a new WebsocketMessage instance.
//
// If reporter is nil, the function panics.
// Content may be nil.
//
// Example:
//
//	m := NewWebsocketMessage(t, websocket.TextMessage, []byte("content"), 0)
//	m.TextMessage()
func NewWebsocketMessage(
	reporter Reporter, typ int, content []byte, closeCode ...int,
) *WebsocketMessage {
	return newWebsocketMessage(
		newChainWithDefaults("WebsocketMessage()", reporter),
		typ,
		content,
		closeCode...,
	)
}

// NewWebsocketMessageC returns a new WebsocketMessage instance with config.
//
// Requirements for config are same as for WithConfig function.
// Content may be nil.
//
// Example:
//
//	m := NewWebsocketMessageC(config, websocket.TextMessage, []byte("content"), 0)
//	m.TextMessage()
func NewWebsocketMessageC(
	config Config, typ int, content []byte, closeCode ...int,
) *WebsocketMessage {
	return newWebsocketMessage(
		newChainWithConfig("WebsocketMessage()", config.withDefaults()),
		typ,
		content,
		closeCode...,
	)
}

func newWebsocketMessage(
	parent *chain, typ int, content []byte, closeCode ...int,
) *WebsocketMessage {
	wm := newEmptyWebsocketMessage(parent)

	opChain := wm.chain.enter("")
	defer opChain.leave()

	wm.typ = typ

	if content != nil {
		wm.content = make([]byte, len(content))
		copy(wm.content, content)
	} else {
		wm.content = []byte{}
	}

	if len(closeCode) > 1 {
		opChain.fail(AssertionFailure{
			Type: AssertUsage,
			Errors: []error{
				errors.New("unexpected multiple closeCode arguments"),
			},
		})
		return wm
	}

	if len(closeCode) != 0 {
		wm.closeCode = closeCode[0]
	}

	return wm
}

func newEmptyWebsocketMessage(parent *chain) *WebsocketMessage {
	return &WebsocketMessage{
		chain: parent.clone(),
	}
}

// Raw returns underlying type, content and close code of WebSocket message.
// Theses values are originally read from WebSocket connection.
func (wm *WebsocketMessage) Raw() (typ int, content []byte, closeCode int) {
	return wm.typ, wm.content, wm.closeCode
}

// Alias is similar to Value.Alias.
func (wm *WebsocketMessage) Alias(name string) *WebsocketMessage {
	opChain := wm.chain.enter("Alias(%q)", name)
	defer opChain.leave()

	wm.chain.setAlias(name)
	return wm
}

// CloseMessage is a shorthand for m.Type(websocket.CloseMessage).
func (wm *WebsocketMessage) CloseMessage() *WebsocketMessage {
	opChain := wm.chain.enter("CloseMessage()")
	defer opChain.leave()

	wm.checkType(opChain, websocket.CloseMessage)

	return wm
}

// NotCloseMessage is a shorthand for m.NotType(websocket.CloseMessage).
func (wm *WebsocketMessage) NotCloseMessage() *WebsocketMessage {
	opChain := wm.chain.enter("NotCloseMessage()")
	defer opChain.leave()

	wm.checkNotType(opChain, websocket.CloseMessage)

	return wm
}

// BinaryMessage is a shorthand for m.Type(websocket.BinaryMessage).
func (wm *WebsocketMessage) BinaryMessage() *WebsocketMessage {
	opChain := wm.chain.enter("BinaryMessage()")
	defer opChain.leave()

	wm.checkType(opChain, websocket.BinaryMessage)

	return wm
}

// NotBinaryMessage is a shorthand for m.NotType(websocket.BinaryMessage).
func (wm *WebsocketMessage) NotBinaryMessage() *WebsocketMessage {
	opChain := wm.chain.enter("NotBinaryMessage()")
	defer opChain.leave()

	wm.checkNotType(opChain, websocket.BinaryMessage)

	return wm
}

// TextMessage is a shorthand for m.Type(websocket.TextMessage).
func (wm *WebsocketMessage) TextMessage() *WebsocketMessage {
	opChain := wm.chain.enter("TextMessage()")
	defer opChain.leave()

	wm.checkType(opChain, websocket.TextMessage)

	return wm
}

// NotTextMessage is a shorthand for m.NotType(websocket.TextMessage).
func (wm *WebsocketMessage) NotTextMessage() *WebsocketMessage {
	opChain := wm.chain.enter("NotTextMessage()")
	defer opChain.leave()

	wm.checkNotType(opChain, websocket.TextMessage)

	return wm
}

// Type succeeds if WebSocket message type is one of the given.
//
// WebSocket message types are defined in RFC 6455, section 11.8.
// See also https://godoc.org/github.com/gorilla/websocket#pkg-constants
//
// Example:
//
//	msg := conn.Expect()
//	msg.Type(websocket.TextMessage, websocket.BinaryMessage)
func (wm *WebsocketMessage) Type(types ...int) *WebsocketMessage {
	opChain := wm.chain.enter("Type()")
	defer opChain.leave()

	wm.checkType(opChain, types...)

	return wm
}

// NotType succeeds if WebSocket message type is none of the given.
//
// WebSocket message types are defined in RFC 6455, section 11.8.
// See also https://godoc.org/github.com/gorilla/websocket#pkg-constants
//
// Example:
//
//	msg := conn.Expect()
//	msg.NotType(websocket.CloseMessage, websocket.BinaryMessage)
func (wm *WebsocketMessage) NotType(types ...int) *WebsocketMessage {
	opChain := wm.chain.enter("NotType()")
	defer opChain.leave()

	wm.checkNotType(opChain, types...)

	return wm
}

func (wm *WebsocketMessage) checkType(opChain *chain, types ...int) {
	if opChain.failed() {
		return
	}

	if len(types) == 0 {
		opChain.fail(AssertionFailure{
			Type: AssertUsage,
			Errors: []error{
				errors.New("missing type argument"),
			},
		})
		return
	}

	found := false
	for _, t := range types {
		if t == wm.typ {
			found = true
			break
		}
	}

	if !found {
		if len(types) == 1 {
			opChain.fail(AssertionFailure{
				Type:     AssertEqual,
				Actual:   &AssertionValue{wsMessageType(wm.typ)},
				Expected: &AssertionValue{wsMessageType(types[0])},
				Errors: []error{
					errors.New("expected: message types are equal"),
				},
			})
		} else {
			typeList := make([]interface{}, 0, len(types))
			for _, t := range types {
				typeList = append(typeList, wsMessageType(t))
			}

			opChain.fail(AssertionFailure{
				Type:     AssertBelongs,
				Actual:   &AssertionValue{wsMessageType(wm.typ)},
				Expected: &AssertionValue{AssertionList(typeList)},
				Errors: []error{
					errors.New("expected: message type belongs to given list"),
				},
			})
		}
	}
}

func (wm *WebsocketMessage) checkNotType(opChain *chain, types ...int) {
	if opChain.failed() {
		return
	}

	if len(types) == 0 {
		opChain.fail(AssertionFailure{
			Type: AssertUsage,
			Errors: []error{
				errors.New("missing type argument"),
			},
		})
		return
	}

	found := false
	for _, t := range types {
		if t == wm.typ {
			found = true
			break
		}
	}

	if found {
		if len(types) == 1 {
			opChain.fail(AssertionFailure{
				Type:     AssertNotEqual,
				Actual:   &AssertionValue{wsMessageType(wm.typ)},
				Expected: &AssertionValue{wsMessageType(types[0])},
				Errors: []error{
					errors.New("expected: message types are non-equal"),
				},
			})
		} else {
			typeList := make([]interface{}, 0, len(types))
			for _, t := range types {
				typeList = append(typeList, wsMessageType(t))
			}

			opChain.fail(AssertionFailure{
				Type:     AssertNotBelongs,
				Actual:   &AssertionValue{wsMessageType(wm.typ)},
				Expected: &AssertionValue{AssertionList(typeList)},
				Errors: []error{
					errors.New("expected: message type does not belong to given list"),
				},
			})
		}
	}
}

// Code succeeds if WebSocket close code is one of the given.
//
// Code fails if WebSocket message type is not "8 - Connection Close Frame".
//
// WebSocket close codes are defined in RFC 6455, section 11.7.
// See also https://godoc.org/github.com/gorilla/websocket#pkg-constants
//
// Example:
//
//	msg := conn.Expect().Closed()
//	msg.Code(websocket.CloseNormalClosure, websocket.CloseGoingAway)
func (wm *WebsocketMessage) Code(codes ...int) *WebsocketMessage {
	opChain := wm.chain.enter("Code()")
	defer opChain.leave()

	wm.checkCode(opChain, codes...)

	return wm
}

// NotCode succeeds if WebSocket close code is none of the given.
//
// NotCode fails if WebSocket message type is not "8 - Connection Close Frame".
//
// WebSocket close codes are defined in RFC 6455, section 11.7.
// See also https://godoc.org/github.com/gorilla/websocket#pkg-constants
//
// Example:
//
//	msg := conn.Expect().Closed()
//	msg.NotCode(websocket.CloseAbnormalClosure, websocket.CloseNoStatusReceived)
func (wm *WebsocketMessage) NotCode(codes ...int) *WebsocketMessage {
	opChain := wm.chain.enter("NotCode()")
	defer opChain.leave()

	wm.checkNotCode(opChain, codes...)

	return wm
}

func (wm *WebsocketMessage) checkCode(opChain *chain, codes ...int) {
	if opChain.failed() {
		return
	}

	if len(codes) == 0 {
		opChain.fail(AssertionFailure{
			Type: AssertUsage,
			Errors: []error{
				errors.New("missing code argument"),
			},
		})
		return
	}

	if wm.typ != websocket.CloseMessage {
		opChain.fail(AssertionFailure{
			Type:     AssertEqual,
			Actual:   &AssertionValue{wsMessageType(wm.typ)},
			Expected: &AssertionValue{wsMessageType(websocket.CloseMessage)},
			Errors: []error{
				errors.New("expected: close message"),
			},
		})
		return
	}

	found := false
	for _, c := range codes {
		if c == wm.closeCode {
			found = true
			break
		}
	}

	if !found {
		if len(codes) == 1 {
			opChain.fail(AssertionFailure{
				Type:     AssertEqual,
				Actual:   &AssertionValue{wsCloseCode(wm.closeCode)},
				Expected: &AssertionValue{wsCloseCode(codes[0])},
				Errors: []error{
					errors.New("expected: close codes are equal"),
				},
			})
		} else {
			codeList := make([]interface{}, 0, len(codes))
			for _, c := range codes {
				codeList = append(codeList, wsCloseCode(c))
			}

			opChain.fail(AssertionFailure{
				Type:     AssertBelongs,
				Actual:   &AssertionValue{wsCloseCode(wm.closeCode)},
				Expected: &AssertionValue{AssertionList(codeList)},
				Errors: []error{
					errors.New("expected: close code belongs to given list"),
				},
			})
		}
	}
}

func (wm *WebsocketMessage) checkNotCode(opChain *chain, codes ...int) {
	if opChain.failed() {
		return
	}

	if len(codes) == 0 {
		opChain.fail(AssertionFailure{
			Type: AssertUsage,
			Errors: []error{
				errors.New("missing code argument"),
			},
		})
		return
	}

	if wm.typ != websocket.CloseMessage {
		opChain.fail(AssertionFailure{
			Type:     AssertEqual,
			Actual:   &AssertionValue{wsMessageType(wm.typ)},
			Expected: &AssertionValue{wsMessageType(websocket.CloseMessage)},
			Errors: []error{
				errors.New("expected: close message"),
			},
		})
		return
	}

	found := false
	for _, c := range codes {
		if c == wm.closeCode {
			found = true
			break
		}
	}

	if found {
		if len(codes) == 1 {
			opChain.fail(AssertionFailure{
				Type:     AssertNotEqual,
				Actual:   &AssertionValue{wsCloseCode(wm.closeCode)},
				Expected: &AssertionValue{wsCloseCode(codes[0])},
				Errors: []error{
					errors.New("expected: close codes are non-equal"),
				},
			})
		} else {
			codeList := make([]interface{}, 0, len(codes))
			for _, c := range codes {
				codeList = append(codeList, wsCloseCode(c))
			}

			opChain.fail(AssertionFailure{
				Type:     AssertNotBelongs,
				Actual:   &AssertionValue{wsCloseCode(wm.closeCode)},
				Expected: &AssertionValue{AssertionList(codeList)},
				Errors: []error{
					errors.New("expected: close code dose not belong to given list"),
				},
			})
		}
	}
}

// NoContent succeeds if WebSocket message has no content (is empty).
func (wm *WebsocketMessage) NoContent() *WebsocketMessage {
	opChain := wm.chain.enter("NoContent()")
	defer opChain.leave()

	if opChain.failed() {
		return wm
	}

	if len(wm.content) != 0 {
		var actual interface{}
		switch wm.typ {
		case websocket.BinaryMessage:
			actual = wm.content

		default:
			actual = string(wm.content)
		}

		opChain.fail(AssertionFailure{
			Type:   AssertEmpty,
			Actual: &AssertionValue{actual},
			Errors: []error{
				errors.New("expected: message content is empty"),
			},
		})
	}

	return wm
}

// Body returns a new String instance with WebSocket message content.
//
// Example:
//
//	msg := conn.Expect()
//	msg.Body().NotEmpty()
//	msg.Body().Length().IsEqual(100)
func (wm *WebsocketMessage) Body() *String {
	opChain := wm.chain.enter("Body()")
	defer opChain.leave()

	if opChain.failed() {
		return newString(opChain, "")
	}

	return newString(opChain, string(wm.content))
}

// JSON returns a new Value instance with JSON contents of WebSocket message.
//
// JSON succeeds if JSON may be decoded from message content.
//
// Example:
//
//	msg := conn.Expect()
//	msg.JSON().Array().ConsistsOf("foo", "bar")
func (wm *WebsocketMessage) JSON() *Value {
	opChain := wm.chain.enter("JSON()")
	defer opChain.leave()

	if opChain.failed() {
		return newValue(opChain, nil)
	}

	var value interface{}

	if err := json.Unmarshal(wm.content, &value); err != nil {
		opChain.fail(AssertionFailure{
			Type: AssertValid,
			Actual: &AssertionValue{
				string(wm.content),
			},
			Errors: []error{
				errors.New("failed to decode json"),
				err,
			},
		})
		return newValue(opChain, nil)
	}

	return newValue(opChain, value)
}

type wsMessageType int

func (wmt wsMessageType) String() string {
	s := "unknown"

	switch wmt {
	case websocket.TextMessage:
		s = "text"
	case websocket.BinaryMessage:
		s = "binary"
	case websocket.CloseMessage:
		s = "close"
	case websocket.PingMessage:
		s = "ping"
	case websocket.PongMessage:
		s = "pong"
	}

	return fmt.Sprintf("%s(%d)", s, wmt)
}

type wsCloseCode int

// https://developer.mozilla.org/en-US/docs/Web/API/CloseEvent/code
func (wcc wsCloseCode) String() string {
	s := "Unknown"

	switch wcc {
	case 1000:
		s = "NormalClosure"
	case 1001:
		s = "GoingAway"
	case 1002:
		s = "ProtocolError"
	case 1003:
		s = "UnsupportedData"
	case 1004:
		s = "Reserved"
	case 1005:
		s = "NoStatusReceived"
	case 1006:
		s = "AbnormalClosure"
	case 1007:
		s = "InvalidFramePayloadData"
	case 1008:
		s = "PolicyViolation"
	case 1009:
		s = "MessageTooBig"
	case 1010:
		s = "MandatoryExtension"
	case 1011:
		s = "InternalServerError"
	case 1012:
		s = "ServiceRestart"
	case 1013:
		s = "TryAgainLater"
	case 1014:
		s = "BadGateway"
	case 1015:
		s = "TLSHandshake"
	}

	return fmt.Sprintf("%s(%d)", s, wcc)
}

