func TestWebsocket_Subprotocol(t *testing.T) {
	subproto := "soap"
	ws := NewWebsocketC(
		Config{
			Reporter: NewAssertReporter(t),
		},
		&mockWebsocketConn{
			subprotocol: subproto,
		})

	ws.Subprotocol()

	if got := ws.Subprotocol().value; got != subproto {
		t.Errorf("Websocket.Subprotocol() = %v, want %v", got, subproto)
	}
}

