package gitlab

import (
	"bytes"
	"fmt"
	"reflect"
)

// Stringify attempts to create a reasonable string representation of types in
// the Gitlab library.  It does things like resolve pointers to their values
// and omits struct fields with nil values.
func Stringify(message interface{}) string {
	var buf bytes.Buffer
	v := reflect.ValueOf(message)
	stringifyValue(&buf, v)
	return buf.String()
}

// stringifyValue was heavily inspired by the goprotobuf library.
func stringifyValue(buf *bytes.Buffer, val reflect.Value) {
	if val.Kind() == reflect.Ptr && val.IsNil() {
		buf.WriteString("<nil>")
		return
	}

	v := reflect.Indirect(val)

	switch v.Kind() {
	case reflect.String:
		fmt.Fprintf(buf, `"%s"`, v)
	case reflect.Slice:
		buf.WriteByte('[')
		for i := 0; i < v.Len(); i++ {
			if i > 0 {
				buf.WriteByte(' ')
			}

			stringifyValue(buf, v.Index(i))
		}

		buf.WriteByte(']')
		return
	case reflect.Struct:
		if v.Type().Name() != "" {
			buf.WriteString(v.Type().String())
		}

		buf.WriteByte('{')

		var sep bool
		for i := 0; i < v.NumField(); i++ {
			fv := v.Field(i)
			if fv.Kind() == reflect.Ptr && fv.IsNil() {
				continue
			}
			if fv.Kind() == reflect.Slice && fv.IsNil() {
				continue
			}

			if sep {
				buf.WriteString(", ")
			} else {
				sep = true
			}

			buf.WriteString(v.Type().Field(i).Name)
			buf.WriteByte(':')
			stringifyValue(buf, fv)
		}

		buf.WriteByte('}')
	default:
		if v.CanInterface() {
			fmt.Fprint(buf, v.Interface())
		}
	}
}

package gitlab

import (
	"testing"

	"github.com/stretchr/testify/assert"
)

type Person struct {
	Name      string
	Age       int
	NickNames []string
	Address   Address
	Company   *Company
}

type Address struct {
	Street   string
	City     string
	Province string
	Country  string
}

type Company struct {
	Name    string
	Address Address
	Country string
}

func TestStringify_nil(t *testing.T) {
	var person *Person
	resp := Stringify(person)
	assert.Equal(t, "<nil>", resp)
}

func TestStringify(t *testing.T) {
	person := &Person{"name", 16, []string{"n", "a", "m", "e"}, Address{}, nil}
	resp := Stringify(person)
	want := "gitlab.Person{Name:\"name\", Age:16, NickNames:[\"n\" \"a\" \"m\" \"e\"], Address:gitlab.Address{Street:\"\", City:\"\", Province:\"\", Country:\"\"}}"
	assert.Equal(t, want, resp)
}

func TestStringify_emptySlice(t *testing.T) {
	person := &Person{"name", 16, nil, Address{}, nil}
	resp := Stringify(person)
	want := "gitlab.Person{Name:\"name\", Age:16, Address:gitlab.Address{Street:\"\", City:\"\", Province:\"\", Country:\"\"}}"
	assert.Equal(t, want, resp)
}

