package giu

import (
	"fmt"
	"log"
	"runtime"
	"strings"
	"sync"
	"unsafe"

	"github.com/AllenDang/cimgui-go/imgui"
	"github.com/AllenDang/cimgui-go/utils"
	"github.com/AllenDang/go-findfont"
)

const (
	preRegisterString = " \"#$%&'()*+,-./0123456789:;<=>?@ABCDEFGHIJKLMNOPQRSTUVWXYZ[\\]^_`abcdefghijklmnopqrstuvwxyz{|}~"
	windows           = "windows"
	defaultFontSize   = 14
)

// FontInfo represents a giu implementation of imgui font.
type FontInfo struct {
	fontName string
	fontPath string
	fontByte []byte
	size     float32
}

// String returns a string representation of the FontInfo. It is intended to be unique for each FontInfo.
func (f *FontInfo) String() string {
	return fmt.Sprintf("%s:%.2f", f.fontName, f.size)
}

// SetSize sets the font size.
func (f *FontInfo) SetSize(size float32) *FontInfo {
	result := *f
	result.size = size

	for _, i := range Context.FontAtlas.extraFonts {
		if i.String() == result.String() {
			return &result
		}
	}

	Context.FontAtlas.extraFonts = append(Context.FontAtlas.extraFonts, result)
	Context.FontAtlas.shouldRebuildFontAtlas = true

	return &result
}

// FontAtlas is a mechanism to automatically manage fonts in giu.
// When you add a string in your app, it is registered inside the FontAtlas.
// Then, font data are built based on the registered strings.
// for more details, see: https://github.com/ocornut/imgui/blob/master/docs/FONTS.md
// DefaultFont = font that is used for normal rendering.
// ExtraFont = font that can be set and then it'll be used for rendering things.
type FontAtlas struct {
	shouldRebuildFontAtlas bool
	stringMap              sync.Map // key is rune, value indicates whether it's a new rune.
	defaultFonts           []FontInfo
	extraFonts             []FontInfo
	extraFontMap           map[string]*imgui.Font
	fontSize               float32
}

func newFontAtlas() *FontAtlas {
	result := FontAtlas{
		extraFontMap: make(map[string]*imgui.Font),
		fontSize:     defaultFontSize,
	}

	// Pre register numbers
	result.RegisterString(preRegisterString)

	// Pre-register fonts
	switch runtime.GOOS {
	case "darwin":
		// English font
		result.registerDefaultFont("Menlo", result.fontSize)
		// Chinese font
		result.registerDefaultFont("STHeiti", result.fontSize-1)
		// Jananese font
		result.registerDefaultFont(" W0", result.fontSize+3)
		// Korean font
		result.registerDefaultFont("AppleSDGothicNeo", result.fontSize+2)
	case windows:
		// English font
		result.registerDefaultFont("Calibri", result.fontSize+2)
		// Chinese font
		result.registerDefaultFont("MSYH", result.fontSize+2)
		// Japanese font
		result.registerDefaultFont("MSGOTHIC", result.fontSize+2)
		// Korean font
		result.registerDefaultFont("MALGUNSL", result.fontSize+2)
	case "linux":
		// English fonts
		result.registerDefaultFonts([]FontInfo{
			{
				fontName: "FreeSans.ttf",
				size:     result.fontSize + 1,
			},
			{
				fontName: "FiraCode-Medium",
				size:     result.fontSize + 1,
			},
			{
				fontName: "sans",
				size:     result.fontSize + 1,
			},
		})
		// Chinese fonts
		result.registerDefaultFonts([]FontInfo{
			{
				fontName: "wqy-microhei",
				size:     result.fontSize + 1,
			},
			{
				fontName: "SourceHanSansCN",
				size:     result.fontSize + 3,
			},
		})
	}

	return &result
}

// SetDefaultFontSize sets the default font size. Invoke this before MasterWindow.NewMasterWindow(..).
func (a *FontAtlas) SetDefaultFontSize(size float32) {
	a.fontSize = size
}

// SetDefaultFont changes default font.
func (a *FontAtlas) SetDefaultFont(fontName string, size float32) {
	fontPath, err := findfont.Find(fontName)
	if err != nil {
		log.Fatalf("Cannot find font %s", fontName)
		return
	}

	fontInfo := FontInfo{fontName: fontName, fontPath: fontPath, size: size}
	a.defaultFonts = append([]FontInfo{fontInfo}, a.defaultFonts...)
}

// SetDefaultFontFromBytes changes default font by bytes of the font file.
func (a *FontAtlas) SetDefaultFontFromBytes(fontBytes []byte, size float32) {
	a.defaultFonts = append([]FontInfo{
		{
			fontByte: fontBytes,
			size:     size,
		},
	}, a.defaultFonts...)
}

// GetDefaultFonts returns a list of currently loaded default fonts.
func (a *FontAtlas) GetDefaultFonts() []FontInfo {
	return a.defaultFonts
}

// AddFont adds font by name, if the font is found, return *FontInfo, otherwise return nil.
// To use added font, use giu.Style().SetFont(...).
func (a *FontAtlas) AddFont(fontName string, size float32) *FontInfo {
	fontPath, err := findfont.Find(fontName)
	if err != nil {
		fmt.Printf("[Warning]Cannot find font %s at system, related text will not be rendered.\n", fontName)
		return nil
	}

	fi := FontInfo{
		fontName: fontName,
		fontPath: fontPath,
		size:     size,
	}

	a.extraFonts = append(a.extraFonts, fi)

	return &fi
}

// AddFontFromBytes does similar to AddFont, but using data from memory.
func (a *FontAtlas) AddFontFromBytes(fontName string, fontBytes []byte, size float32) *FontInfo {
	fi := FontInfo{
		fontName: fontName,
		fontByte: fontBytes,
		size:     size,
	}

	a.extraFonts = append(a.extraFonts, fi)

	return &fi
}

func (a *FontAtlas) registerDefaultFont(fontName string, size float32) {
	fontPath, err := findfont.Find(fontName)
	if err != nil {
		return
	}

	fontInfo := FontInfo{fontName: fontName, fontPath: fontPath, size: size}
	a.defaultFonts = append(a.defaultFonts, fontInfo)
}

func (a *FontAtlas) registerDefaultFonts(fontInfos []FontInfo) {
	var firstFoundFont *FontInfo

	for _, fi := range fontInfos {
		fontPath, err := findfont.Find(fi.fontName)
		if err == nil {
			firstFoundFont = &FontInfo{fontName: fi.fontName, fontPath: fontPath, size: fi.size}
			break
		}
	}

	if firstFoundFont != nil {
		a.defaultFonts = append(a.defaultFonts, *firstFoundFont)
	}
}

// RegisterString register string to font atlas builder.
// Note only register strings that will be displayed on the UI.
func (a *FontAtlas) RegisterString(str string) string {
	for _, s := range str {
		if _, ok := a.stringMap.Load(s); !ok {
			a.stringMap.Store(s, false)
			a.shouldRebuildFontAtlas = true
		}
	}

	return str
}

// RegisterStringPointer registers string pointer to font atlas builder.
// Note only register strings that will be displayed on the UI.
func (a *FontAtlas) RegisterStringPointer(str *string) *string {
	a.RegisterString(*str)
	return str
}

// RegisterStringSlice calls RegisterString for each slice element.
func (a *FontAtlas) RegisterStringSlice(str []string) []string {
	for _, s := range str {
		a.RegisterString(s)
	}

	return str
}

// Rebuild font atlas when necessary.
// The whole magic happens here.
func (a *FontAtlas) rebuildFontAtlas() {
	if !a.shouldRebuildFontAtlas {
		return
	}

	fonts := Context.IO().Fonts()
	fonts.Clear()

	var sb strings.Builder

	a.stringMap.Range(func(k, _ any) bool {
		a.stringMap.Store(k, true)

		if ks, ok := k.(rune); ok {
			sb.WriteRune(ks)
		}

		return true
	})

	ranges := imgui.NewGlyphRange()
	builder := imgui.NewFontGlyphRangesBuilder()

	// Because we pre-registered numbers, so default string map's length should greater then 11.
	if sb.Len() > len(preRegisterString) {
		builder.AddText(sb.String())
	} else {
		builder.AddRanges(fonts.GlyphRangesDefault())
	}

	builder.BuildRanges(ranges)

	if len(a.defaultFonts) > 0 {
		fontConfig := imgui.NewFontConfig()
		fontConfig.SetOversampleH(2)
		fontConfig.SetOversampleV(2)
		fontConfig.SetRasterizerMultiply(1.5)

		for i, fontInfo := range a.defaultFonts {
			if i > 0 {
				fontConfig.SetMergeMode(true)
			}

			// Scale font size with DPI scale factor
			if runtime.GOOS == windows {
				xScale, _ := Context.backend.ContentScale()
				fontInfo.size *= xScale
			}

			if len(fontInfo.fontByte) == 0 {
				fonts.AddFontFromFileTTFV(fontInfo.fontPath, fontInfo.size, fontConfig, ranges.Data())
			} else {
				fontConfig.SetFontDataOwnedByAtlas(false)
				fonts.AddFontFromMemoryTTFV(
					uintptr(unsafe.Pointer(utils.SliceToPtr(fontInfo.fontByte))),
					int32(len(fontInfo.fontByte)),
					fontInfo.size,
					fontConfig,
					ranges.Data(),
				)
			}
		}

		// Fall back if no font is added
		if fonts.FontCount() == 0 {
			fonts.AddFontDefault()
		}
	} else {
		fonts.AddFontDefault()
	}

	// Add extra fonts
	for _, fontInfo := range a.extraFonts {
		// Scale font size with DPI scale factor
		if runtime.GOOS == windows {
			xScale, _ := Context.backend.ContentScale()
			fontInfo.size *= xScale
		}

		// Store imgui.Font for PushFont
		var f *imgui.Font
		if len(fontInfo.fontByte) == 0 {
			f = fonts.AddFontFromFileTTFV(
				fontInfo.fontPath,
				fontInfo.size,
				imgui.NewFontConfig(),
				ranges.Data(),
			)
		} else {
			fontConfig := imgui.NewFontConfig()
			fontConfig.SetFontDataOwnedByAtlas(false)
			f = fonts.AddFontFromMemoryTTFV(
				uintptr(unsafe.Pointer(utils.SliceToPtr(fontInfo.fontByte))),
				int32(len(fontInfo.fontByte)),
				fontInfo.size,
				fontConfig,
				ranges.Data(),
			)
		}

		a.extraFontMap[fontInfo.String()] = f
	}

	fontTextureImg, w, h, _ := fonts.GetTextureDataAsRGBA32()
	tex := Context.backend.CreateTexture(fontTextureImg, int(w), int(h))
	fonts.SetTexID(tex)
	fonts.SetTexReady(true)

	a.shouldRebuildFontAtlas = false
}

package giu

import (
	"image/color"

	"github.com/AllenDang/cimgui-go/imgui"
	"github.com/AllenDang/cimgui-go/imguizmo"
	"github.com/AllenDang/cimgui-go/utils"
	glm "github.com/gucio321/glm-go"
)

// GizmoOperation specifies the operation of Gizmo (used by manipulate).
type GizmoOperation int

// Possible Operations.
const (
	OperationTranslateX GizmoOperation = GizmoOperation(imguizmo.TRANSLATEX)
	OperationTranslateY GizmoOperation = GizmoOperation(imguizmo.TRANSLATEY)
	OperationTranslateZ GizmoOperation = GizmoOperation(imguizmo.TRANSLATEZ)
	OperationTranslate  GizmoOperation = GizmoOperation(imguizmo.TRANSLATE)

	OperationRotateX GizmoOperation = GizmoOperation(imguizmo.ROTATEX)
	OperationRotateY GizmoOperation = GizmoOperation(imguizmo.ROTATEY)
	OperationRotateZ GizmoOperation = GizmoOperation(imguizmo.ROTATEZ)
	OperationRotate  GizmoOperation = GizmoOperation(imguizmo.ROTATE)

	OperationScaleX GizmoOperation = GizmoOperation(imguizmo.SCALEX)
	OperationScaleY GizmoOperation = GizmoOperation(imguizmo.SCALEY)
	OperationScaleZ GizmoOperation = GizmoOperation(imguizmo.SCALEZ)
	OperationScale  GizmoOperation = GizmoOperation(imguizmo.SCALE)

	OperationScaleXU GizmoOperation = GizmoOperation(imguizmo.SCALEXU)
	OperationScaleYU GizmoOperation = GizmoOperation(imguizmo.SCALEYU)
	OperationScaleZU GizmoOperation = GizmoOperation(imguizmo.SCALEZU)
	OperationScaleU  GizmoOperation = GizmoOperation(imguizmo.SCALEU)

	OperationBounds GizmoOperation = GizmoOperation(imguizmo.BOUNDS)

	OperationRotateScreen GizmoOperation = GizmoOperation(imguizmo.ROTATESCREEN)

	OperationUniversal GizmoOperation = GizmoOperation(imguizmo.UNIVERSAL)
)

// GizmoMode specifies the mode of Gizmo (used by manipulate).
type GizmoMode int

// values are not explained in source code.
const (
	ModeLocal GizmoMode = GizmoMode(imguizmo.LOCAL)
	ModeWorld GizmoMode = GizmoMode(imguizmo.WORLD)
)

// GizmoI should be implemented by every sub-element of GizmoWidget.
type GizmoI interface {
	Gizmo(view *ViewMatrix, projection *ProjectionMatrix)
}

var _ Widget = &GizmoWidget{}

// GizmoWidget implements ImGuizmo features.
// It is designed just like PlotWidget.
// This structure provides an "area" where you can put Gizmos (see (*GizmoWidget).Gizmos).
// If you want to have more understanding about what is going on here, read this:
// https://www.opengl-tutorial.org/beginners-tutorials/tutorial-3-matrices/ (DISCLAIMER: giu authors are not responsible if you go mad or something!)
type GizmoWidget struct {
	gizmos       []GizmoI
	view         *ViewMatrix
	projection   *ProjectionMatrix
	id           ID
	disabled     bool
	orthographic bool
}

// Gizmo creates a new GizmoWidget.
func Gizmo(view *ViewMatrix, projection *ProjectionMatrix) *GizmoWidget {
	return &GizmoWidget{
		gizmos:       []GizmoI{},
		view:         view,
		projection:   projection,
		id:           GenAutoID("gizmo"),
		disabled:     false,
		orthographic: false,
	}
}

// ID sets the ID of the GizmoWidget. (useful if you use multiple gizmo widgets. It is set by AutoID anyway).
func (g *GizmoWidget) ID(id ID) *GizmoWidget {
	g.id = id
	return g
}

// Disabled sets GizmoWidget's disabled state.
func (g *GizmoWidget) Disabled(b bool) *GizmoWidget {
	g.disabled = b
	return g
}

// Orthographic sets the projection matrix to orthographic.
func (g *GizmoWidget) Orthographic(b bool) *GizmoWidget {
	g.orthographic = b
	return g
}

// Gizmos adds GizmoI elements to the GizmoWidget area.
func (g *GizmoWidget) Gizmos(gizmos ...GizmoI) *GizmoWidget {
	g.gizmos = append(g.gizmos, gizmos...)
	return g
}

// build is a local build function.
// Just to separate Global() and Build() methods.
func (g *GizmoWidget) build() {
	imguizmo.PushIDStr(string(g.id))
	imguizmo.Enable(!g.disabled)
	imguizmo.SetOrthographic(g.orthographic)

	for _, gizmo := range g.gizmos {
		gizmo.Gizmo(g.view, g.projection)
	}

	imguizmo.PopID()
}

// Build implements Widget interface.
func (g *GizmoWidget) Build() {
	imguizmo.SetDrawlist()

	displaySize := imgui.ContentRegionAvail()
	pos0 := imgui.CursorScreenPos()

	imguizmo.SetRect(pos0.X, pos0.Y, displaySize.X, displaySize.Y)
	g.build()
}

// Global works like Build() but does not attach the gizmo to the current window.
func (g *GizmoWidget) Global() {
	displaySize := imgui.CurrentIO().DisplaySize()
	pos0 := imgui.MainViewport().Pos()
	imguizmo.SetRect(pos0.X, pos0.Y, displaySize.X, displaySize.Y)
	g.build()
}

// [Gizmos]

var _ GizmoI = &GridGizmo{}

// GridGizmo draws a grid in the gizmo area.
type GridGizmo struct {
	// default to Identity
	matrix    *ViewMatrix
	thickness float32
}

// Grid creates a new GridGizmo.
func Grid() *GridGizmo {
	return &GridGizmo{
		matrix:    IdentityMatrix(),
		thickness: 10,
	}
}

// Thickness sets a thickness of grid lines.
func (g *GridGizmo) Thickness(t float32) *GridGizmo {
	g.thickness = t
	return g
}

// Matrix allows to set grid matrix. Default to IdentityMatrix.
func (g *GridGizmo) Matrix(matrix *ViewMatrix) *GridGizmo {
	g.matrix = matrix
	return g
}

// Gizmo implements GizmoI interface.
func (g *GridGizmo) Gizmo(view *ViewMatrix, projection *ProjectionMatrix) {
	imguizmo.DrawGrid(
		view.getMatrix(),
		projection.getMatrix(),
		g.matrix.getMatrix(),
		g.thickness,
	)
}

var _ GizmoI = &CubeGizmo{}

// CubeGizmo draws a 3D cube in the gizmo area.
// View and Projection matrices are provided by GizmoWidget.
type CubeGizmo struct {
	matrix     *ViewMatrix
