// ImageWithURLWidget allows to display an image using
// an URL as image source.
type ImageWithURLWidget struct {
	id              ID
	imgURL          string
	downloadTimeout time.Duration
	whenLoading     Layout
	whenFailure     Layout
	onReady         func()
	onFailure       func(error)
	img             *ImageWidget
}

// ImageWithURL creates ImageWithURLWidget.
// The default size is the size of the image,
// to set a specific size, use .Size(width, height).
func ImageWithURL(url string) *ImageWithURLWidget {
	return &ImageWithURLWidget{
		id:              ID(fmt.Sprintf("ImageWithURL_%s", url)),
		imgURL:          url,
		downloadTimeout: 10 * time.Second,
		whenLoading:     Layout{Dummy(100, 100)},
		whenFailure:     Layout{Dummy(100, 100)},
		img:             Image(nil),
	}
}

// OnReady sets event trigger when image is downloaded and ready to display.
func (i *ImageWithURLWidget) OnReady(onReady func()) *ImageWithURLWidget {
	i.onReady = onReady
	return i
}

// OnFailure sets event trigger when image failed to download/load.
func (i *ImageWithURLWidget) OnFailure(onFailure func(error)) *ImageWithURLWidget {
	i.onFailure = onFailure
	return i
}

// OnClick sets click callback.
func (i *ImageWithURLWidget) OnClick(cb func()) *ImageWithURLWidget {
	i.img.OnClick(cb)
	return i
}

// Timeout sets download timeout.
func (i *ImageWithURLWidget) Timeout(downloadTimeout time.Duration) *ImageWithURLWidget {
	i.downloadTimeout = downloadTimeout
	return i
}

// Size sets image's size.
func (i *ImageWithURLWidget) Size(width, height float32) *ImageWithURLWidget {
	i.img.Size(width, height)
	return i
}

// LayoutForLoading allows to set layout rendered while loading an image.
func (i *ImageWithURLWidget) LayoutForLoading(widgets ...Widget) *ImageWithURLWidget {
	i.whenLoading = widgets
	return i
}

// LayoutForFailure allows to specify layout when image failed to download.
func (i *ImageWithURLWidget) LayoutForFailure(widgets ...Widget) *ImageWithURLWidget {
	i.whenFailure = widgets
	return i
}

// Build implements Widget interface.
func (i *ImageWithURLWidget) Build() {
	var imgState *imageState
	if imgState = GetState[imageState](Context, i.id); imgState == nil {
		imgState = &imageState{}
		SetState(Context, i.id, imgState)

		// Prevent multiple invocation to download image.
		downloadContext, cancelFunc := ctx.WithCancel(ctx.Background())

		SetState(Context, i.id, &imageState{loading: true, cancel: cancelFunc})

		errorFn := func(err error) {
			SetState(Context, i.id, &imageState{failure: true})

			// Trigger onFailure event
			if i.onFailure != nil {
				i.onFailure(err)
			}
		}

		go func() {
			// Load image from url
			client := &http.Client{Timeout: i.downloadTimeout}

			req, err := http.NewRequestWithContext(downloadContext, http.MethodGet, i.imgURL, http.NoBody)
			if err != nil {
				errorFn(err)
				return
			}

			resp, err := client.Do(req)
			if err != nil {
				errorFn(err)
				return
			}

			defer func() {
				if closeErr := resp.Body.Close(); closeErr != nil {
					errorFn(closeErr)
				}
			}()

			img, _, err := image.Decode(resp.Body)
			if err != nil {
				errorFn(err)
				return
			}

			rgba := ImageToRgba(img)

			EnqueueNewTextureFromRgba(rgba, func(tex *Texture) {
				SetState(Context, i.id, &imageState{
					loading: false,
					failure: false,
					texture: tex,
				})
			})

			// Trigger onReady event
			if i.onReady != nil {
				i.onReady()
			}
		}()
	}

	imgState = GetState[imageState](Context, i.id)

	switch {
	case imgState.failure:
		i.whenFailure.Build()
	case imgState.loading:
		i.whenLoading.Build()
	default:
		i.img.texture = imgState.texture
		i.img.Build()
	}
}

package giu

// input manager is used to register a keyboard shortcuts in an app.

// Shortcut represents a keyboard shortcut.
type Shortcut struct {
	Key      Key
	Modifier Modifier
	Callback func()
	IsGlobal ShortcutType
}

// WindowShortcut represents a window-level shortcut
// could be used as an argument to (*Window).RegisterKeyboardShortcuts.
type WindowShortcut struct {
	Key      Key
	Modifier Modifier
	Callback func()
}

// ShortcutType represents a type of shortcut (global or local).
type ShortcutType bool

const (
	// GlobalShortcut is registered for all the app.
	GlobalShortcut ShortcutType = true

	// LocalShortcut is registered for current window only.
	LocalShortcut ShortcutType = false
)

// InputHandlerHandleCallback is a callback which is called when a shortcut is triggered.
type InputHandlerHandleCallback func(Key, Modifier, Action)

// InputHandler is an interface which needs to be implemented
// by user-defined input handlers.
type InputHandler interface {
	// RegisterKeyboardShortcuts adds a specified shortcuts into input handler
	RegisterKeyboardShortcuts(...Shortcut)
	// UnregisterKeyboardShortcuts removes window shortcuts from input handler
	UnregisterWindowShortcuts()
	// Handle handles a shortcut
	Handle(Key, Modifier, Action)
}

// --- Default implementation of giu input manager ---

var _ InputHandler = &inputHandler{}

func newInputHandler() *inputHandler {
	return &inputHandler{
		shortcuts: make(map[keyCombo]*callbacks),
	}
}

type inputHandler struct {
	shortcuts map[keyCombo]*callbacks
}

func (i *inputHandler) RegisterKeyboardShortcuts(s ...Shortcut) {
	for _, shortcut := range s {
		combo := keyCombo{shortcut.Key, shortcut.Modifier}

		cb, isRegistered := i.shortcuts[combo]
		if !isRegistered {
			cb = &callbacks{}
		}

		if shortcut.IsGlobal {
			cb.global = shortcut.Callback
		} else {
			cb.window = shortcut.Callback
		}

		i.shortcuts[combo] = cb
	}
}

func (i *inputHandler) UnregisterWindowShortcuts() {
	for _, s := range i.shortcuts {
		s.window = nil
	}
}

func (i *inputHandler) Handle(key Key, mod Modifier, a Action) {
	if a != Press {
		return
	}

	for combo, cb := range i.shortcuts {
		if combo.key != key || combo.modifier != mod {
			continue
		}

		if cb.window != nil {
			cb.window()
		} else if cb.global != nil {
			cb.global()
		}
	}
}

type keyCombo struct {
	key      Key
	modifier Modifier
}

type callbacks struct {
	global func()
	window func()
}

package giu

import (
	"testing"

	"github.com/stretchr/testify/assert"
)

func Test_InputHandler_new(t *testing.T) {
	i := newInputHandler()
	assert.NotNil(t, i, "input handler wasn't created")
	assert.NotNil(t, i.shortcuts, "input handler wasn't created")
}

func Test_InputHandle_RegisterKeyboardShortcuts(t *testing.T) {
	tests := []struct {
		id       string
		key      Key
		mod      Modifier
		isGlobal ShortcutType
		cb       func()
	}{
		{"global shortcut", Key(1), Modifier(2), ShortcutType(true), func() {}},
		{"window shortcut", Key(9), Modifier(3), ShortcutType(false), func() {}},
	}

	for _, tt := range tests {
		t.Run(tt.id, func(lt *testing.T) {
			a := assert.New(lt)
			i := newInputHandler()
			i.RegisterKeyboardShortcuts(Shortcut{
				Key:      tt.key,
				Modifier: tt.mod,
				Callback: tt.cb,
				IsGlobal: tt.isGlobal,
			})

			combo := keyCombo{
				key:      tt.key,
				modifier: tt.mod,
			}

			shortcut, exist := i.shortcuts[combo]

			a.True(exist, "shortcut wasn't registered in input manager")

			if tt.isGlobal {
				// TODO: figure out why it doesn't work
				// a.Equal(shortcut.global, tt.cb, "wrong shortcut set in input manager")
				a.NotNil(shortcut.global, "wrong shortcut set in input manager")
				a.Nil(shortcut.window, "wrong shortcut set in input manager")
			} else {
				// TODO: figure out why it doesn't work
				// a.Equal(shortcut.window, tt.cb, "wrong shortcut set in input manager")
				a.NotNil(shortcut.window, "wrong shortcut set in input manager")
				a.Nil(shortcut.global, "wrong shortcut set in input manager")
			}
		})
	}
}

func Test_InputHandler_UnregisterWindowShortcuts(t *testing.T) {
	i := newInputHandler()
	sh := []Shortcut{
		{Key(5), Modifier(0), func() {}, true},
		{Key(8), Modifier(2), func() {}, false},
	}

	i.RegisterKeyboardShortcuts(sh...)

	i.UnregisterWindowShortcuts()

	for _, s := range i.shortcuts {
		assert.Nil(t, s.window, "some window shortcuts wasn't unregistered")
	}
}

func Test_InputHandler_Handle(t *testing.T) {
	a := assert.New(t)
	i := newInputHandler()

	var shortcut1, shortcut2 bool

	sh := []Shortcut{
		{Key(5), Modifier(0), func() { shortcut1 = true }, true},
		{Key(8), Modifier(2), func() { shortcut2 = true }, false},
	}

	i.RegisterKeyboardShortcuts(sh...)

	i.Handle(Key(0), Modifier(0), Press)
	a.False(shortcut1, "Shortcut 1 was handled, but shouldn't.")
	a.False(shortcut2, "Shortcut 2 was handled, but shouldn't.")
	i.Handle(Key(5), Modifier(0), Press)
	a.True(shortcut1, "Shortcut 1 was not handled, but should be.")
	a.False(shortcut2, "Shortcut 2 was handled, but shouldn't.")
	i.Handle(Key(8), Modifier(2), Press)
	a.True(shortcut1, "Shortcut 1 was not handled, but should be.")
	a.True(shortcut2, "Shortcut 2 was not handled, but should be.")
}

package giu

const (
	// Auto is used to widget.Size to indicate height or width to occupy available spaces.
	Auto float32 = -1
)

// Widget is a base unit of giu rendering system.
// each widget just needs to implement Build method which is called,
// when widget needs to be rendered.
type Widget interface {
	Build()
}

var (
	_ Widget    = Layout{}
	_ Splitable = Layout{}
)

// Layout is a set of widgets. It implements Widget interface so
// Layout can be used as a widget.
type Layout []Widget

// Build implements Widget interface.
func (l Layout) Build() {
	for _, w := range l {
		if w != nil {
			w.Build()
		}
	}
}

// Splitable is implemented by widgets, which can be split (ranged)
// Layout implements Splitable.
type Splitable interface {
	Range(func(w Widget))
}

// Range ranges over the Layout, calling rangeFunc
// on each loop iteration.
func (l Layout) Range(rangeFunc func(Widget)) {
	for _, w := range l {
		if splitable, canRange := w.(Splitable); canRange {
			splitable.Range(rangeFunc)
			continue
		}

		rangeFunc(w)
	}
}

package giu

import (
	"testing"

	"github.com/stretchr/testify/assert"
)

type testwidget struct {
	counter *int
}

func (w *testwidget) Build() {
	if w.counter == nil {
		return
	}

	*w.counter++
}

type splitablewidget struct {
	w1, w2 *testwidget
}

func (w *splitablewidget) Build() {
	w.w1.Build()
	w.w2.Build()
}

func (w *splitablewidget) Range(r func(w Widget)) {
	r(w.w1)
	r(w.w2)
}

func Test_Layout_Range(t *testing.T) {
	tests := []struct {
		name                     string
		expectedTestWidgetsCount int
		layout                   Layout
	}{
		{"standard layout", 3, Layout{
			&testwidget{},
			&testwidget{},
			&testwidget{},
		}},
		{"layout with splitable widgets", 4, Layout{
			&testwidget{},
			&splitablewidget{&testwidget{}, &testwidget{}},
			&testwidget{},
		}},
	}

	for _, test := range tests {
		t.Run(test.name, func(tt *testing.T) {
			counter := 0

			test.layout.Range(func(w Widget) {
				if _, isTestwidget := w.(*testwidget); isTestwidget {
					counter++
				}
			})

			assert.Equal(tt, test.expectedTestWidgetsCount, counter, "Layout wasn't ranged correctly")
		})
	}
}

func Test_Layout_Build(t *testing.T) {
	tests := []struct {
		name                        string
		expectedNumTestWidgetsBuilt int
		layout                      Layout
	}{
		{"standard layout", 2, Layout{
			&testwidget{},
			&testwidget{},
		}},
		{"layout with nil widgets", 2, Layout{
			&testwidget{},
			nil,
			&testwidget{},
		}},
		{"layout with nested layouts", 5, Layout{
			&testwidget{},
			Layout{
				&testwidget{},
				&testwidget{},
				Layout{
					&testwidget{},
				},
			},
			&testwidget{},
		}},
	}

	for _, test := range tests {
		t.Run(test.name, func(tt *testing.T) {
			counter := 0

			test.layout.Range(func(w Widget) {
				if tw, isTestwidget := w.(*testwidget); isTestwidget {
					tw.counter = &counter
				}
			})

			test.layout.Build()

			assert.Equal(tt, test.expectedNumTestWidgetsBuilt, counter, "layout wasn't built correctly")
		})
	}
}

package giu

import "github.com/AllenDang/cimgui-go/imgui"

var _ Widget = &ListClipperWrapper{}

// ListClipperWrapper is a ImGuiListClipper implementation.
// it can be used to display a large, vertical list of items and
// avoid rendering them.
type ListClipperWrapper struct {
	layout Layout
}

// ListClipper creates list clipper.
func ListClipper() *ListClipperWrapper {
	return &ListClipperWrapper{}
}

// Layout sets layout for list clipper.
func (l *ListClipperWrapper) Layout(layout ...Widget) *ListClipperWrapper {
	l.layout = layout
	return l
}

// Build implements widget interface.
func (l *ListClipperWrapper) Build() {
	// read all the layout widgets and (eventually) split nested layouts
	var layout Layout

	l.layout.Range(func(w Widget) {
		layout = append(layout, w)
	})

	clipper := imgui.NewListClipper()
	defer clipper.Destroy()

	clipper.Begin(int32(len(layout)))

	for clipper.Step() {
		for i := clipper.DisplayStart(); i < clipper.DisplayEnd(); i++ {
			layout[i].Build()
		}
	}

	clipper.End()
}

package giu

import (
	"image"
	"image/color"
	"net/http"
	"strings"
	"time"

	"github.com/AllenDang/cimgui-go/backend"
	"github.com/AllenDang/cimgui-go/imgui"
	"github.com/AllenDang/cimgui-go/immarkdown"
)

type markdownState struct {
	cfg    immarkdown.MarkdownConfig
	images map[string]immarkdown.MarkdownImageData
}

func (m *markdownState) Dispose() {
	// noop
}

// MarkdownWidget implements DearImGui markdown extension
// https://github.com/juliettef/imgui_markdown
// It is like LabelWidget but with md formatting.
type MarkdownWidget struct {
	md      string
	id      ID
	headers [3]immarkdown.MarkdownHeadingFormat
}

func (m *MarkdownWidget) getState() *markdownState {
	if s := GetState[markdownState](Context, m.id); s != nil {
		return s
	}

	newState := m.newState()
	SetState[markdownState](Context, m.id, newState)

	return newState
}

func (m *MarkdownWidget) newState() *markdownState {
	cfg := immarkdown.NewEmptyMarkdownConfig()
	fmtCb := immarkdown.MarkdownFormalCallback(func(data *immarkdown.MarkdownFormatInfo, start bool) {
		immarkdown.DefaultMarkdownFormatCallback(*data, start)
	})

	cfg.SetFormatCallback(&fmtCb)

	imgCb := immarkdown.MarkdownImageCallback(func(data immarkdown.MarkdownLinkCallbackData) immarkdown.MarkdownImageData {
		link := data.Link()[:data.LinkLength()] // this is because imgui_markdown returns the whole text starting on link and returns link length (for some reason)
		if existing, ok := m.getState().images[link]; ok {
			return existing
		}

		result := mdLoadImage(link)
		m.getState().images[link] = result

		return result
	})

	cfg.SetImageCallback(&imgCb)

	return &markdownState{
		cfg:    *cfg,
		images: make(map[string]immarkdown.MarkdownImageData),
	}
}

// Markdown creates new markdown widget.
func Markdown(md string) *MarkdownWidget {
	return (&MarkdownWidget{
		md: md,
		id: GenAutoID("MarkdownWidget"),
		headers: [3]immarkdown.MarkdownHeadingFormat{
			*immarkdown.NewEmptyMarkdownHeadingFormat(),
			*immarkdown.NewEmptyMarkdownHeadingFormat(),
			*immarkdown.NewEmptyMarkdownHeadingFormat(),
		},
	}).OnLink(OpenURL)
}

// OnLink sets another than default link callback.
// NOTE: due to cimgui-go's limitation https://github.com/AllenDang/cimgui-go?tab=readme-ov-file#callbacks
// we clear MarkdownLinkCallback pool every frame. No further action from you should be required (just feel informed).
// ref (*MasterWindow).beforeRender.
func (m *MarkdownWidget) OnLink(cb func(url string)) *MarkdownWidget {
	igCb := immarkdown.MarkdownLinkCallback(func(data immarkdown.MarkdownLinkCallbackData) {
		link := data.Link()[:data.LinkLength()]
		cb(link)
	})

	m.getState().cfg.SetLinkCallback(&igCb)

	return m
}

// Header sets header formatting
// NOTE: level (counting from 0!) is header level. (for instance, header `# H1` will have level 0).
// NOTE: since cimgui-go there are only 3 levels (so level < 3 here). This will panic if level >= 3!
// TODO: it actually doesn't work.
func (m *MarkdownWidget) Header(level int, font *FontInfo, separator bool) *MarkdownWidget {
	// ensure level is in range
	Assert(level < 3, "MarkdownWidget", "Header", "Header level must be less than 3!")

	m.headers[level] = *immarkdown.NewEmptyMarkdownHeadingFormat()

	if font != nil {
		if f, ok := Context.FontAtlas.extraFontMap[font.String()]; ok {
			m.headers[level].SetFont(f)
		}
	}

	m.headers[level].SetSeparator(separator)

	state := m.getState()
	state.cfg.SetHeadingFormats(&m.headers)

	return m
}

// Build implements Widget interface.
func (m *MarkdownWidget) Build() {
	state := m.getState()
	immarkdown.Markdown(
		Context.FontAtlas.RegisterString(m.md),
		uint64(len(m.md)),
		state.cfg,
	)
}

func mdLoadImage(path string) immarkdown.MarkdownImageData {
	var (
		img *image.RGBA
		err error
	)

	switch {
	case strings.HasPrefix(path, "http://") || strings.HasPrefix(path, "https://"):
		// Load image from url
		client := &http.Client{Timeout: 5 * time.Second}

		resp, respErr := client.Get(path)
		if respErr != nil {
			return *immarkdown.NewEmptyMarkdownImageData()
		}

		defer func() {
			closeErr := resp.Body.Close()
			Assert((closeErr == nil), "MarkdownWidget", "mdLoadImage", "Could not close http request!")
		}()

		rgba, _, imgErr := image.Decode(resp.Body)
		if imgErr != nil {
			return *immarkdown.NewEmptyMarkdownImageData()
		}

		img = ImageToRgba(rgba)
	default:
		img, err = LoadImage(path)
		if err != nil {
			return *immarkdown.NewEmptyMarkdownImageData()
		}
	}

	size := img.Bounds()
	id := backend.NewTextureFromRgba(img).ID

	result := immarkdown.NewEmptyMarkdownImageData()
	result.SetUsertextureid(id)
	result.SetSize(imgui.Vec2{
		X: float32(size.Dx()),
		Y: float32(size.Dy()),
	})
	result.SetUseLinkCallback(true)
	result.SetUv0(ToVec2(image.Point{0, 0}))
	result.SetUv1(ToVec2(image.Point{1, 1}))
	result.SetTintcol(ToVec4Color(color.RGBA{255, 255, 255, 255}))
	result.SetBordercol(ToVec4Color(color.RGBA{0, 0, 0, 0}))

	result.SetIsValid(true)

	return *result
}

