}

// ExecCondition represents imgui.Cond.
type ExecCondition imgui.Cond

// imgui conditions.
const (
	ConditionAlways       ExecCondition = ExecCondition(imgui.CondAlways)
	ConditionOnce         ExecCondition = ExecCondition(imgui.CondOnce)
	ConditionFirstUseEver ExecCondition = ExecCondition(imgui.CondFirstUseEver)
	ConditionAppearing    ExecCondition = ExecCondition(imgui.CondAppearing)
)

// SetNextWindowPos sets position of next window.
func SetNextWindowPos(x, y float32) {
	imgui.SetNextWindowPos(imgui.Vec2{X: x, Y: y})
}

// SetNextWindowSizeV does similar to SetNextWIndowSize but allows to specify imgui.Cond.
func SetNextWindowSizeV(width, height float32, condition ExecCondition) {
	imgui.SetNextWindowSizeV(
		imgui.Vec2{
			X: width,
			Y: height,
		},
		imgui.Cond(condition),
	)
}

// SetItemDefaultFocus set the item focused by default.
func SetItemDefaultFocus() {
	imgui.SetItemDefaultFocus()
}

// SetKeyboardFocusHere sets keyboard focus at *NEXT* widget.
func SetKeyboardFocusHere() {
	SetKeyboardFocusHereV(0)
}

// SetKeyboardFocusHereV sets keyboard on the next widget. Use positive 'offset' to access sub components of a multiple component widget. Use -1 to access previous widget.
func SetKeyboardFocusHereV(i int) {
	imgui.SetKeyboardFocusHereV(int32(i))
}

// PushClipRect pushes a clipping rectangle for both ImGui logic (hit-testing etc.) and low-level ImDrawList rendering.
func PushClipRect(clipRectMin, clipRectMax image.Point, intersectWithClipRect bool) {
	imgui.PushClipRect(ToVec2(clipRectMin), ToVec2(clipRectMax), intersectWithClipRect)
}

// PopClipRect should be called to end PushClipRect.
func PopClipRect() {
	imgui.PopClipRect()
}

// Assert checks if cond. If not cond, it alls golang panic.
func Assert(cond bool, t, method, msg string, args ...any) {
	if !cond {
		fatal(t, method, msg, args...)
	}
}

func fatal(widgetName, method, message string, args ...any) {
	if widgetName != "" {
		widgetName = fmt.Sprintf("(*%s)", widgetName)
	}

	log.Panicf("giu: %s.%s: %s", widgetName, method, fmt.Sprintf(message, args...))
}

// OpenURL opens `url` in default browser.
func OpenURL(url string) {
	if err := browser.OpenURL(url); err != nil {
		log.Printf("Error opening %s: %v", url, err)
	}
}

// ColorToUint converts GO color into Uint32 color
// it is 0xRRGGBBAA.
func ColorToUint(col color.Color) uint32 {
	r, g, b, a := col.RGBA()
	mask := uint32(0xff)

	return r&mask +
		g&mask<<8 +
		b&mask<<16 +
		a&mask<<24
}

// UintToColor converts uint32 of form 0xRRGGBB into color.RGBA.
func UintToColor(col uint32) *color.RGBA {
	mask := 0xff
	r := byte(col >> 24 & uint32(mask))
	g := byte(col >> 16 & uint32(mask))
	b := byte(col >> 8 & uint32(mask))
	a := byte(col >> 0 & uint32(mask))

	return &color.RGBA{
		R: r,
		G: g,
		B: b,
		A: a,
	}
}

// Deg2Rad converts degrees to radians.
func Deg2Rad(deg float32) float32 {
	return deg * 0.01745329252 // deg * 2pi / 360
}

package giu

import (
	"image"
	"image/color"
	"testing"

	"github.com/AllenDang/cimgui-go/imgui"
	"github.com/stretchr/testify/assert"
)

func Test_ToVec4(t *testing.T) {
	tests := []struct {
		name     string
		source   color.Color
		expected imgui.Vec4
	}{
		{
			name:     "Red - RGBA",
			source:   &color.RGBA{R: 255, G: 0, B: 0, A: 255},
			expected: imgui.Vec4{X: 1, Y: 0, Z: 0, W: 1},
		},
		{
			name:     "Purple - RGBA",
			source:   &color.RGBA{R: 158, G: 0, B: 173, A: 255},
			expected: imgui.Vec4{X: 0.61960787, Y: 0, Z: 0.6784314, W: 1},
		},
		{
			name:     "Purple - CMYK",
			source:   &color.CMYK{C: 22, M: 255, Y: 0, K: 82},
			expected: imgui.Vec4{X: 0.6198978, Y: 0, Z: 0.6784314, W: 1},
		},
	}

	for _, test := range tests {
		t.Run(test.name, func(tt *testing.T) {
			assert.Equal(tt, test.expected, ToVec4Color(test.source), "Unexpected result")
		})
	}
}

func Test_ToVec2(t *testing.T) {
	tests := []struct {
		name     string
		source   image.Point
		expected imgui.Vec2
	}{
		{"Point 0,0", image.Pt(0, 0), imgui.Vec2{X: 0, Y: 0}},
		{"Random point 1", image.Pt(80, 209), imgui.Vec2{X: 80, Y: 209}},
		{"Random point 2", image.Pt(200, 128), imgui.Vec2{X: 200, Y: 128}},
	}

	for _, test := range tests {
		t.Run(test.name, func(tt *testing.T) {
			assert.Equal(tt, test.expected, ToVec2(test.source), "Unexpected result")
		})
	}
}

func Test_Vec4ToRGBA(t *testing.T) {
	tests := []struct {
		name     string
		source   imgui.Vec4
		expected color.RGBA
	}{
		{
			name:     "Red",
			source:   imgui.Vec4{X: 1, Y: 0, Z: 0, W: 1},
			expected: color.RGBA{R: 255, G: 0, B: 0, A: 255},
		},
		{
			name:     "Red - with 20% alpha",
			source:   imgui.Vec4{X: 1, Y: 0, Z: 0, W: 0.2},
			expected: color.RGBA{R: 255, G: 0, B: 0, A: 51},
		},
		{
			name:     "Purple - RGBA",
			source:   imgui.Vec4{X: 0.61960787, Y: 0, Z: 0.6784314, W: 1},
			expected: color.RGBA{R: 158, G: 0, B: 173, A: 255},
		},
	}

	for _, test := range tests {
		t.Run(test.name, func(tt *testing.T) {
			assert.Equal(tt, test.expected, Vec4ToRGBA(test.source), "Unexpected result")
		})
	}
}

func Test_Assert(t *testing.T) {
	tests := []struct {
		name        string
		condition   bool
		shouldPanic bool
	}{
		{"expected behavior - no panic", true, false},
		{"something happened - panic", false, true},
	}

	for _, test := range tests {
		t.Run(test.name, func(tt *testing.T) {
			if test.shouldPanic {
				assert.Panics(tt, func() { Assert(test.condition, "somewidget", "somemethod", "panics") }, "unexpected behavior")
			} else {
				assert.NotPanics(tt, func() { Assert(test.condition, "somewidget", "somemethod", "panics") }, "unexpected behavior")
			}
		})
	}
}

func TestUintToColor(t *testing.T) {
	tests := []struct {
		name string
		col  uint32
		want *color.RGBA
	}{
		{"full red full alpha", 0xFF0000FF, &color.RGBA{R: 255, G: 0, B: 0, A: 255}},
		{"full red 0 alpha", 0xFF000000, &color.RGBA{R: 255, G: 0, B: 0, A: 0}},
		{"full green", 0x00FF00FF, &color.RGBA{R: 0, G: 255, B: 0, A: 255}},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			assert.Equalf(t, tt.want, UintToColor(tt.col), "UintToColor(%v)", tt.col)
		})
	}
}

