{
    "title": "区块链系列教程",
    "description": "从基本原型，工作量证明，到交易，网络等各部分的区块链系列教程",
    "author": "liuchengxu",
    "output.name": "site",
    "language": "zh-hans",
    "gitbook": "3.2.3",
    "root": "./content",
    "links": {
        "sidebar": {
            "Home": "https://github.com/liuchengxu/blockchain-tutorial"
        }
    },
    "styles": {
        "website": "styles/website.css"
    },
    "plugins": [
        "-lunr",
        "-search",
        "-highlight",
        "-livereload",
        "search-plus@^0.0.11",
        "simple-page-toc@^0.1.1",
        "github@^2.0.0",
        "github-buttons@2.1.0",
        "edit-link@^2.0.2",
        "prism@^2.1.0",
        "prism-themes@^0.0.2",
        "advanced-emoji@^0.2.1",
        "anchors@^0.7.1",
        "include-codeblock@^3.0.2",
        "ace@^0.3.2",
        "emphasize@^1.1.0",
        "katex@^1.1.3",
        "splitter@^0.0.8",
        "mermaid-gb3@2.1.0",
        "tbfed-pagefooter@^0.0.1",
        "expandable-chapters-small@^0.1.7",
        "sectionx@^3.1.0",
        "local-video@^1.0.1",
        "anchor-navigation-ex@0.1.8",
        "favicon@^0.0.2",
        "alerts@^0.2.0",
        "include-csv@^0.1.0",
        "puml@^1.0.1",
        "-sharing",
        "sharing-plus@^0.0.2",
        "image-captions",
        "donate"
    ],

    "pluginsConfig": {
        "theme-default": {
            "showLevel": true
        },
        "prism": {
            "css": [
                "prism-themes/themes/prism-base16-ateliersulphurpool.light.css"
            ]
        },
        "github": {
            "url": "https://github.com/liuchengxu/blockchain-tutorial"
        },
        "github-buttons": {
            "repo": "liuchengxu/blockchain-tutorial",
            "types": [
                "star",
                "watch",
                "fork"
            ],
            "size": "small"
        },
        "include-codeblock": {
            "template": "ace",
            "unindent": true,
            "edit": true
        },
        "sharing": {
           "douban": false,
           "facebook": false,
           "google": true,
           "hatenaBookmark": false,
           "instapaper": false,
           "line": false,
           "linkedin": false,
           "messenger": false,
           "pocket": false,
           "qq": false,
           "qzone": false,
           "stumbleupon": false,
           "twitter": true,
           "viber": false,
           "vk": false,
           "weibo": false,
           "whatsapp": false,
           "all": [
               "facebook", "google", "twitter",
               "weibo", "instapaper", "linkedin",
               "pocket", "stumbleupon", "qq", "qzone"
           ]
       },
        "tbfed-pagefooter": {
            "copyright": "Copyright © liuchengxu 2017-2018",
            "modify_label": "该文件修订时间：",
            "modify_format": "YYYY-MM-DD HH:mm:ss"
        },
        "simple-page-toc": {
            "maxDepth": 3,
            "skipFirstH1": true
        },
        "edit-link": {
            "base": "https://github.com/liuchengxu/blockchain-tutorial/edit/master",
            "label": "Edit This Page"
        },
        "sitemap-general": {
            "prefix": "http://blockchain.liuchengxu.org"
        },
        "anchor-navigation-ex": {
            "isRewritePageTitle": false,
            "tocLevel1Icon": "fa fa-hand-o-right",
            "tocLevel2Icon": "fa fa-hand-o-right",
            "tocLevel3Icon": "fa fa-hand-o-right"
        },
        "sectionx": {
            "tag": "b"
        },
        "favicon": {
            "shortcut": "favicon.ico",
            "bookmark": "favicon.ico"
        },
        "image-captions": {
            "caption": "_PAGE_LEVEL_._PAGE_IMAGE_NUMBER_ -- _CAPTION_"
        },
        "donate": {
            "wechat": "https://raw.githubusercontent.com/liuchengxu/img/master/gitbook/wechat.jpeg",
            "alipay": "https://raw.githubusercontent.com/liuchengxu/img/master/gitbook/alipay.jpeg",
            "title": "",
            "button": "赞赏 👍  ",
            "alipayText": "支付宝打赏",
            "wechatText": "微信打赏"
        }
    }
}
