	// context without headers will return empty map
	ctx := &cbContext{
		msg: &message{
			key: "key",
		},
	}
	headers := ctx.Headers()
	require.NotNil(t, headers)
	require.Equal(t, 0, len(headers))

	ctx = &cbContext{
		msg: &message{
			key: "key",
			headers: []*sarama.RecordHeader{
				{
					Key:   []byte("key"),
					Value: []byte("value"),
				},
			},
		},
	}
	headers = ctx.Headers()
	require.Equal(t, []byte("value"), headers["key"])
}

