package goka

import (
	"log"
	"sync/atomic"
	"testing"
	"time"
)

// This tests how a producer behaves when being shutdown to make sure,
// no promises stay unfinished.
// To run the test, get a local kafka-container running (e.g. go to
// examples-directory and do `make restart`), then run the tests with
// `go test -v github.com/lovoo/goka/kafka/ -tags=kafka`
func TestProducerError(t *testing.T) {
	cfg := DefaultConfig()
	p, err := NewProducer([]string{"localhost:9092"}, cfg)

	if err != nil {
		t.Fatalf("error creating producer: %v", err)
	}

	var (
		promises     []*Promise
		donePromises int64
		emitted      int64
		done         = make(chan bool)
	)

	go func() {
		defer func() {
			recover()
		}()
		defer close(done)
		for {
			promise := p.Emit("test", "test", []byte{})
			promise.Then(func(err error) {
				atomic.AddInt64(&donePromises, 1)
				if err != nil {
					log.Printf("error producing message: %v", err)
				}
			})
			promises = append(promises, promise)
			emitted++
			time.Sleep(20 * time.Millisecond)
		}
	}()

	// let it run for 1 second
	time.Sleep(1000 * time.Millisecond)

	// close the producer
	err = p.Close()
	if err != nil {
		log.Printf("Error closing producer: %v", err)
	}
	// wait for the promises to be
	<-done

	if len(promises) != int(emitted) {
		t.Errorf("Promises/Emits do not match: promises: %d, emitted. %d", len(promises), emitted)
	}
	if len(promises) != int(donePromises) {
		t.Errorf("Promises/Done promises do not match: promises: %d, done. %d", len(promises), donePromises)
	}
}

package goka

import (
	"sync"

	"github.com/IBM/sarama"
)

// Promise as in https://en.wikipedia.org/wiki/Futures_and_promises
type Promise struct {
	sync.Mutex
	err      error
	msg      *sarama.ProducerMessage
	finished bool

	callbacks []func(msg *sarama.ProducerMessage, err error)
}

// PromiseFinisher finishes a promise
type PromiseFinisher func(msg *sarama.ProducerMessage, err error) *Promise

// NewPromise creates a new Promise
func NewPromise() *Promise {
	return new(Promise)
}

// NewPromiseWithFinisher creates a new Promise and a separate finish method.
// This is necessary if the promise is used outside of goka package.
func NewPromiseWithFinisher() (*Promise, PromiseFinisher) {
	p := new(Promise)
	return p, p.finish
}

// execute all callbacks conveniently
// The caller needs to lock!
func (p *Promise) executeCallbacks() {
	// already resolved
	if p.finished {
		return
	}
	for _, s := range p.callbacks {
		s(p.msg, p.err)
	}
	// mark as finished
	p.finished = true
}

// Then chains a callback to the Promise
func (p *Promise) Then(callback func(err error)) *Promise {
	return p.ThenWithMessage(func(_ *sarama.ProducerMessage, err error) {
		callback(err)
	})
}

// ThenWithMessage chains a callback to the Promise
func (p *Promise) ThenWithMessage(callback func(msg *sarama.ProducerMessage, err error)) *Promise {
	p.Lock()
	defer p.Unlock()

	// promise already run, call the callback immediately
	if p.finished {
		callback(p.msg, p.err)
		// append it to the subscribers otherwise
	} else {
		p.callbacks = append(p.callbacks, callback)
	}
	return p
}

// Finish finishes the promise by executing all callbacks and saving the message/error for late subscribers
func (p *Promise) finish(msg *sarama.ProducerMessage, err error) *Promise {
	p.Lock()
	defer p.Unlock()

	p.err = err
	p.msg = msg

	p.executeCallbacks()
	return p
}

