func (a *ec2Autoscaler) ResourceIDFromTags(tags models.Tags) (resourceID, error) {
	// Get the name of the resource
	var name string
	switch {
	case a.groupName != "":
		name = a.groupName
	case a.groupNameTag != "":
		t, ok := tags[a.groupNameTag]
		if ok {
			name = t
		}
	default:
		return nil, errors.New("expected one of GroupName or GroupNameTag to be set")
