import (
	"bufio"
	"fmt"
	"os"
)

// LineReader will accept the name of a file and offset as argument
// and will return a channel from which lines can be read
// one at a time.
func LineReader(filename string) (chan string, error) {
	fp, err := os.Open(filename)
	if err != nil {
		return nil, err
	}

	out := make(chan string)
	go func() {
		defer func() {
			if err = fp.Close(); err != nil {
				fmt.Printf("error closing %s: %v\n", filename, err)
			}
		}()

		// we need to close the out channel in order
		// to signal the end-of-data condition
		defer close(out)

		scanner := bufio.NewScanner(fp)
		scanner.Split(bufio.ScanLines)
		for scanner.Scan() {
			out <- scanner.Text()
		}
	}()

	return out, nil
}

package xray

import (
	"runtime"
	"sync"
	"sync/atomic"
	"time"
)

// This structure contains some runtime statistics.
type Statistics struct {
	// Time the execution started
	Start time.Time
	// Time the execution finished
	Stop time.Time
	// Total duration of the execution
	Total time.Duration
	// Total number of inputs from the wordlist
	Inputs uint64
	// Executions per second
	Eps float64
	// Total number of executions
	Execs uint64
	// Total number of executions with positive results.
	Results uint64
	// % of progress as: ( execs / inputs ) * 100.0
	Progress float64
}

// This is where the main logic goes.
type RunHandler func(line string) interface{}

// This is where positive results are handled.
type ResultHandler func(result interface{})

// The main object.
type Machine struct {
	// Runtime statistics.
	Stats Statistics
	// Number of input consumers.
	consumers uint
	// Dictionary file name.
	filename string
	// Positive results channel.
	output chan interface{}
	// Inputs channel.
	input chan string
	// WaitGroup to stop while the machine is running.
	wait sync.WaitGroup
	// Main logic handler.
	run_handler RunHandler
	// Positive results handler.
	res_handler ResultHandler
}

// Builds a new machine object, if consumers is less or equal than 0, CPU*2 will be used as default value.
func NewMachine(consumers int, filename string, session *Session, run_handler RunHandler, res_handler ResultHandler) *Machine {
	workers := uint(0)
	if consumers <= 0 {
		workers = uint(runtime.NumCPU() * 2)
	} else {
		workers = uint(consumers)
	}

	var stats *Statistics
	if session.Stats != nil && session.Stats.Execs > 0 {
		stats = session.Stats
	} else {
		stats = &Statistics{}
	}

	return &Machine{
		Stats:       *stats,
		consumers:   workers,
		filename:    filename,
		output:      make(chan interface{}),
		input:       make(chan string),
		wait:        sync.WaitGroup{},
		run_handler: run_handler,
		res_handler: res_handler,
	}
}

func (m *Machine) inputConsumer() {
	for in := range m.input {
		atomic.AddUint64(&m.Stats.Execs, 1)

		res := m.run_handler(in)
		if res != nil {
			atomic.AddUint64(&m.Stats.Results, 1)
			m.output <- res
		}
		m.wait.Done()
	}
}

func (m *Machine) outputConsumer() {
	for res := range m.output {
		m.res_handler(res)
	}
}

func (m *Machine) AddInput(input string) {
	m.wait.Add(1)
	m.input <- input
}

// Start the machine.
func (m *Machine) Start() error {
	// start a fixed amount of consumers for inputs
	for i := uint(0); i < m.consumers; i++ {
		go m.inputConsumer()
	}

	// start the output consumer on a goroutine
	go m.outputConsumer()

	// count the inputs we have
	m.Stats.Inputs = 0

	go func(m *Machine) {
		var n = uint64(0)
		if lines, err := LineReader(m.filename); err == nil {
			for range lines {
				n++
			}
		}

		// this way, Inputs will go from 0 directly to N
		atomic.AddUint64(&m.Stats.Inputs, n)
	}(m)

	lines, err := LineReader(m.filename)
	if err != nil {
		return err
	}

	// If the stats have been loaded from a session file.
	if m.Stats.Execs > 0 {
		n := m.Stats.Execs
		for range lines {
			n--
			if n == 0 {
				break
			}
		}
	} else {
		m.Stats.Start = time.Now()
	}

	go func() {
		if ctx := GetContext(); ctx != nil {
			for _, sub := range ctx.CSH.GetSubDomains(ctx) {
				m.AddInput(sub)
			}
		}
	}()

	for line := range lines {
		m.AddInput(line)
	}

	return nil
}

func (m *Machine) UpdateStats() {
	m.Stats.Stop = time.Now()
	m.Stats.Total = m.Stats.Stop.Sub(m.Stats.Start)
	m.Stats.Eps = float64(m.Stats.Execs) / m.Stats.Total.Seconds()
	m.Stats.Progress = (float64(m.Stats.Execs) / float64(m.Stats.Inputs)) * 100.0
}

// Wait for all jobs to be completed.
func (m *Machine) Wait() {
	// wait for everything to be completed
	m.wait.Wait()
	m.UpdateStats()
}

package xray

import (
	"sort"
	"sync"
)

type Pool struct {
	sync.RWMutex
	addrs   []string
	Session *Session
}

func NewPool(session *Session) *Pool {
	return &Pool{
		Session: session,
		addrs:   make([]string, 0),
	}
}

func (p *Pool) WasRestored() bool {
	return len(p.Session.Targets) > 0
}

func (p *Pool) FlushSession(stats *Statistics) {
	p.Lock()
	defer p.Unlock()

	p.Session.Flush(stats)
}

func (p *Pool) Find(address string) *Target {
	p.RLock()
	defer p.RUnlock()

	t, found := p.Session.Targets[address]
	if found {
		return t
	} else {
		return nil
	}
}

func (p *Pool) Add(t *Target) {
	p.Lock()
	defer p.Unlock()

	p.Session.Targets[t.Address] = t
}

func (p *Pool) Sorted() []string {
	if len(p.addrs) == 0 {
		p.addrs = make([]string, 0, len(p.Session.Targets))
		for addr := range p.Session.Targets {
			p.addrs = append(p.addrs, addr)
		}
		sort.Strings(p.addrs)
	}

	return p.addrs
}

package xray

import (
	"encoding/json"
	"fmt"
	"io/ioutil"
	"os"
)

var SessionDefaultFilename = "<domain-name>-xray-session.json"

type Session struct {
	filename string
	Stats    *Statistics
	Targets  map[string]*Target
}

func GetSessionFileName(domain string) string {
	return fmt.Sprintf("%s-xray-session.json", domain)
}

func NewSession(filename string) *Session {
	s := &Session{
		filename: filename,
		Stats:    nil,
		Targets:  make(map[string]*Target),
	}

	if _, err := os.Stat(s.filename); !os.IsNotExist(err) {
		fmt.Printf("@ Restoring session from %s ...\n", s.filename)
		if data, e := ioutil.ReadFile(s.filename); e == nil {
			if e = json.Unmarshal(data, &s); e != nil {
				panic(e)
			}

			fmt.Printf("@ Loaded %d entries from session file.\n", len(s.Targets))
		} else {
			panic(e)
		}
	}

	return s
}

func (s *Session) Flush(stats *Statistics) {
	s.Stats = stats
	if data, err := json.Marshal(s); err == nil {
		if err = ioutil.WriteFile(s.filename, data, 0644); err != nil {
			panic(err)
		}
	} else {
		panic(err)
	}
}

