        # nix develop .#dev
        devShells.dev = pkgs.mkShell {
          packages = with pkgs; [
            go-task
            gofumpt
          ];
        };

        # nix develop .#packagers
        devShells.packagers = pkgs.mkShell {
          packages = with pkgs; [
            dpkg
          ] ++ (lib.optionals pkgs.stdenv.isLinux [
            apk-tools
            rpm
          ]);

        };

        # nix develop .#docs
        devShells.docs = pkgs.mkShell {
          packages = with pkgs; with pkgs.python311Packages; [
