package main

import (
	"bufio"
	"encoding/json"
	"errors"
	"fmt"
	"io"
	"log"
	"os"
	"path"

	"github.com/steebchen/prisma-client-go/generator"
	"github.com/steebchen/prisma-client-go/jsonrpc"
	"github.com/steebchen/prisma-client-go/logger"
)

const DmmfWriteKey = "PRISMA_CLIENT_GO_WRITE_DMMF_FILE"

var writeDebugFile = os.Getenv(DmmfWriteKey) != ""

func reply(w io.Writer, data interface{}) error {
	b, err := json.Marshal(data)
	if err != nil {
		return fmt.Errorf("could not marshal data %w", err)
	}

	b = append(b, byte('\n'))

	if _, err = w.Write(b); err != nil {
		return fmt.Errorf("could not write data %w", err)
	}

	return nil
}

func invokePrisma() error {
	reader := bufio.NewReader(os.Stdin)

	if logger.Enabled || writeDebugFile {
		dir, _ := os.Getwd()
		log.Printf("current working dir: %s", dir)
	}

	for {
		content, err := reader.ReadBytes('\n')
		if errors.Is(err, io.EOF) {
			logger.Debug.Printf("warning: ignoring EOF error. stdin: `%s`", content)
			return nil
		}
		if err != nil {
			return fmt.Errorf("could not read bytes from stdin: %w", err)
		}

		if writeDebugFile {
			if err := os.WriteFile("dmmf.json", content, 0600); err != nil {
				return fmt.Errorf("could not write dmmf.json: %w", err)
			}
		}

		var input jsonrpc.Request

		if err := json.Unmarshal(content, &input); err != nil {
			return fmt.Errorf("could not open stdin %w", err)
		}

		var response interface{}

		switch input.Method {
		case "getManifest":
			response = jsonrpc.ManifestResponse{
				Manifest: jsonrpc.Manifest{
					DefaultOutput: path.Join(".", "db"),
					PrettyName:    "Prisma Client Go",
				},
			}

		case "generate":
			response = nil // success

			var params generator.Root

			if err := json.Unmarshal(input.Params, &params); err != nil {
				dir, _ := os.Getwd()
				return fmt.Errorf("could not unmarshal params into generator.Root type at %s: %w", dir, err)
			}

			generator.Transform(&params)

			if err := generator.Run(&params); err != nil {
				return fmt.Errorf("could not generate code. %w", err)
			}
		default:
			return fmt.Errorf("no such method %s", input.Method)
		}

		if err := reply(os.Stderr, jsonrpc.NewResponse(input.ID, response)); err != nil {
			return fmt.Errorf("could not reply %w", err)
		}
	}
}

package main

import (
	"log"
	"os"
	"os/signal"
	"syscall"

	"github.com/steebchen/prisma-client-go/cli"
	"github.com/steebchen/prisma-client-go/logger"
)

func main() {
	if len(os.Args) > 1 {
		args := os.Args[1:]
		logger.Debug.Printf("invoking command %+v", args)

		switch args[0] {
		case "prefetch":
			// just run prisma -v to trigger the download
			if err := cli.Run([]string{"-v"}, true); err != nil {
				panic(err)
			}
			os.Exit(0)
			return
		case "init":
			// override default init flags
			args = append(args, "--generator-provider", "go run github.com/steebchen/prisma-client-go")
			if err := cli.Run(args, true); err != nil {
				panic(err)
			}
			os.Exit(0)
			return
		}

		// prisma CLI
		if err := cli.Run(args, true); err != nil {
			panic(err)
		}

		return
	}

	// running the prisma generator

	logger.Debug.Printf("invoking prisma")

	// if this wasn't actually invoked by the prisma generator, print a warning and exit
	if os.Getenv("PRISMA_GENERATOR_INVOCATION") == "" {
		logger.Info.Printf("This command is only meant to be invoked internally. Please run the following instead:")
		logger.Info.Printf("`go run github.com/steebchen/prisma-client-go <command>`")
		logger.Info.Printf("e.g.")
		logger.Info.Printf("`go run github.com/steebchen/prisma-client-go generate`")
		os.Exit(1)
	}

	// exit when signal triggers
	c := make(chan os.Signal, 1)
	signal.Notify(c, os.Interrupt, syscall.SIGTERM)

	go func() {
		<-c
		os.Exit(1)
	}()

	if err := invokePrisma(); err != nil {
		log.Printf("error occurred when invoking prisma: %s", err)
		os.Exit(1)
	}

	logger.Debug.Printf("success")
}
