			name:            "object-error",
			json:            `{"testStr" "hello world!"}`,
			err:             true,
			errType:         &json.SyntaxError{},
			skipCheckResult: true,
		},
		{
			name:            "string-error",
			json:            `"hola amigos!`,
			err:             true,
			errType:         InvalidJSONError(""),
			skipCheckResult: true,
		},
