)

func TestDecodeTime(t *testing.T) {
	testCases := []struct {
		name         string
		json         string
		format       string
		err          bool
		expectedTime string
	}{
		{
			name:         "basic",
			json:         `"2018-02-18"`,
			format:       `2006-01-02`,
			err:          false,
			expectedTime: "2018-02-18",
		},
		{
			name:         "basic",
			json:         `"2017-01-02T15:04:05Z"`,
			format:       time.RFC3339,
			err:          false,
			expectedTime: "2017-01-02T15:04:05Z",
		},
		{
			name:         "basic",
			json:         `"2017-01-02T15:04:05ZINVALID"`,
			format:       time.RFC3339,
			err:          true,
			expectedTime: "",
		},
		{
			name:         "basic",
			json:         `"2017-01-02T15:04:05ZINVALID`,
			format:       time.RFC1123,
			err:          true,
			expectedTime: "",
		},
		{
			name:         "basic",
			json:         `"2017-01-02T15:04:05ZINVALID"`,
			format:       time.RFC1123,
			err:          true,
			expectedTime: "",
		},
		{
			name:         "basic",
			json:         `"2017-01-02T15:04:05ZINVALID`,
			format:       time.RFC3339,
			err:          true,
			expectedTime: "",
		},
	}

	for _, testCase := range testCases {
		t.Run(testCase.name, func(t *testing.T) {
			tm := time.Time{}
			dec := NewDecoder(strings.NewReader(testCase.json))
			err := dec.DecodeTime(&tm, testCase.format)
			if !testCase.err {
				assert.Nil(t, err)
				assert.Equal(t, testCase.expectedTime, tm.Format(testCase.format))
				return
			}
			assert.NotNil(t, err)
		})
	}
}

