func (enc *Encoder) EncodeSQLNullString(v *sql.NullString) error {
	if enc.isPooled == 1 {
		panic(InvalidUsagePooledEncoderError("Invalid usage of pooled encoder"))
	}
	_, _ = enc.encodeString(v.String)
	_, err := enc.Write()
	if err != nil {
		enc.err = err
		return err
	}
	return nil
}

