`
		// create our writer
		w := &TestWriter{target: 5000, mux: &sync.RWMutex{}}
		enc := Stream.NewEncoder(w).NConsumer(50).LineDelimited()
		w.enc = enc
		s := StreamChanObject(make(chan *testObject))
		go feedStream(s, 5000)
