#!/bin/bash

set -o errexit

scripts/de-reset-node-modules.sh

{
  "arrow_spacing": {
    "level": "error"
  },
  "braces_spacing": {
    "level": "error",
    "spaces": 1,
    "empty_object_spaces": 0
  },
  "camel_case_classes": {
    "level": "error"
  },
  "coffeescript_error": {
    "level": "error"
  },
  "colon_assignment_spacing": {
    "level": "ignore",
    "spacing": {
      "left": 0,
      "right": 0
    }
  },
  "cyclomatic_complexity": {
    "value": 12,
    "level": "error"
  },
  "duplicate_key": {
    "level": "error"
  },
  "empty_constructor_needs_parens": {
    "level": "ignore"
  },
  "ensure_comprehensions": {
    "level": "ignore"
  },
  "eol_last": {
    "level": "error"
  },
  "indentation": {
    "value": 2,
    "level": "error"
  },
  "line_endings": {
    "level": "error",
    "value": "unix"
  },
  "max_line_length": {
    "value": 80,
    "level": "ignore",
    "limitComments": true
  },
  "missing_fat_arrows": {
    "level": "ignore",
    "is_strict": false
  },
  "newlines_after_classes": {
    "value": 2,
    "level": "error"
  },
  "no_backticks": {
    "level": "ignore"
  },
  "no_debugger": {
    "level": "error",
    "console": false
  },
  "no_empty_functions": {
    "level": "ignore"
  },
  "no_empty_param_list": {
    "level": "error"
  },
  "no_implicit_braces": {
    "level": "error",
    "strict": false
  },
  "no_implicit_parens": {
    "strict": true,
    "level": "ignore"
  },
  "no_interpolation_in_single_quotes": {
    "level": "ignore"
  },
  "no_nested_string_interpolation": {
    "level": "ignore"
  },
  "no_plusplus": {
    "level": "ignore"
  },
  "no_stand_alone_at": {
    "level": "error"
  },
  "no_tabs": {
    "level": "error"
  },
  "no_this": {
    "level": "ignore"
  },
  "no_throwing_strings": {
    "level": "error"
  },
  "no_trailing_semicolons": {
    "level": "error"
  },
  "no_trailing_whitespace": {
    "level": "error",
    "allowed_in_comments": false,
    "allowed_in_empty_lines": false
  },
  "no_unnecessary_double_quotes": {
    "level": "error"
  },
  "no_private_function_fat_arrows": {
    "level": "error"
  },
  "no_unnecessary_fat_arrows": {
    "level": "error"
  },
  "non_empty_constructor_needs_parens": {
    "level": "ignore"
  },
  "prefer_english_operator": {
    "level": "error",
    "doubleNotLevel": "ignore"
  },
  "space_operators": {
    "level": "error"
  },
  "spacing_after_comma": {
    "level": "error"
  },
  "transform_messes_up_line_numbers": {
    "level": "warn"
  }
}
