{
	"folders": [
		{
			"name": "wox",
			"path": "."
		},
		{
			"name": "wox.core",
			"path": "wox.core"
		},
		{
			"name": "wox.plugin.python",
			"path": "wox.plugin.python"
		},
		{
			"name": "wox.plugin.host.python",
			"path": "wox.plugin.host.python"
		},
		{
			"name": "wox.plugin.nodejs",
			"path": "wox.plugin.nodejs"
		},
		{
			"name": "wox.plugin.host.nodejs",
			"path": "wox.plugin.host.nodejs"
		},
		{
			"name": "wox.ui.flutter",
			"path": "wox.ui.flutter/wox"
		}
	],
	"settings": {
		"python.languageServer": "Default",
		"makefile.configureOnOpen": false
	},
	"extensions": {
		"recommendations": [
			// for go
			"golang.go",
			// for python
			"ms-python.python",
			"charliermarsh.ruff",
			"ms-python.mypy-type-checker",
			// for flutter
			"dart-code.flutter",
			// for js
			"dbaeumer.vscode-eslint",
			"esbenp.prettier-vscode",
			// others
			"ms-vscode.makefile-tools"
		]
	}
}

[
    {
        "Name": "Translate English to Chinese",
        "Author": "qianlifeng",
        "Command": "translate",
        "Prompt": "请将以下英文翻译为中文，不要输出任何与译文无关的解释：\n%s"
    },
    {
        "Name": "TLDR",
        "Author": "qianlifeng",
        "Command": "tldr",
        "Prompt": "Extract all facts from the text and summarize it in all relevant aspects in up to seven bullet points and a 1-liner summary. Pick a good matching emoji for every bullet point. And replay in chinese, thx.\nText: %s\nSummary:"
    }
]
