{
	"name": "Go",
	"image": "mcr.microsoft.com/devcontainers/go:1-1.21-bullseye",
	"customizations": {
		"vscode": {
			"extensions": [
				"golang.Go"
			]
		}
	},
	"postStartCommand": {
		"safe-dir": "git config --global --add safe.directory ${containerWorkspaceFolder}"
	}
}

version_info {
  version_var =    "github.com/opentofu/opentofu/version.Version"
  prerelease_var = "github.com/opentofu/opentofu/version.Prerelease"
}

version_exec = false
disable_provider_requirements = true

