{
  "$schema": "https://docs.renovatebot.com/renovate-schema.json",
  "extends": [
    "config:base",
    ":semanticCommitTypeAll(build)",
    ":separatePatchReleases",
    "workarounds:doNotUpgradeFromAlpineStableToEdge"
  ],
  "ignorePaths": [
  	"docs/**"
  ],
  "ignorePresets": [
    ":prHourlyLimit2",
    ":semanticPrefixFixDepsChoreOthers",
    "workarounds:all"
  ],
  "enabledManagers": [
    "docker-compose",
    "dockerfile",
    "gomod",
    "kubernetes",
    "npm"
  ],
  "kubernetes": {
    "fileMatch": ["kube/.+\\.yml$"]
  },
  "labels": [
    "dependencies"
  ],
  "packageRules": [
    {
      "matchUpdateTypes": ["digest", "minor", "patch"],
      "automerge": true,
      "automergeType": "pr",
      "platformAutomerge": true
    },
    {
      "datasources": [
        "docker"
      ],
      "matchPackageNames": "traefik",
      "matchFileNames": [
      	"internal/suites/example/compose/traefik/docker-compose.v2.yml"
      ],
      "allowedVersions": "<3.0.0"
    },
    {
      "datasources": [
        "docker"
      ],
      "addLabels": [
        "docker"
      ]
    },
    {
      "datasources": [
        "go"
      ],
      "addLabels": [
        "go"
      ]
    },
    {
      "datasources": [
        "kubernetes"
      ],
      "addLabels": [
        "kubernetes"
      ]
    },
    {
      "datasources": [
        "npm"
      ],
      "addLabels": [
        "javascript"
      ]
    }
  ],
  "postUpdateOptions": [
      "gomodTidy",
      "gomodNoMassage"
  ]
}
