func ExampleNewDebounce() {
	i := int32(0)
	calls := []int32{}
	mu := sync.Mutex{}

	debounce, cancel := NewDebounce(time.Millisecond, func() {
		mu.Lock()
		defer mu.Unlock()
		calls = append(calls, atomic.LoadInt32(&i))
	})

	debounce()
	atomic.AddInt32(&i, 1)

	time.Sleep(5 * time.Millisecond)

	debounce()
	atomic.AddInt32(&i, 1)
	debounce()
	atomic.AddInt32(&i, 1)
	debounce()
	atomic.AddInt32(&i, 1)

	time.Sleep(5 * time.Millisecond)

	cancel()

	mu.Lock()
	fmt.Printf("%v", calls)
	mu.Unlock()
	// Output: [1 4]
}

func ExampleNewDebounceBy() {
