func dropUserPrivileges(gid int, uid int) error {
	if err := syscall.Setgid(gid); err != nil {
		return fmt.Errorf("Setgid error: %s", err)
	}
	if err := syscall.Setuid(uid); err != nil {
		return fmt.Errorf("Setuid error: %s", err)
	}
	return nil
}

