//go:embed themes.json
var themesBts []byte

// ThemeNotFoundError is returned when a requested theme is not found.
type ThemeNotFoundError struct {
	Theme       string
	Suggestions []string
}

func (e ThemeNotFoundError) Error() string {
	if len(e.Suggestions) == 0 {
		return fmt.Sprintf("invalid `Set Theme %q`: theme does not exist", e.Theme)
	}

	return fmt.Sprintf("invalid `Set Theme %q`: did you mean %q",
		e.Theme,
		strings.Join(e.Suggestions, ", "),
	)
}

// sortedThemeNames returns the names of the themes, sorted.
func sortedThemeNames() ([]string, error) {
	var keys []string
	for _, bts := range [][]byte{themesBts} {
		themes, err := parseThemes(bts)
		if err != nil {
			return nil, err
		}

		for _, theme := range themes {
			keys = append(keys, theme.Name)
		}
	}
	sort.Slice(keys, func(i, j int) bool {
		return strings.ToLower(keys[i]) < strings.ToLower(keys[j])
	})
	return keys, nil
}

const distance = 2

// findTheme return the given theme, if it exists.
func findTheme(name string) (Theme, error) {
	for _, bts := range [][]byte{themesBts} {
		themes, err := parseThemes(bts)
		if err != nil {
			return DefaultTheme, err
		}

		for _, theme := range themes {
			if theme.Name == name {
				return theme, nil
			}
		}
	}

	// not found, lets find similar themes!
	keys, err := sortedThemeNames()
	if err != nil {
		return DefaultTheme, err
	}

	suggestions := []string{}
	lname := strings.ToLower(name)
	for _, theme := range keys {
		ltheme := strings.ToLower(theme)
		levenshteinDistance := levenshtein.ComputeDistance(lname, ltheme)
		suggestByLevenshtein := levenshteinDistance <= distance
		suggestByPrefix := strings.HasPrefix(lname, ltheme)
		if suggestByLevenshtein || suggestByPrefix {
			suggestions = append(suggestions, theme)
		}
	}
	return DefaultTheme, ThemeNotFoundError{name, suggestions}
}

func parseThemes(bts []byte) ([]Theme, error) {
	var themes []Theme
	if err := json.Unmarshal(bts, &themes); err != nil {
		return nil, fmt.Errorf("could not load themes.json: %w", err)
	}
	return themes, nil
}

package main

import (
	"errors"
	"reflect"
	"sort"
	"testing"
)

func TestFindAllThemes(t *testing.T) {
	themes, err := sortedThemeNames()
	if err != nil {
		t.Fatal(err)
	}
	expect := 348
	if l := len(themes); l != expect {
		t.Errorf("expected to load %d themes, got %d", expect, l)
	}
}

func TestFindTheme(t *testing.T) {
	tests := []struct {
		tname string
		theme string
		err   error
	}{
		{
			tname: "exact match",
			theme: "Catppuccin Latte",
			err:   nil,
		},
		{
			tname: "match found",
			theme: "caTppuccin ltt",
			err:   ThemeNotFoundError{"caTppuccin ltt", []string{"Catppuccin Latte"}},
		},
		{
			tname: "no match found",
			theme: "stArf1sh",
			err:   ThemeNotFoundError{"stArf1sh", []string{}},
		},
		{
			tname: "single char",
			theme: "s",
			err:   ThemeNotFoundError{"s", []string{}},
		},
		{
			tname: "empty string",
			theme: "",
			err:   ThemeNotFoundError{"", []string{}},
		},
	}

	for _, tc := range tests {
		t.Run(tc.tname, func(t *testing.T) {
			_, err := findTheme(tc.theme)
			if tc.err != nil {
				if err == nil {
					t.Fatal("expected an error:", tc.err)
				}
				// check we got the right error
				var perr ThemeNotFoundError
				if !errors.As(err, &perr) {
					t.Fatal(err)
				}
				gotErr := err.(ThemeNotFoundError)
				wantErr := tc.err.(ThemeNotFoundError)
				// check suggestions
				sort.Strings(gotErr.Suggestions)
				sort.Strings(wantErr.Suggestions)
				if !reflect.DeepEqual(gotErr.Suggestions, wantErr.Suggestions) {
					t.Fatalf("got != want. got: %v, want: %v", err, tc.err)
				}
				// check names
				if !reflect.DeepEqual(gotErr.Theme, wantErr.Theme) {
					t.Fatalf("got != want. got: %v, want: %v", err, tc.err)
				}
			}
			if err != nil && tc.err == nil {
				t.Fatal("unexpected error:", err)
			}
		})
	}
}

// Package vhs tty.go spawns the ttyd process.
// It runs on the specified port and is generally meant to run in the background
// so that other processes (go-rod) can connect to the tty.
//
// xterm.js is used for rendering the terminal and can be adjusted using the Set command.
//
// Set FontFamily "DejaVu Sans Mono"
// Set FontSize 12
// Set Padding 50
package main

import (
	"fmt"
	"net"
	"os"
	"os/exec"
)

// randomPort returns a random port number that is not in use.
func randomPort() int {
	addr, _ := net.Listen("tcp", ":0") //nolint:gosec
	_ = addr.Close()
	return addr.Addr().(*net.TCPAddr).Port
}

// buildTtyCmd builds the ttyd exec.Command on the given port.
func buildTtyCmd(port int, shell Shell) *exec.Cmd {
	args := []string{
		fmt.Sprintf("--port=%d", port),
		"--interface", "127.0.0.1",
		"-t", "rendererType=canvas",
		"-t", "disableResizeOverlay=true",
		"-t", "enableSixel=true",
		"-t", "customGlyphs=true",
		"--once", // will allow one connection and exit
		"--writable",
	}

	args = append(args, shell.Command...)

	//nolint:gosec
	cmd := exec.Command("ttyd", args...)
	if shell.Env != nil {
		cmd.Env = append(shell.Env, os.Environ()...)
	}
	return cmd
}

