func ListHasDomain(ds map[string]byte, domain string, c *cache.Cache) bool {
	if ds == nil {
		return false
	}
	if c != nil {
		any, ok := c.Get(domain)
		if ok {
			return any.(bool)
		}
	}
	ss := strings.Split(domain, ".")
	var s1 string
	for i := len(ss) - 1; i >= 0; i-- {
		if s1 == "" {
			s1 = ss[i]
		} else {
			s1 = ss[i] + "." + s1
		}
		if _, ok := ds[s1]; ok {
			if c != nil {
				c.Set(domain, true, 24*time.Hour)
			}
			return true
		}
	}
	if c != nil {
		c.Set(domain, false, 24*time.Hour)
	}
	return false
}

func ListHasIP(c4, c6 []*net.IPNet, i net.IP, c *cache.Cache, geo []string) bool {
	if c4 == nil && c6 == nil && len(geo) == 0 {
		return false
	}
	if c != nil {
		any, ok := c.Get(i.String())
		if ok {
			return any.(bool)
		}
	}
	if len(geo) != 0 {
		bs := iploc.Country(i)
		if bs != "" {
			for _, v := range geo {
				if v == bs {
					if c != nil {
						c.Set(i.String(), true, 24*time.Hour)
					}
					return true
				}
			}
		}
	}
	if i.To4() != nil {
		if c4 == nil {
			return false
		}
		ii := i.To4()
		for _, v := range c4 {
			if v.Contains(ii) {
				if c != nil {
					c.Set(i.String(), true, 24*time.Hour)
				}
				return true
			}
		}
	}
	if i.To4() == nil {
		if c6 == nil {
			return false
		}
		ii := i.To16()
		for _, v := range c6 {
			if v.Contains(ii) {
				if c != nil {
					c.Set(i.String(), true, 24*time.Hour)
				}
				return true
			}
		}
	}
	if c != nil {
		c.Set(i.String(), false, 24*time.Hour)
	}
	return false
}

func ReadDomainList(url string) (map[string]byte, error) {
	ds := make(map[string]byte)
	ss, err := ReadList(url)
	if err != nil {
