	}
	for _, v := range ss {
		ds[v] = 0
	}
	return ds, nil
}

func ReadCIDRList(url string) ([]*net.IPNet, error) {
	c := make([]*net.IPNet, 0)
	l, err := ReadList(url)
	if err != nil {
		return nil, err
	}
	for _, v := range l {
		_, in, err := net.ParseCIDR(v)
		if err != nil {
			Log(Error{"when": "net.ParseCIDR", "cidr": v, "error": err.Error()})
			continue
		}
		c = append(c, in)
	}
	return c, nil
}

func ReadList(url string) ([]string, error) {
	var data []byte
	var err error
	if strings.HasPrefix(url, "http://") || strings.HasPrefix(url, "https://") {
		c := &http.Client{
			Timeout: 9 * time.Second,
			Transport: &http.Transport{
				DialContext: func(ctx context.Context, network, addr string) (net.Conn, error) {
					h, p, err := net.SplitHostPort(addr)
					if err != nil {
						return nil, err
					}
					s, err := Resolve6(h)
					if err == nil {
						c, err := net.Dial(network, net.JoinHostPort(s, p))
						if err == nil {
							return c, nil
						}
					}
					s, err = Resolve4(h)
					if err == nil {
						c, err := net.Dial(network, net.JoinHostPort(s, p))
						if err == nil {
							return c, nil
						}
					}
					return nil, errors.New("Can not fetch " + addr)
				},
			},
		}
		r, err := c.Get(url)
		if err != nil {
			return nil, err
		}
		defer r.Body.Close()
		data, err = io.ReadAll(r.Body)
		if err != nil {
			return nil, err
		}
	}
	if !strings.HasPrefix(url, "http://") && !strings.HasPrefix(url, "https://") {
		data, err = os.ReadFile(url)
		if err != nil {
			return nil, err
		}
	}
	data = bytes.TrimSpace(data)
	data = bytes.Replace(data, []byte{0x20}, []byte{}, -1)
	data = bytes.Replace(data, []byte{0x0d, 0x0a}, []byte{0x0a}, -1)
	ss := strings.Split(string(data), "\n")
	return ss, nil
}

