package goquery

import (
	"strconv"
	"testing"
)

func BenchmarkEach(b *testing.B) {
	var tmp, n int

	b.StopTimer()
	sel := DocW().Find("td")
	f := func(i int, s *Selection) {
		tmp++
	}
	b.StartTimer()
	for i := 0; i < b.N; i++ {
		sel.Each(f)
		if n == 0 {
			n = tmp
		}
	}
	if n != 59 {
		b.Fatalf("want 59, got %d", n)
	}
}

func BenchmarkEachIter(b *testing.B) {
	var tmp, n int

	b.StopTimer()
	sel := DocW().Find("td")
	b.StartTimer()
	for i := 0; i < b.N; i++ {
