{
  inputs = {
    nixpkgs.url = "github:nixos/nixpkgs/nixos-unstable";
    carlos.url = "github:caarlos0/nur";
    flake-utils.url = "github:numtide/flake-utils";
  };
  outputs =
    {
      nixpkgs,
      carlos,
      flake-utils,
      ...
    }:
    flake-utils.lib.eachDefaultSystem (
      system:
      let
        pkgs = nixpkgs.legacyPackages.${system};
        cpkgs = carlos.packages.${system};
      in
      {
        packages.default = pkgs.buildGoModule {
          pname = "goreleaser";
          version = "unversioned";
          src = ./.;
          ldflags = [
            "-s"
            "-w"
            "-X main.version=dev"
            "-X main.builtBy=flake"
          ];
          doCheck = false;
          vendorHash = "";
        };

        devShells.default = pkgs.mkShellNoCC {
          shellHook = "go mod tidy";
        };

        # nix develop .#dev
        devShells.dev = pkgs.mkShellNoCC {
          packages =
            with pkgs;
            [
              go-task
              gofumpt
              syft
              upx
              cosign
              gnupg
              nix-prefetch
              rustup
              zig
              bun
              deno
            ]
            ++ (lib.optionals pkgs.stdenv.isLinux [
              snapcraft
            ]);
        };

        # nix develop .#docs
        devShells.docs = pkgs.mkShellNoCC {
          packages =
            with pkgs;
            [
              go-task
              htmltest
            ]
            ++ (with cpkgs; [
              mkdocs-git-revision-date-localized-plugin
              mkdocs-include-markdown-plugin # https://github.com/NixOS/nixpkgs/pull/277351
            ])
            ++ (with pkgs.python312Packages; [
              regex
              mkdocs-material
              mkdocs-redirects
              mkdocs-minify
              mkdocs-rss-plugin
              filelock
            ]);
        };
      }
    );
}

package main

import (
	"os"

	_ "embed"

	goversion "github.com/caarlos0/go-version"
	"github.com/caarlos0/log"
	"github.com/charmbracelet/lipgloss"
	"github.com/goreleaser/goreleaser/v2/cmd"
	"github.com/muesli/termenv"
	"go.uber.org/automaxprocs/maxprocs"
)

//nolint:gochecknoglobals
var (
	version   = ""
	commit    = ""
	treeState = ""
	date      = ""
	builtBy   = ""
)

func init() {
	// enable colored output on github actions et al
	if os.Getenv("CI") != "" {
		lipgloss.SetColorProfile(termenv.TrueColor)
	}
	// automatically set GOMAXPROCS to match available CPUs.
	// GOMAXPROCS will be used as the default value for the --parallelism flag.
	if _, err := maxprocs.Set(); err != nil {
		log.WithError(err).Warn("failed to set GOMAXPROCS")
	}
}

func main() {
	cmd.Execute(
		buildVersion(version, commit, date, builtBy, treeState),
		os.Exit,
		os.Args[1:],
	)
}

const website = "https://goreleaser.com"

//go:embed art.txt
var asciiArt string

func buildVersion(version, commit, date, builtBy, treeState string) goversion.Info {
	return goversion.GetVersionInfo(
		goversion.WithAppDetails("goreleaser", "Release engineering, simplified.", website),
		goversion.WithASCIIName(asciiArt),
		func(i *goversion.Info) {
			if commit != "" {
				i.GitCommit = commit
			}
			if treeState != "" {
				i.GitTreeState = treeState
			}
			if date != "" {
				i.BuildDate = date
			}
			if version != "" {
				i.GitVersion = version
			}
			if builtBy != "" {
				i.BuiltBy = builtBy
			}
		},
	)
}

package main

import (
	"testing"

	"github.com/goreleaser/goreleaser/v2/internal/golden"
	"github.com/stretchr/testify/require"
)

func TestVersion(t *testing.T) {
	const goVersion = "go1.20.3"
	const compiler = "gc"
	const platform = "linux/amd64"

	for name, tt := range map[string]struct {
		version, commit, date, builtBy, treeState string
	}{
		"all empty": {},
		"complete": {
			version:   "1.2.3",
			date:      "12/12/12",
			commit:    "aaaa",
			builtBy:   "me",
			treeState: "clean",
		},
		"only version": {
			version: "1.2.3",
		},
		"version and date": {
			version: "1.2.3",
			date:    "12/12/12",
		},
		"version, date, built by": {
			version: "1.2.3",
			date:    "12/12/12",
			builtBy: "me",
		},
	} {
		t.Run(name, func(t *testing.T) {
			v := buildVersion(tt.version, tt.commit, tt.date, tt.builtBy, tt.treeState)
			v.GoVersion = goVersion
			v.Compiler = compiler
			v.Platform = platform
			out, err := v.JSONString()
			require.NoError(t, err)

			golden.RequireEqualJSON(t, []byte(out))
		})
	}
}
