package revel

import (
	"encoding/json"
	"fmt"
	"io"
	"io/ioutil"
	"os"
	"reflect"
	"sort"
	"strings"
	"testing"
	"time"

	"github.com/revel/config"
)

type A struct {
	ID   int
	Name string
	B    B
	//nolint:unused
	private int
}

type B struct {
	Extra string
}

var (
	ParamTestValues = map[string][]string{
		"int":                            {"1"},
		"int8":                           {"1"},
		"int16":                          {"1"},
		"int32":                          {"1"},
		"int64":                          {"1"},
		"uint":                           {"1"},
		"uint8":                          {"1"},
		"uint16":                         {"1"},
		"uint32":                         {"1"},
		"uint64":                         {"1"},
		"float32":                        {"1.000000"},
		"float64":                        {"1.000000"},
		"str":                            {"hello"},
		"bool-true":                      {"true"},
		"bool-1":                         {"1"},
		"bool-on":                        {"on"},
		"bool-false":                     {"false"},
		"bool-0":                         {"0"},
		"bool-0.0":                       {"0.0"},
		"bool-off":                       {"off"},
		"bool-f":                         {"f"},
		"date":                           {"1982-07-09"},
		"datetime":                       {"1982-07-09 21:30"},
		"customDate":                     {"07/09/1982"},
		"arr[0]":                         {"1"},
		"arr[1]":                         {"2"},
		"arr[3]":                         {"3"},
		"uarr[]":                         {"1", "2"},
		"arruarr[0][]":                   {"1", "2"},
		"arruarr[1][]":                   {"3", "4"},
		"2darr[0][0]":                    {"0"},
		"2darr[0][1]":                    {"1"},
		"2darr[1][0]":                    {"10"},
		"2darr[1][1]":                    {"11"},
		"A.ID":                           {"123"},
		"A.Name":                         {"rob"},
		"B.ID":                           {"123"},
		"B.Name":                         {"rob"},
		"B.B.Extra":                      {"hello"},
		"pB.ID":                          {"123"},
		"pB.Name":                        {"rob"},
		"pB.B.Extra":                     {"hello"},
		"priv.private":                   {"123"},
		"arrC[0].ID":                     {"5"},
		"arrC[0].Name":                   {"rob"},
		"arrC[0].B.Extra":                {"foo"},
		"arrC[1].ID":                     {"8"},
		"arrC[1].Name":                   {"bill"},
		"m[a]":                           {"foo"},
		"m[b]":                           {"bar"},
