type testClient struct {
	*BuiltinEventEngine
	client        *Client
	tester        *testing.T
	eng           Engine
	network       string
	addr          string
	multicore     bool
	async         bool
	nclients      int
	started       int32
	connected     int32
	clientActive  int32
	disconnected  int32
	workerPool    *goPool.Pool
	udpReadHeader int32
}

func (s *testClient) OnBoot(eng Engine) (action Action) {
