{
  description = "templ";

  inputs = {
    nixpkgs.url = "github:NixOS/nixpkgs/nixos-24.11";
    nixpkgs-unstable.url = "github:NixOS/nixpkgs/nixos-unstable";
    gitignore = {
      url = "github:hercules-ci/gitignore.nix";
      inputs.nixpkgs.follows = "nixpkgs";
    };
    xc = {
      url = "github:joerdav/xc";
      inputs.nixpkgs.follows = "nixpkgs";
    };
  };

  outputs = { self, nixpkgs, nixpkgs-unstable, gitignore, xc }:
    let
      allSystems = [
        "x86_64-linux" # 64-bit Intel/AMD Linux
        "aarch64-linux" # 64-bit ARM Linux
        "x86_64-darwin" # 64-bit Intel macOS
        "aarch64-darwin" # 64-bit ARM macOS
      ];
      forAllSystems = f: nixpkgs.lib.genAttrs allSystems (system: f {
        inherit system;
        pkgs = import nixpkgs { inherit system; };
        pkgs-unstable = import nixpkgs-unstable { inherit system; };
      });
    in
    {
      packages = forAllSystems ({ system, pkgs, ... }:
        rec {
          default = templ;

          templ = pkgs.buildGo123Module {
            name = "templ";
            subPackages = [ "cmd/templ" ];
            src = gitignore.lib.gitignoreSource ./.;
            vendorHash = "sha256-ipLn52MsgX7KQOJixYcwMR9TCeHz55kQQ7fgkIgnu7w=";
            CGO_ENABLED = 0;
            flags = [
              "-trimpath"
            ];
            ldflags = [
              "-s"
              "-w"
              "-extldflags -static"
            ];
          };
        });

      # `nix develop` provides a shell containing development tools.
      devShell = forAllSystems ({ system, pkgs, pkgs-unstable, ... }:
        pkgs.mkShell {
          buildInputs = [
            pkgs.golangci-lint
            pkgs.cosign # Used to sign container images.
            pkgs.esbuild # Used to package JS examples.
            pkgs.go
            pkgs-unstable.gopls
            pkgs.goreleaser
            pkgs.gotestsum
            pkgs.ko # Used to build Docker images.
            pkgs.nodejs # Used to build templ-docs.
            xc.packages.${system}.xc
          ];
        });

      # This flake outputs an overlay that can be used to add templ and
      # templ-docs to nixpkgs as per https://templ.guide/quick-start/installation/#nix
      #
      # Example usage:
      #
      # nixpkgs.overlays = [
      #   inputs.templ.overlays.default
      # ];
      overlays.default = final: prev: {
        templ = self.packages.${final.stdenv.system}.templ;
        templ-docs = self.packages.${final.stdenv.system}.templ-docs;
      };
    };
}

package templ

import (
	"context"
	"io"
)

// Flush flushes the output buffer after all its child components have been rendered.
func Flush() FlushComponent {
	return FlushComponent{}
}

type FlushComponent struct {
}

type flusherError interface {
	Flush() error
}

type flusher interface {
	Flush()
}

func (f FlushComponent) Render(ctx context.Context, w io.Writer) (err error) {
	if err = GetChildren(ctx).Render(ctx, w); err != nil {
		return err
	}
	switch w := w.(type) {
	case flusher:
		w.Flush()
		return nil
	case flusherError:
		return w.Flush()
	}
	return nil
}

package templ

import (
	"context"
	"fmt"
	"io"
	"strings"
	"testing"
)

type flushableErrorWriter struct {
	lastFlushPos    int
	pos             int
	sb              strings.Builder
	flushedSections []string
}

func (f *flushableErrorWriter) Write(p []byte) (n int, err error) {
	n, err = f.sb.Write(p)
	if err != nil {
		return
	}
	if n < len(p) {
		err = io.ErrShortWrite
	}
	f.pos += n
	return
}

func (f *flushableErrorWriter) Flush() error {
	f.flushedSections = append(f.flushedSections, f.sb.String()[f.lastFlushPos:f.pos])
	f.lastFlushPos = f.pos
	return nil
}

type flushableWriter struct {
	lastFlushPos    int
	pos             int
	sb              strings.Builder
	flushedSections []string
}

func (f *flushableWriter) Write(p []byte) (n int, err error) {
	n, err = f.sb.Write(p)
	if err != nil {
		return
	}
	if n < len(p) {
		err = io.ErrShortWrite
	}
	f.pos += n
	return
}

func (f *flushableWriter) Flush() {
	f.flushedSections = append(f.flushedSections, f.sb.String()[f.lastFlushPos:f.pos])
	f.lastFlushPos = f.pos
}

func TestFlush(t *testing.T) {
	t.Run("errors in child components are propagated", func(t *testing.T) {
		expectedErr := fmt.Errorf("test error")
		child := ComponentFunc(func(ctx context.Context, w io.Writer) error {
			return expectedErr
		})

		sb := new(strings.Builder)
		ctx := WithChildren(context.Background(), child)

		err := Flush().Render(ctx, sb)
		if err == nil {
			t.Fatalf("expected an error, got nil")
		}
		if err != expectedErr {
			t.Fatalf("expected error to be %v, got %v", expectedErr, err)
		}
	})
	t.Run("can render to a flushable error writer", func(t *testing.T) {
		child := ComponentFunc(func(ctx context.Context, w io.Writer) error {
			_, err := w.Write([]byte("hello"))
			return err
		})

		b := &flushableErrorWriter{}
		ctx := WithChildren(context.Background(), child)

		// Render the FlushComponent to the buffer
		if err := Flush().Render(ctx, b); err != nil {
			t.Fatalf("unexpected error: %v", err)
		}

		if len(b.flushedSections) != 1 {
			t.Fatalf("expected 1 flushed section, got %d", len(b.flushedSections))
		}
		if b.flushedSections[0] != "hello" {
			t.Fatalf("expected flushed section to be 'hello', got %q", b.flushedSections[0])
		}
	})
	t.Run("can render to a flushable writer", func(t *testing.T) {
		child := ComponentFunc(func(ctx context.Context, w io.Writer) error {
			_, err := w.Write([]byte("hello"))
			return err
		})

		b := &flushableWriter{}
		ctx := WithChildren(context.Background(), child)

		// Render the FlushComponent to the buffer
		if err := Flush().Render(ctx, b); err != nil {
			t.Fatalf("unexpected error: %v", err)
		}

		if len(b.flushedSections) != 1 {
			t.Fatalf("expected 1 flushed section, got %d", len(b.flushedSections))
		}
		if b.flushedSections[0] != "hello" {
			t.Fatalf("expected flushed section to be 'hello', got %q", b.flushedSections[0])
		}
	})
	t.Run("non-flushable streams are a no-op", func(t *testing.T) {
		sb := new(strings.Builder)
		if err := Flush().Render(context.Background(), sb); err != nil {
			t.Fatalf("expected no error, got %v", err)
		}
	})
}

package templ

import "net/http"

// ComponentHandler is a http.Handler that renders components.
type ComponentHandler struct {
	Component      Component
	Status         int
	ContentType    string
	ErrorHandler   func(r *http.Request, err error) http.Handler
	StreamResponse bool
}

const componentHandlerErrorMessage = "templ: failed to render template"

func (ch *ComponentHandler) ServeHTTPBuffered(w http.ResponseWriter, r *http.Request) {
	// Since the component may error, write to a buffer first.
	// This prevents partial responses from being written to the client.
	buf := GetBuffer()
	defer ReleaseBuffer(buf)
	err := ch.Component.Render(r.Context(), buf)
	if err != nil {
		if ch.ErrorHandler != nil {
			w.Header().Set("Content-Type", ch.ContentType)
			ch.ErrorHandler(r, err).ServeHTTP(w, r)
			return
		}
		http.Error(w, componentHandlerErrorMessage, http.StatusInternalServerError)
		return
	}
	w.Header().Set("Content-Type", ch.ContentType)
	if ch.Status != 0 {
		w.WriteHeader(ch.Status)
	}
	// Ignore write error like http.Error() does, because there is
	// no way to recover at this point.
	_, _ = w.Write(buf.Bytes())
}

func (ch *ComponentHandler) ServeHTTPStreamed(w http.ResponseWriter, r *http.Request) {
	w.Header().Set("Content-Type", ch.ContentType)
	if ch.Status != 0 {
		w.WriteHeader(ch.Status)
	}
	if err := ch.Component.Render(r.Context(), w); err != nil {
		if ch.ErrorHandler != nil {
			w.Header().Set("Content-Type", ch.ContentType)
			ch.ErrorHandler(r, err).ServeHTTP(w, r)
			return
		}
		http.Error(w, componentHandlerErrorMessage, http.StatusInternalServerError)
	}
}

// ServeHTTP implements the http.Handler interface.
func (ch ComponentHandler) ServeHTTP(w http.ResponseWriter, r *http.Request) {
	if ch.StreamResponse {
		ch.ServeHTTPStreamed(w, r)
		return
	}
	ch.ServeHTTPBuffered(w, r)
}

// Handler creates a http.Handler that renders the template.
func Handler(c Component, options ...func(*ComponentHandler)) *ComponentHandler {
	ch := &ComponentHandler{
		Component:   c,
		ContentType: "text/html; charset=utf-8",
	}
	for _, o := range options {
		o(ch)
	}
	return ch
}

// WithStatus sets the HTTP status code returned by the ComponentHandler.
func WithStatus(status int) func(*ComponentHandler) {
	return func(ch *ComponentHandler) {
		ch.Status = status
	}
}

// WithContentType sets the Content-Type header returned by the ComponentHandler.
func WithContentType(contentType string) func(*ComponentHandler) {
	return func(ch *ComponentHandler) {
		ch.ContentType = contentType
	}
}

// WithErrorHandler sets the error handler used if rendering fails.
func WithErrorHandler(eh func(r *http.Request, err error) http.Handler) func(*ComponentHandler) {
	return func(ch *ComponentHandler) {
		ch.ErrorHandler = eh
	}
}

// WithStreaming sets the ComponentHandler to stream the response instead of buffering it.
func WithStreaming() func(*ComponentHandler) {
	return func(ch *ComponentHandler) {
		ch.StreamResponse = true
	}
}

package templ_test

import (
	"context"
	"errors"
	"io"
	"net/http"
	"net/http/httptest"
	"testing"

	"github.com/a-h/templ"
	"github.com/google/go-cmp/cmp"
)

func TestHandler(t *testing.T) {
	hello := templ.ComponentFunc(func(ctx context.Context, w io.Writer) error {
		if _, err := io.WriteString(w, "Hello"); err != nil {
			t.Fatalf("failed to write string: %v", err)
		}
		return nil
	})
	errorComponent := templ.ComponentFunc(func(ctx context.Context, w io.Writer) error {
		if _, err := io.WriteString(w, "Hello"); err != nil {
			t.Fatalf("failed to write string: %v", err)
		}
		return errors.New("handler error")
	})

	tests := []struct {
		name             string
		input            *templ.ComponentHandler
		expectedStatus   int
		expectedMIMEType string
		expectedBody     string
	}{
		{
			name:             "handlers return OK by default",
			input:            templ.Handler(hello),
			expectedStatus:   http.StatusOK,
			expectedMIMEType: "text/html; charset=utf-8",
			expectedBody:     "Hello",
		},
		{
			name:             "handlers return OK by default",
			input:            templ.Handler(templ.Raw(`  &spades; &#8216;`)),
			expectedStatus:   http.StatusOK,
			expectedMIMEType: "text/html; charset=utf-8",
			expectedBody:     "  &spades; &#8216;",
		},
		{
			name:             "handlers can be configured to return an alternative status code",
			input:            templ.Handler(hello, templ.WithStatus(http.StatusNotFound)),
			expectedStatus:   http.StatusNotFound,
			expectedMIMEType: "text/html; charset=utf-8",
			expectedBody:     "Hello",
		},
		{
			name:             "handlers can be configured to return an alternative status code and content type",
			input:            templ.Handler(hello, templ.WithStatus(http.StatusOK), templ.WithContentType("text/csv")),
			expectedStatus:   http.StatusOK,
			expectedMIMEType: "text/csv",
			expectedBody:     "Hello",
		},
		{
			name:             "handlers that fail return a 500 error",
			input:            templ.Handler(errorComponent),
			expectedStatus:   http.StatusInternalServerError,
			expectedMIMEType: "text/plain; charset=utf-8",
			expectedBody:     "templ: failed to render template\n",
		},
		{
			name: "error handling can be customised",
			input: templ.Handler(errorComponent, templ.WithErrorHandler(func(r *http.Request, err error) http.Handler {
				// Because the error is received, it's possible to log the detail of the request.
				// log.Printf("template render error for %v %v: %v", r.Method, r.URL.String(), err)
				return http.HandlerFunc(func(w http.ResponseWriter, r *http.Request) {
					w.WriteHeader(http.StatusBadRequest)
					if _, err := io.WriteString(w, "custom body"); err != nil {
						t.Fatalf("failed to write string: %v", err)
					}
				})
			})),
			expectedStatus:   http.StatusBadRequest,
			expectedMIMEType: "text/html; charset=utf-8",
			expectedBody:     "custom body",
		},
	}
	for _, tt := range tests {
		tt := tt
		t.Run(tt.name, func(t *testing.T) {
			w := httptest.NewRecorder()
			r := httptest.NewRequest("GET", "/test", nil)
			tt.input.ServeHTTP(w, r)
			if got := w.Result().StatusCode; tt.expectedStatus != got {
				t.Errorf("expected status %d, got %d", tt.expectedStatus, got)
			}
			if mimeType := w.Result().Header.Get("Content-Type"); tt.expectedMIMEType != mimeType {
				t.Errorf("expected content-type %s, got %s", tt.expectedMIMEType, mimeType)
			}
			body, err := io.ReadAll(w.Result().Body)
			if err != nil {
				t.Errorf("failed to read body: %v", err)
			}
			if diff := cmp.Diff(tt.expectedBody, string(body)); diff != "" {
				t.Error(diff)
			}
		})
	}

	t.Run("streaming mode allows responses to be flushed", func(t *testing.T) {
		w := httptest.NewRecorder()
		r := httptest.NewRequest("GET", "/test", nil)

		component := templ.ComponentFunc(func(ctx context.Context, w io.Writer) error {
			// Write part 1.
			if _, err := io.WriteString(w, "Part 1"); err != nil {
				return err
			}
			// Flush.
			if f, ok := w.(http.Flusher); ok {
				f.Flush()
			}
			// Check partial response.
			wr := w.(*httptest.ResponseRecorder)
			actualBody := wr.Body.String()
			if diff := cmp.Diff("Part 1", actualBody); diff != "" {
				t.Error(diff)
			}
			// Write part 2.
			if _, err := io.WriteString(w, "\nPart 2"); err != nil {
				return err
			}
			return nil
		})

		templ.Handler(component, templ.WithStatus(http.StatusCreated), templ.WithStreaming()).ServeHTTP(w, r)
		if got := w.Result().StatusCode; http.StatusCreated != got {
			t.Errorf("expected status %d, got %d", http.StatusCreated, got)
		}
		if mimeType := w.Result().Header.Get("Content-Type"); "text/html; charset=utf-8" != mimeType {
			t.Errorf("expected content-type %s, got %s", "text/html; charset=utf-8", mimeType)
		}
		body, err := io.ReadAll(w.Result().Body)
		if err != nil {
			t.Errorf("failed to read body: %v", err)
		}
		if diff := cmp.Diff("Part 1\nPart 2", string(body)); diff != "" {
			t.Error(diff)
		}
	})
	t.Run("streaming mode handles errors", func(t *testing.T) {
		w := httptest.NewRecorder()
		r := httptest.NewRequest("GET", "/test", nil)

		expectedErr := errors.New("streaming error")

		component := templ.ComponentFunc(func(ctx context.Context, w io.Writer) error {
			if _, err := io.WriteString(w, "Body"); err != nil {
				return err
			}
			return expectedErr
		})

		var errorHandlerCalled bool
		errorHandler := func(r *http.Request, err error) http.Handler {
			if expectedErr != err {
				t.Errorf("expected error %v, got %v", expectedErr, err)
			}
			errorHandlerCalled = true
			return http.HandlerFunc(func(w http.ResponseWriter, r *http.Request) {
				// This will be ignored, because the header has already been written.
				w.WriteHeader(http.StatusBadRequest)
				// This will be written, but will be appended to the written body.
				if _, err := io.WriteString(w, "Error message"); err != nil {
					t.Errorf("failed to write error message: %v", err)
				}
			})
		}

		h := templ.Handler(component,
			templ.WithStatus(http.StatusCreated),
			templ.WithStreaming(),
			templ.WithErrorHandler(errorHandler),
		)
		h.ServeHTTP(w, r)

		if !errorHandlerCalled {
			t.Error("expected error handler to be called")
		}
		// Expect the status code to be 201, not 400, because in streaming mode,
		// we have to write the header before we can call the error handler.
		if actualRepsonseCode := w.Result().StatusCode; http.StatusCreated != actualRepsonseCode {
			t.Errorf("expected status %d, got %d", http.StatusCreated, actualRepsonseCode)
		}
		// Expect the body to be "BodyError message", not just "Error message" because
		// in streaming mode, we've already written part of the body to the response, unlike in
		// standard mode where the body is written to a buffer before the response is written,
		// ensuring that partial responses are not sent.
		actualBody, err := io.ReadAll(w.Result().Body)
		if err != nil {
			t.Errorf("failed to read body: %v", err)
		}
		if diff := cmp.Diff("BodyError message", string(actualBody)); diff != "" {
			t.Error(diff)
		}
	})
}

package templ

import (
	"context"
	"io"
)

// Join returns a single `templ.Component` that will render provided components in order.
// If any of the components return an error the Join component will immediately return with the error.
func Join(components ...Component) Component {
	return ComponentFunc(func(ctx context.Context, w io.Writer) (err error) {
		for _, c := range components {
			if err = c.Render(ctx, w); err != nil {
				return err
			}
		}
		return nil
	})
}

package templ_test

import (
	"bytes"
	"context"
	"errors"
	"io"
	"testing"

	"github.com/a-h/templ"
	"github.com/google/go-cmp/cmp"
)

func TestJoin(t *testing.T) {
	compErr := errors.New("component error")

	hello := templ.ComponentFunc(func(ctx context.Context, w io.Writer) error {
		if _, err := io.WriteString(w, "Hello"); err != nil {
			t.Fatalf("failed to write string: %v", err)
		}
		return nil
	})
	world := templ.ComponentFunc(func(ctx context.Context, w io.Writer) error {
		if _, err := io.WriteString(w, "World"); err != nil {
			t.Fatalf("failed to write string: %v", err)
		}
		return nil
	})
	err := templ.ComponentFunc(func(ctx context.Context, w io.Writer) error {
		return compErr
	})

	tests := []struct {
		name           string
		input          []templ.Component
		expectedOutput string
		expectedErr    error
	}{
		{
			name:           "a nil slice of components produces no output",
			input:          nil,
			expectedOutput: "",
		},
		{
			name:           "an empty list of components produces no output",
			input:          []templ.Component{},
			expectedOutput: "",
		},
		{
			name:           "components are rendered in order",
			input:          []templ.Component{hello, world},
			expectedOutput: "HelloWorld",
		},
		{
			name:           "components are rendered in order, and errors returned",
			input:          []templ.Component{hello, err},
			expectedOutput: "Hello",
			expectedErr:    compErr,
		},
		{
			name:           "no further components are rendered after an error",
			input:          []templ.Component{err, hello},
			expectedOutput: "",
			expectedErr:    compErr,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			got := templ.Join(tt.input...)
			b := new(bytes.Buffer)
			err := got.Render(context.Background(), b)
			if err != tt.expectedErr {
				t.Fatalf("failed to render component: %v", err)
			}
			if diff := cmp.Diff(tt.expectedOutput, b.String()); diff != "" {
				t.Error(diff)
			}
		})
	}
}

package templ

import (
	"crypto/sha256"
	"encoding/hex"
	"html"
)

// JSUnsafeFuncCall calls arbitrary JavaScript in the js parameter.
//
// Use of this function presents a security risk - the JavaScript must come
// from a trusted source, because it will be included as-is in the output.
func JSUnsafeFuncCall[T ~string](js T) ComponentScript {
	sum := sha256.Sum256([]byte(js))
	return ComponentScript{
		Name: "jsUnsafeFuncCall_" + hex.EncodeToString(sum[:]),
		// Function is empty because the body of the function is defined elsewhere,
		// e.g. in a <script> tag within a templ.Once block.
		Function:   "",
		Call:       html.EscapeString(string(js)),
		CallInline: string(js),
	}
}

// JSFuncCall calls a JavaScript function with the given arguments.
//
// It can be used in event handlers, e.g. onclick, onhover, etc. or
// directly in HTML.
func JSFuncCall[T ~string](functionName T, args ...any) ComponentScript {
	call := SafeScript(string(functionName), args...)
	sum := sha256.Sum256([]byte(call))
	return ComponentScript{
		Name: "jsFuncCall_" + hex.EncodeToString(sum[:]),
		// Function is empty because the body of the function is defined elsewhere,
		// e.g. in a <script> tag within a templ.Once block.
		Function:   "",
		Call:       call,
		CallInline: SafeScriptInline(string(functionName), args...),
	}
}

