#!/usr/bin/env bash

# build the app
CGO_ENABLED=0 go build -o ./bin/spf

{
  description = "A fancy, pretty terminal file manager";
  inputs = {
    nixpkgs.url = "github:nixos/nixpkgs/nixos-unstable";

    flake-utils.url = "github:numtide/flake-utils";

    flake-compat.url = "github:edolstra/flake-compat";
    flake-compat.flake = false;

    gomod2nix.url = "github:nix-community/gomod2nix";
    gomod2nix.inputs.nixpkgs.follows = "nixpkgs";
    gomod2nix.inputs.flake-utils.follows = "flake-utils";
  };

  outputs = inputs @ {...}:
    inputs.flake-utils.lib.eachDefaultSystem
    (
      system: let
        overlays = [
          inputs.gomod2nix.overlays.default
        ];
        pkgs = import inputs.nixpkgs {
          inherit system overlays;
        };
      in rec {
        packages = rec {
          superfile = pkgs.buildGoApplication {
            pname = "superfile";
            version = "1.1.7.1";
            src = ./.;
            modules = ./gomod2nix.toml;
          };
          default = superfile;
        };

        apps = rec {
          superfile = {
            type = "app";
            program = "${packages.superfile}/bin/superfile";
          };
          default = superfile;
        };

        devShells = {
          default = pkgs.mkShell {
            packages = with pkgs; [
              ## golang
              delve
              go-outline
              go
              golangci-lint
              gopkgs
              gopls
              gotools
              nix
              gomod2nix
              nixpkgs-fmt
            ];
          };
        };
      }
    );
}

package main

import (
	"embed"

	cmd "github.com/yorukot/superfile/src/cmd"
)

var (
	//go:embed src/superfile_config/*
	content embed.FS
)

func main() {
	cmd.Run(content)
}
