	return o
}

func (rt *runtime) newArrayOf(valueArray []Value) *object {
	o := rt.newArray(uint32(len(valueArray)))
	for index, value := range valueArray {
		if value.isEmpty() {
			continue
		}
		o.defineProperty(strconv.FormatInt(int64(index), 10), value, 0o111, false)
	}
	return o
}

